//and fullscreen (if your GPU can handle it...)
const float WAVE_NR = 10.0;

/*************************************************************/


void mainImage( out vec4  
fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    // Move origin so x and y goes from -0.5 to 0.5
    uv -= 0.5;
    // Scale x coordinate to rectangular window (x is about -0.8 to 0.8)
    uv.x *= iResolution.x / iResolution.y;

    float WAVE_WIDTH = 0.02/sqrt(WAVE_NR);
    //nr_factor allows the waves to live for 10 seconds
    float nr_factor = WAVE_NR/10.0;
    //Background color
    //solid color
    vec3 col = vec3(0.1,0.4,0.7);
    //soft moving bluish color
    float red   = (sin(iTime*0.354)+1.0)*0.15;
    float green = (sin(iTime*0.123)+1.0)*0.15+0.25;
    float blue  = (sin(iTime*0.678)+1.0)*0.25+0.5;
    col = vec3(red,green,blue);
    //White color (for wave)
    vec3 white = vec3(1.0);
    //"Integer" part of iTime (adjusted with nr_factor if more than 10 waves)
    float intTime = floor(iTime*nr_factor)/nr_factor;
    //Create a loop that looks back in time
    for (float i = 0.0; i < WAVE_NR; i++)
    {
        //Calculate time backwards
        float iT = (intTime - i/nr_factor);
        //See where the startPoint was some time ago
        vec2 startPoint = 0.5*vec2(sin(iT*0.95), cos(iT*0.67));
        //Get the distance from a previous startpoint to the current point (pixel)
        float dist = distance(uv,startPoint);
        //The edge of the wave has a radius that grows.
        //The further back in time, the larger the radius
        //The radius goes from 0.0 up to 1.0, because i goes from 0.0 to 10.0
        float waveRadius = (fract(iTime*nr_factor)/nr_factor + i/nr_factor)/10.0;
        //Compare the distance to the wave radius
        float wave = abs(dist-waveRadius);
        //If the distance and the radius are close then color this pixel
        if (wave < WAVE_WIDTH){
            //The center of the wave edge should be most intense
            //float intensity = (WAVE_WIDTH-wave)*50.0;
            float intensity = 0.5*smoothstep(WAVE_WIDTH,0.5*WAVE_WIDTH,wave);
            //Create fading effect
            //If the distance is small, the color should be intense
            //If the distance is large, the color should be pale
            intensity = (1.0-dist)*intensity;
            //Use intensity to make white color
            col += white*intensity;

        }
        //Testing the path of startpoint
        //if (dist < 0.1) col += vec3(0.5,0.0,0.5)/i;
    }

    // Output to screen
    fragColor = vec4(col,1.0);
}

const float NUM_ITERATIONS = 512.0;
const float zoomSpeed = 10.0;
const float width = 10.0;
//const vec2 center = vec2(-0.742978, 0.1);
const vec2 center = vec2(-0.747005, 0.150006);
//const vec2 center = vec2(0.0, 0.0);

vec2 f(vec2 z, vec2 c) {    
    return vec2(z.x * z.x - z.y * z.y, 2.0 * z.y * z.x) + c;
}

float iter(vec2 c) {

    float nrm = dot(c, c);
    // http://iquilezles.org/www/articles/mset_1bulb/mset1bulb.htm
    if( 256.0*nrm*nrm - 96.0*nrm + 32.0*c.x - 3.0 < 0.0 ) return 0.0;
    // http://iquilezles.org/www/articles/mset_2bulb/mset2bulb.htm
    if( 16.0*(nrm+2.0*c.x+1.0) - 1.0 < 0.0 ) return 0.0;

    vec2 z = vec2(0.0, 0.0);
    float i;
    for (i = 0.0; i < NUM_ITERATIONS; i += 1.0) {
        z = f(z, c);
        if (dot(z, z) > 4.0) break;
    }
    if (i >= NUM_ITERATIONS) 
        return 0.0;  

    // smooth out the coloring
    float si = i - log2(log2(dot(z,z))) + 4.0;
    float ai = smoothstep( -0.1, 0.0, 0.0);
    i = mix(i, si, ai);

    return i;
}

vec2 rotateCoord(vec2 c) {
    float theta = 3.14 + 3.14 * cos(iTime / 10.0);
    float x = (c.x - center.x) * cos(theta) - (c.y - center.y) * sin(theta) + center.x;
    float y = (c.x - center.x) * sin(theta) + (c.y - center.y) * cos(theta) + center.y;
    return vec2(x, y);

}

vec2 transformCoord(vec2 fragCoord) {
    float rescale = pow(zoomSpeed, -2.75 + 2.75*cos(iTime / 10.0));
    vec2 size = vec2(width, iResolution.y / iResolution.x * width) * rescale;
    vec2 uv = fragCoord/iResolution.xy;
    return rotateCoord((uv - 0.5) * size + center);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 c = transformCoord(fragCoord);
    float i = iter(c);    
    // color calculation
    vec3 col = 0.5 + 0.5*cos( 3.0 + 0.05*i + vec3(0.50, 0.05, 0.05));
    fragColor = vec4(col, 1.0);
}

#define pi acos(-1.)
#define eps 1./iResolution.y


float noise(vec2 st){
    return fract(sin(dot(vec2(12.23,74.343),st))*4254.);  
}


float noise2D(vec2 st){

  //id,fract
  vec2 id =floor(st);
  vec2 f = fract(st);

  //nachbarn
  float a = noise(id);
  float b = noise(id + vec2(1.,0.));
  float c = noise(id + vec2(0.,1.));
  float d = noise(id + vec2(1.));


  //f
  f = smoothstep(0.,1.,f);

  //mix
  float ab = mix(a,b,f.x);
  float cd = mix(c,d,f.x);
  return mix(ab,cd,f.y);
}

mat2 rot45 = mat2(0.707,-0.707,0.707,0.707);

mat2 rot(float a){
  float s = sin(a); float c = cos(a);
  return mat2(c,-s,s,c);
}
float fbm(vec2 st, float N, float rt){
    st*=3.;

  float s = .5;
  float ret = 0.;
  for(float i = 0.; i < N; i++){

      ret += noise2D(st)*s; st *= 2.9; s/=2.; st *= rot((pi*(i+1.)/N)+rt*8.);
      //st.x += iTime;
  }
  return ret;

}

float voronoi(vec2 uv){

    float d = 100.;
    vec2 uvFL = floor(uv);
    vec2 uvFR = fract(uv);

    for(float i = -1.; i <= 1.; i++){
      for(float j = -1.; j <= 1.; j++){

        vec2 nachbar = vec2(i,j);

        d = min(d, length( uvFR - noise(uvFL + nachbar)  - nachbar));
      }
    }
    return d;
}
