void rot(inout vec3 p,vec3 a,float r){
	a=normalize(a);
	p=mix(a*dot(p,a),p,cos(r))+sin(r)*cross(p,a);
}

#define sabs(p,k)sqrt((p)*(p)+(k))

void sfold(inout vec2 p, vec2 v, float k)
{
    float g=dot(p,v);
    p-=(g-sabs(g,k))*v;
}

void sfold90(inout vec2 p, float k)
{
    vec2 v=normalize(vec2(1,-1));
    sfold(p,v,k);
}

float lpNorm(in vec2 p, in float n)
{
	p = pow(abs(p), vec2(n));
	return pow(p.x+p.y, 1.0/n);
}

#define hash(n) fract(sin(n*5555.5))

float pt(vec2 uv)
{
    vec3 p=(vec3(uv,0));
    p.z-=iTime*.05;
    p.z=sin(p.z);
    //p=asin(cos(p*3.));
    float s=2.,e,res;
    for(int i=0;i<10;i++)
        p=abs(p)-1.2,
        s*=e=5./clamp(dot(p,p),.8,4.),
        p=p*e-3.;
    e=(p.z/s)*.4;
    return e;
}


float map(in vec3 p)
{
	rot(p,vec3(cos(iTime*.1),sin(iTime*.1),.7).xzy,iTime*.2);
    p=abs(p)-vec3(2.);
    float k=1e-7;
    sfold90(p.xz,k);
	sfold90(p.yz,k);
	sfold90(p.xy,k);
    p.x=abs(p.x)-.3;

	float d= lpNorm(p.xy,5.)-.05;
    float q=pt(p.yz);
    return min(d,lpNorm(vec2(q,p.x-clamp(p.x,-.01,.01)),5.)-.005 +hash(q*.1)*.005)*.7;


}

vec3 calcNormal(vec3 p)
{
  vec3 n=vec3(0);
  for(int i=0; i<4; i++){
    vec3 e=1e-5*(vec3(9>>i&1, i>>1&1, i&1)*2.-1.);
    n+=e*map(p+e);
  }
  return normalize(n);
}

float march(vec3 ro, vec3 rd, float near, float far)
{
    float t=near,d;
    for(int i=0;i<70;i++)
    {
        t+=d=map(ro+rd*t);
        if (d<.001) return t;
        if (t>=far) return far;
    }
    return far;
}

vec3 doColor(vec3 p)
{
    return vec3(.3,.5,.8)+cos(p)*.5+.5;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord.xy*2.-iResolution.xy)/iResolution.y;
    vec3 ro = vec3(0,0,-3);
    vec3 rd = normalize(vec3(uv,2));
    vec3 col= vec3(0);
	const float maxd=50.;
    float g=march(ro,rd,0.,maxd);
    if(g<maxd)
    {
        vec3 p=ro+rd*g;
        col=doColor(p); 
        vec3 n=calcNormal(p);      
		vec3 lightPos=ro+vec3(2,5,2);
    	vec3 li=lightPos-p;
		float len=length(li);
		li/=len;
		float dif=clamp(dot(n,li),0.,1.);
        col*=max(dif,.3);
        float rimd=pow(clamp(1.-dot(reflect(-li,n),-rd),0.,1.),2.5);
		float frn=rimd+2.2*(1.-rimd);
    	col*=frn*.6;
        col*=max(.5+.5*n.y,.4);
        col*=exp2(-2.*pow(max(0.,1.-map(p+n*.8)/.8),2.));
        col+=vec3(.8,.6,.2)*pow(clamp(dot(reflect(rd,n),li),0.,1.),10.);
        col *= exp(-.1 * g * g);
        //col = pow(col,vec3(1.5))*3.;
    }
    fragColor=vec4(col,1);
}

float N(float t)
{
    //Psuedo random number between 0 and 1
    //Enlarge t so that small changes in t will end up at 
    //very different points on the sine curve
    //Enlarge the sine value (-1 to 1) and then return 
    //only the fraction part.
    return fract(sin(t*3456.0)*6547.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    int BALLS = 5;
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv -= 0.5;
    uv.x *= iResolution.x / iResolution.y;
    vec3 colors[5];
    float cS = 0.4;
    colors[0] = vec3(1.0,cS,cS);
    colors[1] = vec3(1.0,1.0,cS);
    colors[2] = vec3(cS,1.0,cS);
    colors[3] = vec3(cS,cS,1.0);
    colors[4] = vec3(1.0,cS,1.0);

    float sum = 0.0;
    vec3 col = vec3(0.0);
    for (int i = 0; i < BALLS; i++){
        float t = float(i);
        float ranA = N(t*50.0);
        float ranB = N(t*50.0+500.0);
        vec2 ball = 0.5*vec2(sin(iTime*ranA), cos(iTime*ranB));    
        float dist = distance(uv,ball)*15.0;
        sum = 1.0 / dist;
        if (sum > 1.0) sum = 1.0;

        col += colors[i%5]*sum;
    }


    // Output to screen
    fragColor = vec4(col,1.0);
}

