// p: position
// r: radius
float sdCircle(vec2 p,float r)
{
    return length(p)-r;
}

// p:   position
// s.x: width
// s.y: height
float sdBox(vec2 p,vec2 s)
{
    vec2 d = abs(p)-s;

    return length(max(d,0.))+min(max(d.x,d.y),0.);
}

// p:   position
// s.x: width
// s.y: height
// r.x: roundness top-right  
// r.y: roundness bottom-right
// r.z: roundness top-left
// r.w: roundness bottom-left
float sdRoundBox(vec2 p,vec2 s,vec4 r) 
{
    r.xy = (p.x>0.)?r.xy:r.zw;
    r.x  = (p.y>0.)?r.x :r.y;

    vec2 q = abs(p)-s+r.x;

    return min(max(q.x,q.y),0.)+length(max(q,0.))-r.x;
}

float sdEllipse(vec2 p,vec2 ab)
{
    p = abs(p); if( p.x > p.y ) {p=p.yx;ab=ab.yx;}

    float l = ab.y*ab.y - ab.x*ab.x;
    float m = ab.x*p.x/l;      float m2 = m*m; 
    float n = ab.y*p.y/l;      float n2 = n*n; 
    float c = (m2+n2-1.0)/3.0; float c3 = c*c*c;
    float q = c3 + m2*n2*2.0;
    float d = c3 + m2*n2;
    float g = m + m*n2;
    float co;

    if( d<0.0 )
    {
        float h = acos(q/c3)/3.0;
        float s = cos(h);
        float t = sin(h)*sqrt(3.0);
        float rx = sqrt( -c*(s + t + 2.0) + m2 );
        float ry = sqrt( -c*(s - t + 2.0) + m2 );
        co = (ry+sign(l)*rx+abs(g)/(rx*ry)- m)/2.0;
    }
    else
    {
        float h = 2.0*m*n*sqrt( d );
        float s = sign(q+h)*pow(abs(q+h), 1.0/3.0);
        float u = sign(q-h)*pow(abs(q-h), 1.0/3.0);
        float rx = -s - u - c*4.0 + 2.0*m2;
        float ry = (s - u)*sqrt(3.0);
        float rm = sqrt( rx*rx + ry*ry );
        co = (ry/sqrt(rm-rx)+2.0*g/rm-m)/2.0;
    }

    vec2 r = ab * vec2(co, sqrt(1.0-co*co));

    return length(r-p) * sign(p.y-r.y);
}

// sca: sin/cos of the orientation
// scb: sin/cos of the aperture
float sdArc(vec2 p,vec2 sca,vec2 scb,float ra,float rb)
{
    p *= mat2(sca.x,sca.y,-sca.y,sca.x);
    p.x = abs(p.x);

    float k = (scb.y*p.x>scb.x*p.y) ? dot(p,scb) : length(p);

    return sqrt( dot(p,p) + ra*ra - 2.0*ra*k ) - rb;
}
