float sdCubicBezier(vec2 uv,vec2 p,vec2 a,vec2 b,vec2 c,vec2 d)
{
    float cd = cubic_bezier_dis( uv-p,a,b,c,d);
    float cs = cubic_bezier_sign(uv-p,a,b,c,d);

    return cd * cs;
}

// p: position
// r: radius
// b: blur
vec4 Circle(vec4 col,vec2 uv,vec2 p,float r,float b)
{
    float d = sdCircle(uv-p,r);

    col.a = S(r+b,r-b,d);

    return col;
}

// d: distance
// s: start
// e: end
// b: blur
vec4 Band(vec4 col,float d,float s,float e,float b)
{
    float s1 = S(s-b,s+b,d);
    float s2 = S(e+b,e-b,d);

    col.a = s1*s2;  // intersection

    return col;
}

// p:   position
// s.x: width
// s.y: height
// b:   blur
vec4 Rect(vec4 col,vec2 uv,vec2 p,vec2 s,float b)
{
    float d1 = sdBox(uv-p,s-b);
    float d2 = sdBox(uv-p,s+b);

    col.a = S(d2,d1,b);

    return col;
}

// s.x: width
// s.y: height
// r.x: roundness top-right  
// r.y: roundness bottom-right
// r.z: roundness top-left
// r.w: roundness bottom-left
// b:   blur
vec4 RoundRect(vec4 col,vec2 uv,vec2 p,vec2 s,vec4 r,float b)
{
    float d1 = sdRoundBox(uv-p,s-b,r);
    float d2 = sdRoundBox(uv-p,s+b,r);

    col.a = S(d2,d1,b);

    return col;
}

float sdFlaskBody(
    vec2 uv,
    vec2 tp,vec2 ts,vec4 rd,
    vec2 mp,vec2 ms,
    vec2 bp,float r,
    float s)
{
    float dt = sdRoundBox(uv-tp,ts,rd);
    float dm = sdBox(uv-mp,ms);
    float db = sdCircle(uv-bp,r);

    float d = sdSmoothMerge(sdSmoothMerge(dt,dm,s),db,s);

    return d;
}

// tp: top position
// ts: top size (width, height)
// rd: top roundness
//
// mp: middle position
// ms: middle size (width, height)
//
// bp: bottom position
// r:  bottom radius
//
// s: smoothness
// b: blur
vec4 FlaskBody(
    vec4 col,vec4 bcol,vec2 uv,
    vec2 tp,vec2 ts,vec4 rd,
    vec2 mp,vec2 ms,
    vec2 bp,float r,
    float s,float b)
{         
    float d1 = sdFlaskBody(uv,tp,ts-b,rd-b,mp,ms-b,bp,r-b,s);
    float d2 = sdFlaskBody(uv,tp,ts+b,rd+b,mp,ms+b,bp,r+b,s);

    col = mix(col,bcol,S(d2,d1,b));

    return col;
}

// p: circle position
// r: circle radius
// b: blur
vec4 FlaskFace(
    vec4 col,vec4 bcol,vec4 fcol,vec4 ocol,
    vec2 uv,vec2 p,float r,float ow,float b)
{
    // Liquid.
    float k = .001;  // smoothness

    float cd  = sdCircle(uv-p,r);

    float h = .24;  // height

    vec2 p0 = vec2(p.x-r,p.y+h);
	vec2 p1 = vec2(p.x,p.y+h+r/2.-.02);
	vec2 p2 = vec2(p.x,p.y+h-r/2.-.02);
	vec2 p3 = vec2(p.x+r,p.y+h);

    vec2 bv = vec2(-uv.x,uv.y);

    float cf = sdCubicBezier(uv,p,p0,p1,p2,p3);
    float cb = sdCubicBezier(bv,p,p0,p1,p2,p3);

    float df = sdSmoothMerge(-cd,-cf,k);
    float db = sdSmoothMerge(-cd,-cb,k);

    float bg = p.y+S(.2,2.,-uv.y*7.2);  // back gradient
    float fg = p.y+S(.2,2.,-uv.y*2.2);  // front gradient

    fg += S(-0.5,5.,-abs(uv.x*.2));

    // Eyes.
    vec2 es  = vec2(.035,.056);         // ellipse size
    vec2 elp = vec2(p.x-.135,p.y-.07);  // ellipse left position
    vec2 erp = vec2(p.x+.135,p.y-.07);  // ellipse right position
    float el = sdEllipse(uv-elp,es);    // left
    float er = sdEllipse(uv-erp,es);    // right

    // Eye pupils (or reflections?).
    vec2 ps  = vec2(es.x-.027,es.y-.045);
    vec2 plp = vec2(elp.x+.005,elp.y+.02);
    vec2 prp = vec2(erp.x+.005,erp.y+.02);
    float pl = sdEllipse(uv-plp,ps);
    float pr = sdEllipse(uv-prp,ps);

    // Cheeks.
    vec2 cs  = vec2(es.x+.005,es.y-.035);
    vec2 clp = vec2(elp.x,elp.y-.05);
    vec2 crp = vec2(erp.x,erp.y-.05);
    float cl = sdEllipse(uv-clp,cs);
    float cr = sdEllipse(uv-crp,cs);

    // Mouth.
    vec2  ap = vec2(p.x,p.y-.03);  // mouth position
    float ta = -3.14/2.;
    float tb = 1.;
    float ra = .06;
    float rb = .009;

    float d = sdArc(uv-ap,vec2(sin(ta),cos(ta)),vec2(sin(tb),cos(tb)),ra,rb);
    d = -d;

    // Draw the face.
	col = mix(col,ocol,   S(db+b,db-b,b));
    col = mix(col,fcol-bg,S(db-ow+b,db-ow-b,b));    

	col = mix(col,ocol,   S(df+b,df-b,b));
    col = mix(col,fcol-fg,S(df-ow+b,df-ow-b,b));

    col = mix(bcol+ocol,col,S(cl+b,cl-b,b));
    col = mix(bcol+ocol,col,S(cr+b,cr-b,b));

    col = mix(ocol,col,S(el+b,el-b,b));
    col = mix(ocol,col,S(er+b,er-b,b));

    col = mix(bcol+ocol,col,S(pl+b,pl-b,b));
    col = mix(bcol+ocol,col,S(pr+b,pr-b,b));

    col = mix(ocol,col,S(d-b,d+b,b));

    return col;
}

vec4 FlaskFace(vec4 col,vec2 uv,vec2 p,float t)
{
    uv -= p;

    vec4 mcol = vec4(1.,1.,1.,1.);     // mask color
    vec4 bcol = vec4(.95,.42,.59,1.);  // body color
    vec4 ocol = vec4(.31,.18,.54,1.);  // outline color
    vec4 fcol = vec4(.92,.19,.51,1.);  // face color

    float b = .001;  // blur

    // Top.
    vec2 tp = vec2(.0,.343);   // position
    vec2 ts = vec2(.16,.028);  // width, height
    vec4 rd = vec4(.017);      // roundness

    // Middle.
    vec2 mp = vec2(0.,0.);    // position
    vec2 ms = vec2(.13,.35);  // width, height

    // Bottom.
    vec2  bp = vec2(0.,-.15);  // position
    float r  = .29;            // radius

    // Face.
    vec2 fp = vec2(0.,-.15);  // position

    // Draw the body.
    vec2 rv  = vec2(uv.x+.041,uv.y-.02);
    vec2 rv2 = vec2(rv.x+.09,rv.y);
    vec2 lv  = vec2(uv.x-.04,uv.y);

    float ow = .015; // outline width
    float s  = .01;  // smoothness

    #define FlaskBodyO(col,bcol,uv) \
       FlaskBody(col,bcol,uv,tp,ts+ow,rd+ow,mp,ms+ow,bp,r+ow,s,b)
    #define FlaskBodyS(col,bcol,uv) \
       FlaskBody(col,bcol,uv,tp,ts,rd,mp,ms,bp,r,s,b)
   #define FlaskBodyR(col,bcol,uv) \
       FlaskBody(col,bcol,uv,tp,ts,rd,mp,ms,bp,r-.05,s,b)

    bcol -= ocol;  // account for the outline

    vec4 fo  = FlaskBodyO(col,ocol,uv);  // flask outline
    vec4 f   = FlaskBodyS(col,bcol,uv);  // flask color

    vec4 fm  = FlaskBodyS(col,mcol,uv);  // base mask
    vec4 fml = FlaskBodyS(col,mcol,lv);  // base mask, shifted right

    vec4 fms = FlaskBodyR(col,mcol,rv);   // base mask, shifted left
    vec4 fmr = FlaskBodyR(col,mcol,rv2);  // mask right, shifted left

    vec4 rr = C(0.,1.,fms-fmr);  // reflection right, shifted left
    vec4 rl = C(0.,1.,fm -fml);  // reflection left

    rr *= .1*S(.6,1.,1.-abs(uv.y));  // smooth reflections
    rl *= .1*S(.0,1.,1.-abs(uv.y));

    rr *= S(-.2,2.,uv.x*7.);  // hide the sharp corner on the left

    vec4 br = f + (fm*(S(-uv.y,-uv.y+.7,-.03)));  // base and top gradient
    br = mix(br,fm,fm.a*(uv.y+.3)*.9);            // flask gradient  
    col = mix(col,fo,fo.a);

    col += br;

    // Draw the face.
    float fr = r*.88;  // face radius
    float fg = fp.y+(1.-uv.y)*.2;  // front gradient
    vec4 fa = FlaskFace(col,bcol,fcol,ocol,uv,fp,fr,ow,b);  // face    
    col = fa-(fa*fg);

    col += rl*9.;  // reflections
    col += rr*2.;  

    // Draw bubbles.
    vec4 ucol = fcol;

    float t1 = t/4.;
    float t2 = t/5.;
    float t3 = t/6.;
    float t4 = t/7.;
    float t5 = t/8.;
    float t6 = t/9.;
    float t7 = t/10.;
    float t8 = t/11.;
    float t9 = t/12.;

    vec2 bp1 = vec2(-.03+sin(t1)/20.,.038+mod(t1,.4)-.1);
    vec2 bp2 = vec2(-.062+sin(t2)/20.,.10+mod(t2,.4)-.1);
    vec2 bp3 = vec2(.035+sin(t3)/20.,.06+mod(t3,.4)-.1);
    vec2 bp4 = vec2(.01+sin(t4)/20.,.165+mod(t4,.4)-.1);
    vec2 bp5 = vec2(-.036+sin(t5)/20.,.24+mod(t5,.4)-.1);
    vec2 bp6 = vec2(.048+sin(t6)/20.,.28+mod(t6,.4)-.1);
    vec2 bp7 = vec2(-.06+sin(t7)/20.,.31+mod(t7,.4)-.1);
    vec2 bp8 = vec2(-.02+sin(t8)/20.,.42+mod(t8,.2)-.1);
    vec2 bp9 = vec2(.025+sin(t9)/20.,.445+mod(t9,.2)-.1);

    float bs1 = mod((t1/100.),.009);
    float bs2 = mod((t2/50.),.0054);
    float bs3 = mod((t3/120.),.005);
    float bs4 = mod((t4/70.),.014);
    float bs5 = mod((t5/140.),.009);
    float bs6 = mod((t6/90.),.0083);
    float bs7 = mod((t7/60.),.0045);
    float bs8 = mod((t8/130.),.004);
    float bs9 = mod((t9/80.),.0065);

    ucol += S(uv.y+.3, -uv.y,.5);  // bubble gradient

    vec4 b1 = Circle(ucol,    uv,bp1,bs1,b);
    vec4 b2 = Circle(ucol,    uv,bp2,bs2,b);
    vec4 b3 = Circle(ucol,    uv,bp3,bs3,b);
    vec4 b4 = Circle(ucol,    uv,bp4,bs4,b);
    vec4 b5 = Circle(ucol,    uv,bp5,bs5,b);
    vec4 b6 = Circle(ucol,    uv,bp6,bs6,b);
    vec4 b7 = Circle(ucol,    uv,bp7,bs7,b);
    vec4 b8 = Circle(ucol*.85,uv,bp8,bs8,b);
    vec4 b9 = Circle(ucol*.85,uv,bp9,bs9,b);

    col = mix(col,b1,b1.a);
    col = mix(col,b2,b2.a);
    col = mix(col,b3,b3.a);
    col = mix(col,b4,b4.a);
    col = mix(col,b5,b5.a);
    col = mix(col,b6,b6.a);
    col = mix(col,b7,b7.a);
    col = mix(col,b8,b8.a);
    col = mix(col,b9,b9.a);

    // Draw the rim over the bubbles.
    // XXX: This ugly hack is needed since the flask is not transparent.
    vec2 rp = vec2(tp.x,tp.y+ts.y/2.+ow+ow/2.);  // position
    vec2 rs = vec2(ts.x-rd.x,ow/2.-b/2.-b);      // size
    vec4 ri = Rect(ocol,uv,rp,rs,b);

    col = mix(col,ri,ri.a);

    return col;
}

// https://www.shadertoy.com/view/wdB3DW
// https://en.wikipedia.org/wiki/Rotation_matrix
// https://www.khronos.org/opengl/wiki/Data_Type_(GLSL)#Matrix_constructors
mat2 r2d(float a) {
	float c = cos(a);
    float s = sin(a);

    return mat2(c,s,-s,c);
}

void mainImage(out vec4 fragColor,in vec2 fragCoord)
{
    // Move (0,0) to the center of the screen.
    vec2 uv = fragCoord.xy/iResolution.xy;
    uv -= .5;
    uv.x *= iResolution.x/iResolution.y;  

    // Draw the image.
    vec4 col = vec4(0.);

    float t = iTime;

    vec2 p = vec2(0.,0.);  // position        
    vec4 ocol = col;       // old color

    // t = 10.3;    

    // XXX: Slow.
    /*
    uv *= 2.;  // scale coordinates

    for (int i=-10; i<10; i+=1) {
        float fi = float(i)*1.-mod(t*2.,2.);

        vec2 lv = uv*r2d(t*fi);
        vec2 lp = vec2(p.x+fi,p.y)*r2d(t*fi);

        vec4 f = FlaskFace(col,lv,lp,t);        

        ocol += f;
    }
    */

    uv *= r2d(t);          // rotate
    uv *= (1.+sin(t*2.));  // scale coordinates

    vec4 f = FlaskFace(col,uv,p,t);
    ocol = f;

    col = ocol;

    fragColor = col;
}

//Define vars
vec3 sphereCol = vec3(0.8,.15,.15);
vec3 viewDir = vec3(0,0,-1);
vec3 ambientLight = vec3(.1f);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy-.5f;
    uv.x*=iResolution.x/iResolution.y; 

    //Calculate lightDir by mouse
    vec2 mousePos = iMouse.xy / iResolution.xy +.5f;
    vec3 lightDir = normalize(vec3(1.f-mousePos,-.6f));

    //Define and Phong Shade circle
    float r = (sin(iTime)*.05f+.15f);
    vec3 surface3DPoint = vec3 (uv, sqrt(r - uv.x*uv.x - uv.y*uv.y));
    vec3 normal = normalize(surface3DPoint);

    float lambertian = max(dot(normal, -lightDir) , 0.f);

    vec3 reflectionRay = reflect(lightDir,normal);
    float specular = pow(max(0.f,dot(-viewDir,reflectionRay)),10.f);

    vec3 col = (lambertian*sphereCol*.6f)+(specular*.4f)+ambientLight;

    // Output to screen
    fragColor = vec4(col,1.f);
}

#define time iTime
#define PI     3.14159265

float hash(vec2 p)  // replace this by something better
{
    p  = 50.0*fract( p*0.3183099 + vec2(0.71,0.113));
    return -1.0+2.0*fract( p.x*p.y*(p.x+p.y) );
}

float noise( in vec2 p )
{
    vec2 i = floor( p );
    vec2 f = fract( p );

	vec2 u = f*f*(3.0-2.0*f);

    return mix( mix( hash( i + vec2(0.0,0.0) ), 
                     hash( i + vec2(1.0,0.0) ), u.x),
                mix( hash( i + vec2(0.0,1.0) ), 
                     hash( i + vec2(1.0,1.0) ), u.x), u.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

   vec2 uv = fragCoord.xy;
   float f1 = noise( uv*0.03*0.5 );
   float f2 = noise( uv*0.03*0.5 );
   float f3 = noise( uv*0.03*0.5 );
   f1 *= smoothstep( 0.0, 10.0, abs(uv.x) ) * 200.0;
   f2 *= smoothstep( 0.0, 10.0, abs(uv.x) ) * 200.0;
   f3 *= smoothstep( 0.0, 10.0, abs(uv.x) ) * 200.0;

   float cols[3] = float[3](0.0,0.0,0.0);

   float size = 0.05;

   vec2 center;
   vec2 displacement;

   center = vec2(iResolution.x/2.0 + (cos(iTime * 0.1) * 400.0 + f1), iResolution.y/2.0 + (sin(iTime * 0.3) * 200.0 + f1));
   displacement = uv - center;
   cols[0] += 1.0/(length(displacement)*size);

   center = vec2(iResolution.x/2.0 + (sin(iTime * 0.1) * 400.0 + f2), iResolution.y/2.0 + (cos(iTime * 0.3) * 200.0 + f2));
   displacement = uv - center;
   cols[1] += 1.0/(length(displacement)*size);

   center = vec2(iResolution.x/2.0 + (sin(iTime * 0.1) * 400.0 + f3), iResolution.y/2.0 + (sin(iTime * 0.3) * 200.0 + f3));
   displacement = uv - center;
   cols[2] += 1.0/(length(displacement)*size);

   fragColor = vec4(cols[0],cols[1],cols[2],0.0);

}

// decrease for higher framerate
#define QUALITY 10.0

const float radius = 0.03;
const float gap = 2.5;
const float amplitude = .1;

float speed = 1.0;
float freq = 1.0;
float invert = 1.0;

float start = -.5 * 1.1;
float end = .5 * 1.1;

// separated method for performance
void update(float x) {
    if(cos(iTime * 2. - gap) >= 0.0)
        start = sin(iTime * 2. - gap) / 2.0 * 1.1;
    if(cos(iTime * 2.) >= 0.0)
        end = sin(iTime * 2.) / 2.0 * 1.1;

    if(start == -.5 * 1.1 && end == .5 * 1.1) {
        start = .5 * 1.1;
        end = -.5 * 1.1;
    }

    invert = sin(iTime * 2.0);
    speed = sin(iTime) + 1.0;
}

float func(float x) {
    if(x <= end && x >= start)
        return -10.;
    return sin((x + speed + iTime / 2.0) * 30.0) * amplitude * invert;
}

bool wave(float x, float y) {
    vec2 ratio = vec2(iResolution.x / iResolution.y, 1.0);

    float left = (x - radius * ratio.x) * iResolution.x;
    float right = (x + radius * ratio.x) * iResolution.x;

    update(x);

    // Remove too distant pixels from calculation
    if(abs(y) > amplitude / 2. + radius * 2. + .1)
        return false;

    float leftSin = func(left / iResolution.x);
    float rightSin = func(right / iResolution.x);

    for(float i = left; i <= right; i += 10.0 / QUALITY) {
        float x2 = i / iResolution.x;
        if(distance(vec2(x, y) * ratio, vec2(x2, func(x2)) * ratio) <= radius)
            return true;
    }

    return false;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord / iResolution.xy - vec2(0.5);
    vec3 background = vec3(0);

    vec3 col = 0.5 + 0.5 * cos(iTime + uv.xyx + vec3(0, 2, 4));

    background = mix(vec3(0), col, -cos(iTime) / 2.0 + 0.5);
    col = mix(col, vec3(0), -cos(iTime) / 2.0 + 0.5);

    if(wave(uv.x, uv.y))
        fragColor = vec4(col, 0.0);
    else
        fragColor = vec4(background, 0.0);
}

// "Mandelmaze in Daylight" by dr2 - 2016
// License: Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License

const float pi = 3.14159;
const vec4 cHashA4 = vec4 (0., 1., 57., 58.);
const vec3 cHashA3 = vec3 (1., 57., 113.);
const float cHashM = 43758.54;

vec4 Hashv4f (float p)
{
  return fract (sin (p + cHashA4) * cHashM);
}

float Noisefv2 (vec2 p)
{
  vec4 t;
  vec2 ip, fp;
  ip = floor (p);
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = Hashv4f (dot (ip, cHashA3.xy));
  return exp(mix (mix (t.x, t.y, fp.x), mix (t.z, t.w, fp.x), fp.y));
}

float Fbm2 (vec2 p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int i = 0; i < 5; i ++) {
    f += a * Noisefv2 (p);
    a *= 0.5;
    p *= 2.;
  }
  return f;
}

float PrBox2Df (vec2 p, vec2 b)
{
  vec2 d = abs (p) - b;
  return min (max (d.x, d.y), 0.) + length (max (d, 0.));
}

float PrCylAnDf (vec3 p, float r, float w, float h)
{
  return max (abs (length (p.xy) - r) - w, abs (p.z) - h);
}

mat3 vuMat;
vec3 vuPos;
float tCur, dstFar, chRingO, chRingI, vuVel, bxSize, chSize, qnStep;
int idObj;

float MBoxDf (vec3 p)
{
  vec4 q, q0;
  const float mScale = 2.62;
  const int nIter = 12;
  q0 = vec4 (p, 1.);
  q = q0;
  for (int n = 0; n < nIter; n ++) {
    q.xyz = clamp (q.xyz, -1., 1.) * 2. - q.xyz;
    q = q * mScale / clamp (dot (q.xyz, q.xyz), 0.5, 1.) + q0;
  }
  return length (q.xyz) / abs (q.w);
}

float ObjDf (vec3 p)
{
  vec3 q;
  float dMin, d, dm, tWid;
  dMin = dstFar;
  d = MBoxDf (p);
  q = p;
  q.y -= vuPos.y;
  tWid = 0.7 * chSize;
  dm = min (PrCylAnDf (q.xzy, chRingO, chSize, chSize),
     PrCylAnDf (q.xzy, chRingI, tWid, chSize));
  dm = min (min (dm, PrBox2Df (q.xy, vec2 (tWid, chSize))),
     PrBox2Df (q.zy, vec2 (tWid, chSize)));
  d = max (d, - dm);
  if (d < dMin) { dMin = d;  idObj = 1; }
  return dMin;
}

float ObjRay (vec3 ro, vec3 rd)
{
  const int nStep = 200;
  float dHit, d, s;
  dHit = 0.;
  s = 0.;
  for (int j = 0; j < nStep; j ++) {
    d = ObjDf (ro + dHit * rd);
    dHit += d;
    ++ s;
    if (d < 0.0003 || dHit > dstFar) break;
  }
  qnStep = s / float (nStep);
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  const vec3 e = vec3 (0.001, -0.001, 0.);
  vec4 v = vec4 (ObjDf (p + e.xxx), ObjDf (p + e.xyy),
     ObjDf (p + e.yxy), ObjDf (p + e.yyx));
  return sqrt(normalize (vec3 (v.x - v.y - v.z - v.w) + 2. * v.yzw))-e;
}

vec3 TrackPath (float t)
{
  vec3 p;
  vec2 tr;
  float ti[9], aDir, a, d, r, tO, tI, tR, rGap;
  bool rotStep;
  tO = 0.5 * pi * chRingO / vuVel;
  tI = 0.5 * pi * chRingI / vuVel;
  rGap = chRingO - chRingI;
  tR = rGap / vuVel;
  rotStep = false;
  ti[0] = 0.;
  ti[1] = ti[0] + tO;  ti[2] = ti[1] + tR;
  ti[3] = ti[2] + tI;  ti[4] = ti[3] + tR;
  ti[5] = ti[4] + tO;  ti[6] = ti[5] + tR;
  ti[7] = ti[6] + tI;  ti[8] = ti[7] + tR;
  aDir = 2. * mod (floor (t / ti[8]), 2.) - 1.;
  p.y = 0.7 * bxSize * sin (2. * pi * floor (t / (2. * ti[8])) / 11.);
  t = mod (t, ti[8]);
  r = chRingO;
  tr = vec2 (0.);
  if (t < ti[4]) {
    if (t < ti[1]) {
      rotStep = true;
      a = (t - ti[0]) / (ti[1] - ti[0]);
    } else if (t < ti[2]) {
      tr.y = chRingO - rGap * (t - ti[1]) / (ti[2] - ti[1]);
    } else if (t < ti[3]) {
      rotStep = true;
      a = 1. + (t - ti[2]) / (ti[3] - ti[2]);
      r = chRingI;
    } else {
      tr.x = - (chRingI + rGap * (t - ti[3]) / (ti[4] - ti[3]));
    }
  } else {
    if (t < ti[5]) {
      rotStep = true;
      a = 2. + (t - ti[4]) / (ti[5] - ti[4]);
    } else if (t < ti[6]) {
      tr.y = - chRingO + rGap * (t - ti[5]) / (ti[6] - ti[5]);
    } else if (t < ti[7]) {
      rotStep = true;
      a = 3. + (t - ti[6]) / (ti[7] - ti[6]);
      r = chRingI;
    } else {
      tr.x = chRingI + rGap * (t - ti[7]) / (ti[8] - ti[7]);
    }
  }
  if (rotStep) {
    a *= 0.5 * pi * aDir;
    p.xz = r * vec2 (cos (a), sin (a));
  } else {
    if (aDir < 0.) tr.y *= -1.;
    p.xz = tr;
  }
  return p;
}

void VuPM (float t)
{
  vec3 fpF, fpB, vel;
  float a, ca, sa, dt;
  dt = 0.5;
  fpF = TrackPath (t + dt);
  fpB = TrackPath (t - dt);
  vuPos = 0.5 * (fpF + fpB);
  vuPos.y = fpB.y;
  vel = (fpF - fpB) / (2. * dt);
  a = atan (vel.z, vel.x) - 0.5 * pi;
  ca = cos (a);  sa = sin (a);
  vuMat = mat3 (ca, 0., - sa, 0., 1., 0., sa, 0., ca);
}

float ObjSShadow (vec3 ro, vec3 rd)
{
  float sh, d, h;
  sh = 1.;
  d = 0.05;
  for (int j = 0; j < 25; j ++) {
    h = ObjDf (ro + rd * d);
    sh = min (sh, smoothstep (0., 1., 20. * h / d));
    d += min (0.1, 3. * h);
    if (h < 0.001) break;
  }
  return sh;
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec3 col, vn, ltDir[4];
  float dstHit, sh, dfSum, spSum;
  int idObjT;
  ltDir[0] = normalize (vec3 (1., 1., 0.));
  ltDir[1] = normalize (vec3 (0., 1., 1.));
  ltDir[2] = normalize (vec3 (1., 0., 1.));
  idObj = -1;
  dstHit = ObjRay (ro, rd);
  if (dstHit < dstFar) {
    ro += dstHit * rd;
    idObjT = idObj;
    vn = ObjNf (ro);
    if (idObjT == 1) {
      col = mix (vec3 (0.6, 0.9, 0.6), vec3 (0.9, 0.9, 1.),
         clamp (1.2 * length (ro) / bxSize, 0., 1.));
      col = col * clamp (1. - 1.2 * qnStep * qnStep, 0.2, 1.);
    }
    dfSum = 0.;
    spSum = 0.;
    for (int j = 0; j < 3; j ++) {
      sh = 0.1 + ObjSShadow (ro, ltDir[j]);
      dfSum += sh * (0.2 + max (dot (vn, ltDir[j]), 0.));
      spSum += sh * pow (max (0., dot (ltDir[j], reflect (rd, vn))), 32.);
    }
    col = col * dfSum + 1.3 * spSum;
    ltDir[3] = normalize (- ro);
    sh = ObjSShadow (ro, ltDir[3]);
    col = mix (col, vec3 (0.9, 0., 0.),
       0.5 * (1. + cos (20. * tCur)) * sh * max (dot (vn, ltDir[3]), 0.) /
       dot (ro, ro));
  } else {
    col = mix (vec3 (0., 0., 0.8), vec3 (1.),
       0.3 + 0.2 * (1. - smoothstep (0.8, 0.9, abs (rd.y))) *
       Fbm2 (8. * vec2 (2. * abs (atan (rd.z, rd.x)) / pi, rd.y)));
  }
  return pow (clamp (col, 0., 1.), vec3 (0.7));
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  vec4 mPtr;
  mat3 vuMat2;
  vec3 ro, rd;
  vec2 canvas, uv, ori, ca, sa;
  float az, el;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  dstFar = 30.;
  bxSize = 4.;
  chSize = 0.08 * bxSize;
  chRingO = 0.8 * bxSize;
  chRingI = 0.4 * bxSize;
  vuVel = 0.2 * bxSize;
  el = 0.;
  az = 0.;
  if (mPtr.z > 0.) {
    el = clamp (el - 1.3 * pi * mPtr.y, - 0.49 * pi, 0.49 * pi);
    az = clamp (az - 1.8 * pi * mPtr.x, - pi, pi);
  }
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  vuMat2 = mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x) *
     mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y);
  VuPM (tCur);
  ro = vuPos;
  rd = normalize (vec3 ((1./0.5) * sin (0.5 * uv.x), uv.y, 2.)) * vuMat2 * vuMat;
  fragColor = vec4 (ShowScene (ro, rd), 1.);
}

