#define R          iResolution
#define M          iMouse
#define T          iTime
#define PI         3.14159265359
#define PI2        6.28318530718

mat2 rot (float a) { return mat2(cos(a),sin(a),-sin(a),cos(a)); }
float hash21(vec2 p){ return fract(sin(dot(p,vec2(26.34,45.32)))*4324.23); }
float lsp(float begin, float end, float t) { return clamp((t - begin) / (end - begin), 0.0, 1.0); }
float eoc(float t) { return (t = t - 1.0) * t * t + 1.0; }

vec3 hue(float t){ 
    t*=5.;//tweak for other color variations
    const vec3 d = vec3(0.067,0.812,0.915);
    return .35 + .45*cos(PI2*t*(vec3(.97,.97,.98)*d)); 
}

float box( in vec2 p, in vec2 b ){
    vec2 d = abs(p)-b;
    return length(max(d,0.0)) + min(max(d.x,d.y),0.0);
}

float px = .0231;

vec3 doHatch(vec2 p, float res) {
    p *= res/10.;
    vec2 id = floor(p*.5)-.5;
    float rnd = hash21(floor(p*.5));
    float chk = mod(id.y + id.x,2.) * 2. - 1.;
    if (chk > 0.5) p.x *=-1.;
    float hatch = clamp(sin((p.x - p.y)*PI*3.)*3. + 0.25, 0., 1.);
    if(rnd>.66) hatch = rnd;
    return vec3(clamp(hatch,.3,.7),id);
}

void mainImage( out vec4 O, in vec2 F ) {

    vec2 uv = (2.*F.xy-R.xy)/max(R.x,R.y);
    vec2 ms = (2.*M.xy-R.xy)/max(R.x,R.y);

    px = fwidth(uv.x)*2.;

    vec2 vuv= uv*(2.+.25*sin(T*.135));
    vuv.xy*=rot(.5*sin(T*.2));

    vuv.y+=T*.35;
    float px = fwidth(uv.x);
    float level=1.;
    vec3 dht = doHatch(vuv,155.);
    float chk = mod(dht.z + dht.y,2.) * 2. - 1.;

    vec3 C = mix(hue((chk+vuv.x)*.05),vec3(.01),dht.x);

    vec2 muv = M.z>1.?uv-ms:uv-vec2(.5*sin(T)/level,.25*cos(T)/level);
    float tp = length(muv)-.1;
    tp=smoothstep(px,.0,abs(tp)-.0075);


    for(int k=0; k<4; k++){
        vec2 id = floor(vuv*level);
        float rnd = hash21(id);

        // threshold or if last loop
        if(rnd>.45||k>2) {

            vec2 p = vuv -(id+.5)/level;
            rnd = hash21(rnd+id.yx);
            float dir = rnd>.5 ?-1.:1.;


            vec2 off1= clamp(vec2(muv*.17),vec2(-.1275),vec2(.1275));
            off1/=level;

            // make ring parts - very basic
            float angle = atan(p.x+off1.x, p.y+off1.y);
            float f = length(p);
            float width = .5/level;
            float amt = 24.;
            //vec for moving ring
            vec2 q = vec2(
                fract(dir*amt*angle/PI+T*1.75)/level,
                f-width
            );
            //id for moving ring
            vec2 tid = vec2(
                floor(dir*amt*angle/PI+T*1.75),
                floor(f-width)
            );

            tid.x=mod(tid.x,2.);
            float ds = length(p+off1-vec2(.125/level))-.065/level;
            float dt = length(p+off1+vec2(.115/level))-.030/level;
            float d = length(p)-.455/level;
            float s = length(p)-.375/level;
            float c = box(q+vec2(0,.4/level),vec2(1.,.25)/level);
            float l = length(p+off1)-.195/level;
            float h = abs(l)-.014/level;

            float fd = smoothstep(.005+px,-px,abs(d)-.025/level);
            float md = smoothstep(.025+px,-px,length(p)-.275/level);
            d = smoothstep(.0075+px,-px,d);
            s = smoothstep(.05+px,-px,s);
            c = smoothstep(.01+px,-px,c);
            l = smoothstep(.01+px,-px,l);
            h = smoothstep(.01+px,-px,h);
            ds = smoothstep(.01+px,-px,ds);
            dt = smoothstep(.01+px,-px,dt);

            C=mix(C,vec3(.35),d);
            C=mix(C,vec3(.1),min(s,d));
            c=min(c,s);
            vec3 clr = hue(hash21(float(k)+tid.yx));
            if(tid.x<1.) clr = hue(hash21(tid.yx));
            C=mix(C,clr,min(c,d)+md );
            C=mix(C,vec3(.015),min(l,d));
            C=mix(C,vec3(.005),h);
            C=mix(C,vec3(.9),ds+dt+fd);
            break;
        }
        level*=2.;
    }

    C = mix(C,M.z>0.?vec3(.8,.1,.01):vec3(.01,.4,.9),tp);
    C = pow(C, vec3(.4545)); 
    O = vec4(C,1.0);
}

#define H(p) fract(sin((p)*mat2(127.1,311.7, 269.5,183.3)) *43758.5453123)
#define C(p) (.5+.5*vec2( cos( (H(p).y-.5)*iTime + 6.3*H(p).x + vec2(0,33) ) ) )
//#define hue(v)  ( .6 + .6 * cos( 6.3*(v)  + vec4(0,23,21,0)  ) )

float line(vec2 p, vec2 a,vec2 b) { 
    p -= a, b -= a;
    return length(p - b * clamp(dot(p, b) / dot(b, b), 0., 1.));                      
}

void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy,
         U = 10.*( u+u - R ) / R.y,
         I = floor(U), F = fract(U), D,A,B;        
    O-=O;

    for( int k=0; k<9; k++ ) {
        D = vec2(k%3,k/3)-1., A = D + C(I+D);
        O.r = max(O.r, .3 / length(F-A) );             // red blobs
        for( int j=0; j<9; j+= j==k-1?2:1 ) {
            D = vec2(j%3,j/3)-1., B = D + C(I+D);
            float d = length(B-A); d = max(0., 1.5-d); // A-B connectivity
            if (d>0.) O = max(O, smoothstep(30./R.y,0., line(F,A,B) - .02 * d*4. )  *d*2. );
            //*hue(d) 
    } }


    if ( iMouse.z>0. && min(F.x,F.y)<20./R.y ) O.b++;  // click to see grid
    //O = sqrt(O);
}

int matID = 0;
int scene = 1;

mat2 rotate(float a){
	return mat2(cos(a),-sin(a),sin(a),cos(a));
}

float sdfLP(vec3 p, float norm, float size){
    float px = pow(abs(p.x),norm);
    float py = pow(abs(p.y),norm);
    float pz = pow(abs(p.z),norm);
    return pow(px+py+pz,1./norm)-size;
}

float sdfBox( vec3 p, vec3 b ){
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}

float map(vec3 p,int id){
    float subject = 999.;
    if(scene ==0){
        vec3 q = p;
        q.x+=sin(q.y+iTime)*.5;
        vec3 center = floor(q*.2)+.5;
        subject = sdfLP(q-center,(sin(iTime)*2.)+2.9,.4);
    }else if(scene ==1){
        vec3 q = p;
        subject = sdfLP(q,(sin(iTime)*2.)+2.9,.9);
    }else if(scene ==2){
        vec3 q = p;
        q.y-=.5;
        q.x+=sin(q.y*10.+iTime*1.)*.2;
        vec3 center = vec3(floor(q.xz)+.5,0.);
        subject = sdfLP(q-center.xzy,1.,.3);
    } 

    float plane = p.y+1.5;
    float cil = 1.5-p.y;
    float box = -sdfBox(p+vec3(0.,0.,0.),vec3(4.,4.,4.));
    float best = min(cil,min(box,min(subject,plane)));
    if(id != 0){
        if(best == subject){matID = 2;}
        if(best == plane){matID=3;}

        if(best == box){matID=4;}
        if(best ==cil)matID=5;
    }
    return best;
}
vec3 normal(vec3 p){
    vec2 e= vec2(0,0.001);
	return normalize(vec3(map(p+e.yxx,0)-map(p-e.yxx,0),
                          map(p+e.xyx,0)-map(p-e.xyx,0),
                          map(p+e.xxy,0)-map(p-e.xxy,0)));
}
vec3 march(vec3 ro, vec3 rd){
    float total = 0.;
    float dist = 0.;
    for(int i = 0; i<500; i++){
        dist = map(ro+rd*total,1)*.5;
        total+=dist;
        if(dist < 0.001 || total>500.){
        	break;
        }
    }
    if(dist>0.01){
    	matID = 1;
    }

    return (ro+rd*total);

}
void mainImage( out vec4 fragColor, in vec2 fragCoord ){

    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;
	vec3 ro = vec3(0.);
    vec3 rd = normalize(vec3(uv,1.));
    scene = int(mod(iTime*.1,3.));
    if(scene == 0){
        ro = vec3(0,0.3,-3.5);
        ro.zy*=rotate(.4);
        rd.zy*=rotate(.4);
        ro.xz*= rotate(iTime*.1);
        rd.xz*= rotate(iTime*.1);
    }else if(scene ==1){
        ro = vec3(0,0.8,-2.5);
        rd.zy*=rotate(-.3);
        ro.xz*= rotate(iTime*.1);
        rd.xz*= rotate(iTime*.1);

    }else if(scene ==2){
        ro = vec3(0,.3,-0.3);
        rd.zy*=rotate(.1);
        rd.zy*=rotate(.1);
        ro.xz*= rotate(iTime*.1);
        rd.xz*= rotate(iTime*.1);

    }

    vec3 accum = vec3(1);
    for(int i = 0; i <9; i++){
        vec3 col =(march(ro,rd));
        vec3 n = normal(col);
        if(matID ==1){
    		accum *= vec3(1);
   		}	
        if(matID ==2){
            accum *= vec3(.9);
        	ro = col*2.;
            rd=normalize(reflect(rd,n));
        }
        if(matID ==3){
            accum *= vec3(0.8);
        }
        if(matID ==4){
            accum *= vec3(0.99,.996,0.99);
            float fresnel = pow(1.-dot(- rd,n),0.03);
            accum*=fresnel;  
            ro = col*.999;
            rd=normalize(reflect(rd,n));
        }
        if(matID ==5){
            accum *= vec3(.999);
        }
    }
    fragColor = vec4(accum,1.0);
}

// cylindrical variant of https://shadertoy.com/view/flyGDw

#define H(v) fract(1e4*sin((v)*mat2(R,R-71.)))
#define S(v) smoothstep(30./R.y,0.,v)

#define CYL

void mainImage( out vec4 O, vec2 u )
{
    vec2  R = iResolution.xy, P,D,_P,
          U = 10.* ( u+u - R ) / R.y, U0=U;
    float m = 9.,d, t = iTime, k=8.;
 #ifdef CYL
    U = k*vec2( .995*atan(U.y,U.x), 8./length(U) );        // go to cylinder space
    U.y += t;                                              // animation
 #endif

    for ( int k=0; k < 9; k++ )                            // jittered point grid distribution
        D = vec2(k%3,k/3), P = D - H(mod(floor(U)+D,25.)), // NB: cyclical hash
        d = length(P-fract(U)), 
        d < m ? _P=P, m = d : m;

    P = _P + floor(U);
 #ifdef CYL
    U = U0;
    P = k*8./(P.y-t) * cos( P.x/.995/k + vec2(0,-1.57) ); // back to screen space
    m = length(P-U);
 #endif   
                                                          // draw disk - dark disk
    O = vec4( clamp( S(m-.4) - S( length(P-U+ U*length(U)/1e3 ) -.4 )  ,0.,1.) );
}

#define R(a)mat2(cos(a),sin(a),-sin(a),cos(a))
#define r iResolution.xy
#define t iTime

void mainImage( out vec4 o, in vec2 FC )
{
    o=vec4(0);
    for(float i,e,f,g,s;i++<50.;g+=min(e*.7,f*f*.1)){
        vec3 c=vec3(4.7,5,5.6),n,q,p=q=vec3((FC.xy-.5*r)/r.y*g,g);
        p.zy*=R(.4+sin(t/3.)*.2);
        s=1.4;
        p.z+=t;
        q=p;
        f=abs(p.y*p.y-2.8)+.4;
        e=++p.y;
        mat2 m=R(1.);
        for(;s<1e3;s*=1.7)
            p.xz*=m,
            n.xz*=m,
            q.xz*=m,
            q.yz*=m,
            n+=cos(p*s),
            f-=abs(cos(q.x*s)*cos(q.z*s)-.6)/s,
            q.xz+=t/s*.2,
            e+=sin(p.x*s)/s/2.;
        n.y=.2;
        n/=length(n);
        e-=n.y;
        c.x+=n.y*3.;
        o.rgb+=mix(
            mix(exp(n.z-c),c/3e2,min(g/9.+e,1.4)),
            p-p+exp(-f*f)/50.,
            exp(-f));
    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy - vec2(0.5);

    float c = clamp(sin(uv.x*uv.x*uv.y*uv.y*3000.0 + 3.0 *iTime), 0.0, 1.0);
    float d = clamp(sin((uv.x*uv.y*uv.x)*1000.0 - 3.0 * iTime), 0.0, 1.0);

    float r = max(d, c);

    // Output to screen
    fragColor = vec4(vec3(r),1.0);
}

float theta(in float x, in float y) {
    float pi = 3.14159265;
    return mod(atan(y,x),2.0*pi);
}

float r(in float x, in float y) {
    return sqrt(x*x+y*y);
}

vec3 hsv(in float h, in float s, in float v) {
    // preperation functions
    float r1 = max(min(abs((mod(h,360.0)-180.0)/60.0)-1.0,1.0),0.0);
    float g1 = max(min(2.0-abs((mod(h,360.0)-120.0)/60.0),1.0),0.0);
    float b1 = max(min(2.0-abs((mod(h,360.0)-240.0)/60.0),1.0),0.0);

    //hsv to rgb functions
    float r = (r1+(1.0-r1*(100.0-s)/100.0)-s/100.0)*v/100.0;
    float g = (g1+(1.0-g1*(100.0-s)/100.0)-s/100.0)*v/100.0;
    float b = (b1+(1.0-b1*(100.0-s)/100.0)-s/100.0)*v/100.0;

    return vec3(r,g,b);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // grid with -2 to 2 from -y to y, adjusted for x so there's no distortion
    float x = (fragCoord.x/iResolution.y)*2.0-1.0*iResolution.x/iResolution.y;
    float y = (fragCoord.y/iResolution.y)*2.0-1.0;

    // complex number
    vec2 z = vec2(0,0);

    //pi for convenience
    float pi = 3.1415926;

    for (int i = 0;i < 40;i++) {
        z = vec2(z.x*z.x-z.y*z.y+x,2.0*z.x*z.y+y);
    }

    //check if r(z.x,z.y) [distance to origin] is overflowing, if so color it white.
    float k1,k2,k3;
    if (isnan(r(z.x,z.y))) {k1 = 100.0;k2 = 0.0;k3 = 0.0;}


    else {k1 = r(z.x,z.y)*100.0;k2 = 100.0-r(z.x,z.y)*100.0;k3 = theta(z.x,z.y)*(180.0/pi)+iTime*360.0;}


    fragColor = vec4(hsv(k3,k2,k1),1.0);
}

float time=0.;

float rand2(vec2 uv) {
  return fract(13339.*sin(dot(uv,vec2(86.726113,98.28111))));
}

float box(vec3 p, vec3 a) {
  vec3 q = abs(p)-a;
  return length(max(q,0.)) + min(0., max(q.x,max(q.y,q.z)));
}

mat2 rot(float a){float s=sin(a),c=cos(a); return mat2(c,s,-s,c);}

vec3 M=vec3(0);
float G=0.;
float w(vec3 p) {
  vec3 planep = p;
  planep.xz *= rot(time);
  float plane = box(planep+vec3(0,10,0), vec3(8));
  p.xz *= rot(time);
  p.xy *= rot(.2*time);
  float B= length(p.xz)-1.;//box(p, vec3(1,2,1));//length(p)-2.;
  B = max(B, -(length(p.xz)-.8));

  float C=length(p.xy)-1.5;
  float CC=length(p.yz)-1.5;
  CC = max(CC, -C);

  float BB= max(B,-C);
  M = p.y < 0. ? vec3(.9, .7, .3) : vec3(.2, .8, .5);


  if (CC < BB) M = vec3(.4, .6, 1.);


  BB = min(BB, CC);



  float O = mix(B,BB,.5+.5*sin(time));

  G += -min(O,0.);

  O = mix(length(p)-2.,O,sin(2.*time));
  //O -= (.5+.5*sin(10.*iTime))*rand2(p.xz);

  if (plane < O) M = vec3(.9,.3,.1);

  return min(plane,O);
}

float ray(vec3 p, vec3 dir){
float d=0.,c;
for(int i=0;i<100;i++){
  c=w(p+dir*d);d+=c*.5;
  if(c<.01||d>69.)break;
}

return d;

}

vec3 normal(vec3 p) {
 mat3 k = mat3(p,p,p)-mat3(.01);
 return normalize(w(p)-vec3(w(k[0]),w(k[1]),w(k[2])));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv-=.5;
    uv.x*=iResolution.x/iResolution.y;

    time = 2.*iTime;

    // COMMENT TO TURN OFF SLITSCAN EFFECT
    time += 2.*sin(mix(.5,10.,.5+.5*sin(iTime))*uv.y);// + length(uv);
    //time += .3*rand2(uv);

    //uv *= rot(iTime);
    vec3 col=vec3(0);
    vec3 cam=vec3(0,0,-8);
    vec3 dir=normalize(vec3(uv,1));

    float dist=ray(cam,dir);
    vec3 mat = M;
    float glow=G;

    if (dist<69.) {
      vec3 hit = cam+dir*dist;
      vec3 norm = normal(hit);


      float diff = length(.5+.5*sin(1.+norm))/sqrt(3.);
      col = mat*diff;

      vec3 refldir = reflect(dir,norm);
      float refl = ray(hit+.1*refldir, refldir);

      if (refl<69.){
        vec3 reflM = M;
        vec3 rnorm = normal(hit+refldir*refl);
        float rdiff = length(.5+.5*sin(1.+rnorm))/sqrt(3.);
        col = mix(col, .1+reflM * rdiff, .5);
      }


    }

    //col += glow;

    // Output to screen
    //col=vec3(1)*rand2(uv);
    fragColor = vec4(col,1);


}

#define NUM_LAYERS 3.
#define WIDTH .008
#define ZOOM_FAR 10.
#define ZOOM_CLOSE 5.
#define NUM_RAIN_LAYERS 10.
#define RAIN_SPEED 2.
#define RAIN_WIDTH .007
#define RAIN_LENGTH .3
#define CLOUD_LAYERS 4.
#define CLOUD_ZOOM_CLOSE 1.
#define CLOUD_ZOOM_FAR 1.5

float rand( float n )
{
    return fract(sin(n * 52.48965902) * 247.1294857);
}

float rainLayer( vec2 uv, float layer )
{
    vec2 gv;
    gv.x = fract((uv.x + iTime * .04 * (NUM_LAYERS + 1.)) * 5.)-.5;
    float x_id = floor((uv.x + iTime * .04 * (NUM_LAYERS + 1.)) * 5.);

    gv.y = fract((uv.y + iTime * RAIN_SPEED) * 2.);
    float y_id = floor((uv.y + iTime * RAIN_SPEED) * 2.);

    // randomly offset the raindrop
    // x can range + or - .9
    // y can range 0 to 1-RAIN_LENGTH
    float n = rand(58. * x_id + 2.465 * y_id + 9.465 * layer);
    vec2 offset = vec2(1.8 * n - 1., (1.-RAIN_LENGTH) * fract(n * 79.));
    gv -= offset;

    float a = min(smoothstep(RAIN_WIDTH + 1./iResolution.x, RAIN_WIDTH, abs(gv.x)), smoothstep(RAIN_LENGTH, 0., gv.y));
    a *= smoothstep(-.001, 0., gv.y);
    //a += abs(gv.x) < .01 || abs(gv.y) < .01 ? .2  : 0.;
    return a;
}

float starLayer( vec2 uv, float zoom )
{
    // Create grid coordinates based on zoom
    vec2 gz = fract(uv * zoom) - .5;
    float id = floor(uv * zoom).x * 67. + floor(uv * zoom).y * 13.;

    return min(smoothstep(WIDTH + 1./iResolution.x, WIDTH, abs(gz.x)), smoothstep(WIDTH + 1./iResolution.y, WIDTH, abs(gz.y)));
}

float cloud( vec2 uv, float n)
{
    uv *= 1.7;
    float a;
    // Each cloud is a rounded box with a random height between 1 and 2
    float height = rand(n)+1.;
    n = fract(n * 97.);
    vec2 offset = vec2(0., 0.1);
    float d = length(max(abs((uv - offset)*vec2(.5, height)),.2) - .2);
    a = smoothstep(0.55, 0.5, abs(d / .6) * 5.0);

    // Then there's a cloud that's going to be a rounded box that's shorter and skinnier
    offset = vec2(rand(n)*.6-.3, -.14*(2.7-height));
    n = fract(n * 73.);
    height = rand(n) * 2. + 4.;
    n = fract(n * 32.);
    float width = rand(n)+.5;
    n = fract(n * 21.);
    d = length(max(abs((uv - offset)*vec2(width, height)),.2) - .2);
    a = max(a, smoothstep(0.55, 0.5, abs(d / .6) * 5.0));

    // little bit of cloud connecting the two
    offset.y += .1;
    offset.x *= .9;
    height = 2.;
    width = 4.;
    d = length(max(abs((uv - offset)*vec2(width, height)),.2) - .2);
    a = max(a, smoothstep(0.55, 0.5, abs(d / .6) * 5.0));

    a *= 1.5*(uv.y + .7);
    return clamp(pow(a, 8.), 0., 1.);
}

float cloudLayer( vec2 uv, float zoom, float layer )
{
    float a;
    vec2 gv = fract(uv * zoom)-.5;
    float id = floor(uv * zoom).x * 46.23 + floor(uv * 1.5).y * 9.567;
    // calculate random offset based on grid coordinate
    float n = rand(id + 24.354 * layer);
    vec2 offset = vec2((n*2.-1.) * .2, (fract(n * 25.)*2.-1.) * .3);
    a = cloud(gv-offset, n);
    return smoothstep(.5, .50001, n) * a;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1) irrespective of aspect ratio
    vec2 uv = (fragCoord - 0.5 * iResolution.xy)/iResolution.y;

    vec3 night_col = vec3(0.043, 0.003, 0.074);

    float timeOffset = iTime * .04;

    // Star Layers
    for (float i = 0.; i < NUM_LAYERS; i++) {
        // Calculate the zoom factor
        float zoom = mix(ZOOM_FAR, ZOOM_CLOSE, i/(NUM_LAYERS - 1.));

        // Calculate a parallax offset
        vec2 offset = vec2((i+1.) * timeOffset, 0.);

        // Draw the star layer
        float sl = starLayer(uv + offset, zoom) * (i + 1.)/(NUM_LAYERS);
        night_col = sl + night_col * (1. - sl);
    }

    // Rain Layer
    for (float i = 0.; i < NUM_RAIN_LAYERS; i++) {
        vec3 rainColor = vec3(0.180, 0.192, 0.325);
        float rl = rainLayer(uv, i);
        night_col = rainColor * rl + night_col * (1. - rl);
    }

    vec3 day_col = mix(vec3(0.486, 0.988, 0.988), vec3(0.121, 0.956, 1.), uv.y);

    // Cloud Layers
    for (float i = 0.; i < CLOUD_LAYERS; i++) {
        float zoom = mix(CLOUD_ZOOM_FAR, CLOUD_ZOOM_CLOSE, i/(CLOUD_LAYERS - 1.));
        vec2 offset = vec2(iTime * .01 * (i+1.), 0.2 * (i / (CLOUD_LAYERS - 1.)));
        float cl = cloudLayer(uv+offset, zoom, i) * (i + 1.) / (CLOUD_LAYERS);
        day_col = cl * vec3(1.) + day_col * (1. - cl);
    }

    // Decide if we're looking at day or night
    // 1 = night, 0 = day
    float day_night = smoothstep(.2, .2001, rand(floor(iTime*2.)+76.83));
    vec3 col = night_col * day_night + day_col * (1. - day_night);
    // Output to screen
    fragColor = vec4(col, 1.);
}

#define NUM_LAYERS 15.
#define WIDTH .005
#define ZOOM_FAR 9.
#define ZOOM_CLOSE .0

float rand( float n )
{
    return fract(sin(n * 592.) * 2467.) * .5 + .5;
}

vec4 starLayer( vec2 uv, float zoom, float layer, float blur )
{
    blur = clamp(pow(blur, 3.), 0., 1.);
    // Create grid coordinates based on zoom
    vec2 gz = fract(uv * zoom) - .5;
    float id = floor(uv * zoom).x * 67. + floor(uv * zoom).y * 13.;

    // Give each star a random color determined by the id
    float n = rand(id + layer * 87.);
    vec4 col = vec4(n, fract(n*54.), fract(n*347.), 1.);

    // Decide if the point is in the star or not
    col.a = (abs(gz.x) < WIDTH && abs(gz.y) < WIDTH) ? blur : 0.;
    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1) irrespective of aspect ratio
    vec2 uv = (fragCoord - 0.5 * iResolution.xy)/iResolution.y;

    // Set the background color - click on left side of screen its black, right its deep purple
    vec3 col = vec3(0.14117647058, 0.0862745098, 0.20392156862) * smoothstep(.499999, .5, iMouse.x / iResolution.x);

    for (float i = 0.; i < NUM_LAYERS; i++) {
        // Draw a star layer back to front
        // i = 0, star is far away
        // i = 1, star is close
        float zoom = mix(ZOOM_FAR, ZOOM_CLOSE, fract((i+iTime)/(NUM_LAYERS - 1.)));
        float blur = (zoom - ZOOM_FAR) / ((ZOOM_CLOSE+1.) - ZOOM_FAR);
        vec4 sl = starLayer(uv, zoom, i, blur);
        col = sl.rgb * 2. * sl.a + col * (1. - sl.a);
    }

    // Output to screen
    fragColor = vec4(col, 1.);
}

