void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0.0);
    vec3 col;  
    float t1 = 4.5/8.;

    vec2 uv = (fragCoord-iResolution.xy)/iResolution.y/t1/2.0;
    float tfijo=8.67;
    float ti=iTime;

    uv += vec2(18.67/2.0+ti,18.67/3.0+ti*0.5)/t1/8.0; //antes
    float t2 =tfijo;

    vec3 random1 = (hash31(3.+t2)-.5)/12.;
    vec3 random2 = (hash31(4.+t2)-.5)/12.;

    float offset = .5;
    float scale2 = 1.5;

    vec2 va1;
    vec2 va2;

    for(int c=0;c<3;c++){
        float scale = c1.z;
        for(int i=0;i<3;i++)
        {
            for(int k = 0; k < 3; k++){
                uv /= -scale2;
                uv.yx = triangle_wave(uv.yx-offset,scale)+triangle_wave(uv,scale);
                va1=vec2(random1[k]*random1[0],random2[k]);
                va2=vec2(random1[k],random2[k]);
                uv +=mix( va1, va2, sin(ti));
