void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 col=vec3(0.0);
    vec2 pG=fragCoord.xy / iResolution.xx * scaleFactor;
    vec2 n=floor(pG);
    vec2 f=fract(pG);

    for (int j= 0;j>= -8;j--)
        for (int i= -3;i<=3;i++)
        {
            vec2 g=vec2(float(i),float(j));
            vec2 growFrom=n+g;//hash2(n+g)*0.8;

            vec2 growTo=growFrom+windFactor*vec2(sin(0.7*iTime ))+ (n+g); //+ (n+g).x/10.0,0);

            if (pG.y>growTo.y || pG.y<growFrom.y) continue;

            float rely=(pG.y-growFrom.y)/(growTo.y-growFrom.y);

            float gr=log2(2.0-abs(rely))*grassWidth;
            float dx= -pow(rely,2.0)*(growTo.x-growFrom.x);
            #define ival 0.08
            col=mix(col,vec3(0.0,0.0,0.0)*rely,smoothstep(gr+2.0*ival,gr,abs(growFrom.x+dx-pG.x)));
            col=mix(col,vec3(0.0,1.0,0.0)*rely,smoothstep(gr+ival,gr-ival,abs(growFrom.x+dx-pG.x)));
        }

    fragColor=vec4(col,1.0);
}

