float thc(float a, float b) {
    return tanh(a * cos(b)) / tanh(a);
}

float ths(float a, float b) {
    return tanh(a * sin(b)) / tanh(a);
}

vec2 thc(float a, vec2 b) {
    return tanh(a * cos(b)) / tanh(a);
}

vec2 ths(float a, vec2 b) {
    return tanh(a * sin(b)) / tanh(a);
}

vec3 pal( in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d )
{
    return a + b*cos( 6.28318*(c*t+d) );
}

float h21 (vec2 a) {
    return fract(sin(dot(a.xy, vec2(12.9898, 78.233))) * 43758.5453123);
}

float mlength(vec2 uv) {
    return max(abs(uv.x), abs(uv.y));
}

#define pi 3.14159

float func(vec2 uv, float time) {
    vec2 p = 0.35 * vec2(thc(4., time), ths(4., time));
    float d = length(uv-p);
    float k = 1.1 * thc(4.,3. * uv.x + time);
    float s = smoothstep(-k, k, 0.82 - d);
    return s;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;
    float time = -4. * iTime;
    float a = atan(uv.y, uv.x);
    float r = log(length(uv));

    //vec2 ipos = floor(iTime * uv) + 0.5;
    uv = 0.5 * thc(2. + 1.5 * cos(10. * mlength(uv) + time),
                   10. * uv) - 0. * thc(1.,iTime);

    float s = func(uv, time + a + r * 24.+ length(uv) * 0100.);
    float s2 = func(fract(9. * uv) - 0.5, 8. * r + time);

    s = max(s-s2, 0.08);
    //s = s2;//mix(s,s2, .5 + .5 * thc(4., iTime));

    vec3 col = s2 * pal(ths(r,length(uv) * r - iTime) + s, vec3(1.), vec3(1.), vec3(1.), s * vec3(0.,0.33,0.66));

    // Output to screen
    fragColor = vec4(col,1.0);
}

// "Hash without Sine" by Dave_Hoskins:
// https://www.shadertoy.com/view/4djSRW
float hash12(vec2 p){
	vec3 p3  = fract(vec3(p.xyx) * .1031);
    p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.x + p3.y) * p3.z);
}

float rndUni(vec2 p){
    return hash12(p);
}

float rndAdd(vec2 p){
    float a = hash12(p);
    float b = hash12(p+1.);
    return (a+b)*.5;
}

float rndMul1(vec2 p){
    float a = hash12(p);
    return (a*a*a);
}

float rndMul2(vec2 p){
    float a = hash12(p);
    float b = hash12(p+1.);
    return (a*b);
}

float rndSqrt(vec2 p){
    float a = hash12(p);
    return sqrt(a);
}

float rndPow(vec2 p){
    float a = hash12(p);
    float b = hash12(p+1.);
    //return pow(a, (1.+sin(iTime))*8.);

    return pow(rndMul2(p), .25);
}

float rndGauss(vec2 p) {
    float a = hash12(p);
    float b = hash12(p+1.);
    float value =
    sqrt(-2.*log(a)) *
    sin(2.*acos(-1.) * b);
    value = (value + 3.) / 6.;
    return value;
}


#define drawHistgram(RND_TYPE) {\
    float maxth = ceil(uv.x*float(RESOLUTION))/float(RESOLUTION);\
    float minth = maxth-(1./float(RESOLUTION));\
    float cnt = 0.;\
    float t = fract( iTime );\
    for(int i = 0; i<MAX_SAMPLE; i++) {\
        float rnd = RND_TYPE(vec2(i,domain.x)+t);\
        if(rnd>=minth && rnd<maxth)cnt++;\
    }\
    cnt /= float(MAX_SAMPLE);\
    res = (cnt>uv.y/SCALE_Y) ? 1.: 0.;\
}\

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    const int VARIATIONS = 7;
    vec2 domain = vec2(iResolution.x/float(VARIATIONS), iResolution.y);
    int id = int(floor(fragCoord.x/domain.x));
    fragCoord.x = mod(fragCoord.x, domain.x);
    vec2 uv = fragCoord/domain;

    float res = 0.;
    const int MAX_SAMPLE = 3000;
    const int RESOLUTION = 30;
    const float SCALE_Y = 1.;
    if(id==0) drawHistgram(rndUni);
    if(id==1) drawHistgram(rndAdd);
    if(id==2) drawHistgram(rndMul1);
    if(id==3) drawHistgram(rndMul2);
    if(id==4) drawHistgram(rndSqrt);
    if(id==5) drawHistgram(rndPow);
    if(id==6) drawHistgram(rndGauss);
    //drawHistgram(rndGauss);
    vec3 col = mix(vec3(0.102,0.102,0.102), vec3(.8, .77, .7), res);
    col = max(col, vec3(.8, .77, .7)*.3* ((fragCoord.x>=domain.x-2.) ? vec3(1) : vec3(0)));
    fragColor = vec4(col,1.0);
}

