// https://www.iquilezles.org/www/articles/distfunctions/distfunctions.htm
float sdTorus( vec3 p, vec2 t )
{
  vec2 q = vec2(length(p.xz)-t.x,p.y);
  return length(q)-t.y;
}

// https://www.iquilezles.org/www/articles/distfunctions/distfunctions.htm
float sdLink( vec3 p, float le, float r1, float r2 )
{
  vec3 q = vec3( p.x, max(abs(p.y)-le,0.0), p.z );
  return length(vec2(length(q.xy)-r1,q.z)) - r2;
}

// https://www.iquilezles.org/www/articles/distfunctions/distfunctions.htm
float sdCappedCylinder( vec3 p, float h, float r )
{
  vec2 d = abs(vec2(length(p.yz),p.x)) - vec2(r,h);
  return min(max(d.x,d.y),0.0) + length(max(d,0.0));
}

// https://www.iquilezles.org/www/articles/distfunctions/distfunctions.htm
float sdBox( vec3 p, vec3 b )
{
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}

// https://www.iquilezles.org/www/articles/distfunctions/distfunctions.htm
float sdEllipsoid( vec3 p, vec3 r )
{
  float k0 = length(p/r);
  float k1 = length(p/(r*r));
  return k0*(k0-1.0)/k1;
}

// https://www.iquilezles.org/www/articles/distfunctions/distfunctions.htm
float sdCappedCone( vec3 p, float h, float r1, float r2 )
{
  vec2 q = vec2( length(p.xz), p.y );
  vec2 k1 = vec2(r2,h);
  vec2 k2 = vec2(r2-r1,2.0*h);
  vec2 ca = vec2(q.x-min(q.x,(q.y<0.0)?r1:r2), abs(q.y)-h);
  vec2 cb = q - k1 + k2*clamp( dot(k1-q,k2)/dot2(k2), 0.0, 1.0 );
  float s = (cb.x<0.0 && ca.y<0.0) ? -1.0 : 1.0;
  return s*sqrt( min(dot2(ca),dot2(cb)) );
}

float sdSimpleCylinder(vec3 p, float r, float h){
    float d = length(p.xz)-r;
    d = max(abs(p.y)-h,d);
    return d;
}

float baseOutsole(vec3 p, float h){
    vec3 prevP = p;

    p.xz*=Rot(radians(17.0));
    p.z*=0.4;
    p.x*=0.57;
    p.x+=0.03;
    p.z-=0.07;

    float d =sdSimpleCylinder(p,0.1,h);

    p = prevP;
    p.xy*=Rot(radians(90.0));
    p.y*=1.5;
    p.z*=1.2;
    float d2 = sdCappedCylinder(p-vec3(0.0,0.0,-0.25),h,0.2);
    d = opSmoothUnion(d, d2,0.13);

    p = prevP;
    d = max(abs(p.y)-h,d);

    return (d)*0.6;
}

float outsole(vec3 p){
    p.z*=0.9;
    vec3 prevP = p;

    float d = baseOutsole(p-vec3(0.0,0.02,0.),0.06);

    p = prevP;
    float d2 = baseOutsole(p-vec3(0.0,0.035,0.0),0.035);
    d = min(d,d2);

    p = prevP;
    float a  = radians(4.0);
    p.y-=0.06;
    float mask = dot(p.yz,vec2(cos(a),sin(a)));
    d = max(mask,d);

    p = prevP;
    a  = radians(-8.0);
    p.y+=0.07;
    mask = dot(p.yz,vec2(cos(a),sin(a)));
    d = max(-mask,d);

    p = prevP;
    a  = radians(10.0);
    p.y+=0.08;
    mask = dot(p.yz,vec2(cos(a),sin(a)));
    d = max(-mask,d);

    float k = 0.06;
    p = prevP;
    p.y*=0.7;
    p.xz*=Rot(radians(90.0));
    p.yz*=Rot(radians(90.0));
    d2 = sdLink(p-vec3(0.0,0.01,-0.04),0.08,0.04,0.015);
    d = opSmoothUnion(d,d2,k);
    d2 = sdLink(p-vec3(0.15,0.0,-0.04),0.12,0.04,0.015);
    d = opSmoothUnion(d,d2,k);
    d2 = sdLink(p-vec3(-0.15,0.005,-0.04),0.17,0.04,0.015);
    d = opSmoothUnion(d,d2,k);
    p.xz*=Rot(radians(-15.0));
    d2 = sdLink(p-vec3(0.28,0.0,-0.1),0.065,0.04,0.015);
    d = opSmoothUnion(d,d2,k);

    p = prevP;
    p.y*=0.7;
    p.xz*=Rot(radians(90.0));
    p.yz*=Rot(radians(90.0));
    p.xz*=Rot(radians(10.0));
    d2 = sdLink(p-vec3(-0.28,-0.02,-0.08),0.14,0.04,0.015);
    d = opSmoothUnion(d,d2,k);
    d2 = sdLink(p-vec3(-0.38,-0.03,-0.08),0.08,0.02,0.012);
    d = opSmoothUnion(d,d2,k);

    return d;
}


float upperBase(vec3 p){
    p.z*=0.9;

    vec3 prevP2 = p;
    p.x+=cos(p.y*70.0)*0.005;
    vec3 prevP = p;

    //p.x*=1.;
    p.z*=0.8;

    float d =sdCappedCone(p-vec3(0.0,0.18,-0.15),0.13,0.15,0.06);

    p = prevP;
    p.xz*=Rot(radians(15.0));
    p.yz*=Rot(radians(-6.0));
    p.x*=1.2;
    p.z*=0.8;

    float d2 = sdEllipsoid(p-vec3(-0.05,0.1,0.14),vec3(0.2,0.08,0.2));
    d = opSmoothUnion(d,d2,0.1);//0.1

    p = prevP;
    p.xz*=Rot(radians(0.0));
    p.x*=1.1;
    p.z*=0.8;
    p.yz*=Rot(radians(-35.0));

    d2 = sdEllipsoid(p-vec3(0.0,0.21,-0.1),vec3(0.1,0.04,0.15));

    d = opSmoothUnion(d,d2,0.17);//0.15

    p = prevP2;
    p.xy*=Rot(radians(90.0));
    p.xz*=Rot(radians(18.0));
    p.y*=1.5;
    p.z*=1.0;
    d2 = sdCappedCylinder(p-vec3(-0.1,0.0,-0.2),0.001,0.2);
    d = opSmoothUnion(d, d2,0.07);

    p = prevP2;
    p.xy*=Rot(radians(90.0));
    p.xz*=Rot(radians(15.0));
    p.y*=1.3;
    p.z*=0.99;
    d2 = sdCappedCylinder(p-vec3(-0.04,0.0,-0.22),0.001,0.2);
    d = opSmoothUnion(d, d2,0.07);

    return d;
}

float upper(vec3 p){
    vec3 prevP = p;
    float d = upperBase(p);
    p.x*=1.5;
    p.y*=1.1;
    p.z*=1.12;

    float d2 = upperBase(p-vec3(0.0,0.02,0.0));
    d = max(-d2,d);

    p = prevP;

    p.x*=1.6;
    p.z*=0.7;

    d2 =sdCappedCone(p-vec3(-0.01,0.25,-0.1),0.14,0.14,0.09)*0.6;
    d = max(-d2,d);

    p = prevP;
    float a  = radians(8.0);
    p.y-=0.31;
    float mask = dot(p.yz,vec2(cos(a),sin(a)));
    d = max(mask,d);

    p = prevP;
    p.y+=0.01;
    p.z-=0.005;
    p.yz*=Rot(radians(-12.0));
    p.x = mod(p.x,0.2)-0.1;
    p.y = mod(p.y,0.1)-0.05;
    p.z = mod(p.z,0.2)-0.1;
    p.x*=0.8;
    p.y*=2.0;
    p.z*=0.8;


    d2 = length(p)-0.05;
    d = max(-d2,d);

    p = prevP;
    p.yz*=Rot(radians(-12.0));
    p.x*=1.2;
    p.y*=1.2;
    p.z*=0.65;
    d2 = sdTorus(p-vec3(-0.005,0.35,-0.115),vec2(0.1,0.02));

    d = opSmoothUnion(d,d2,0.04);

    p = prevP;
    p.x*=1.4;
    p.z*=0.75;
    d2 =sdSimpleCylinder(p-vec3(-0.005,0.35,-0.09),0.1,0.2);
    d = max(-d2,d);

    return d*0.8;
}

vec2 GetDist(vec3 p) {
    p+=noise3d(p*600.0)*0.0002;
    vec3 prevP = p;

    p.y+=0.075;
    //p.z+=0.1;
    float d = outsole(p);
    float d2 = upper(p-vec3(0.0,0.0,0.0));
    d =  opSmoothUnion(d,d2,0.03);

    vec2 model = vec2(d,MATERIAL);

    return model;
}

vec2 RayMarch(vec3 ro, vec3 rd, float side, int stepnum) {
    vec2 dO = vec2(0.0);

    for(int i=0; i<stepnum; i++) {
        vec3 p = ro + rd*dO.x;
        vec2 dS = GetDist(p);
        dO.x += dS.x*side;
        dO.y = dS.y;

        if(dO.x>MAX_DIST || abs(dS.x)<SURF_DIST) break;
    }

    return dO;
}

vec3 GetNormal(vec3 p) {
    float d = GetDist(p).x;
    vec2 e = vec2(.001, 0);

    vec3 n = d - vec3(
        GetDist(p-e.xyy).x,
        GetDist(p-e.yxy).x,
        GetDist(p-e.yyx).x);

    return normalize(n);
}

vec3 R(vec2 uv, vec3 p, vec3 l, float z) {
    vec3 f = normalize(l-p),
        r = normalize(cross(vec3(0,1,0), f)),
        u = cross(f,r),
        c = p+f*z,
        i = c + uv.x*r + uv.y*u,
        d = normalize(i-p);
    return d;
}

// https://www.shadertoy.com/view/3lsSzf
float calcOcclusion( in vec3 pos, in vec3 nor )
{
    float occ = 0.0;
    float sca = 1.0;
    for( int i=ZERO; i<3; i++ )
    {
        float h = 0.01 + 0.15*float(i)/4.0;
        vec3 opos = pos + h*nor;
        float d = GetDist( opos ).x;
        occ += (h-d)*sca;
        sca *= 0.95;
    }
    return clamp( 1.0 - 2.0*occ, 0.0, 1.0 );
}

vec3 diffuseMaterial(vec3 n, vec3 rd, vec3 p, vec3 col) {
    float occ = calcOcclusion(p,n);
    vec3 diffCol = vec3(0.0);
    vec3 lightDir = normalize(vec3(1,2,-2));
    float diff = clamp(dot(n,lightDir),0.0,1.0);
    float shadow = step(RayMarch(p+n*0.3,lightDir,1.0, 15).x,0.9);
    float skyDiff = clamp(0.5+0.5*dot(n,vec3(0,1,0)),0.0,1.0);
    float bounceDiff = clamp(0.5+0.5*dot(n,vec3(0,-1,0)),0.0,1.0);
    diffCol = col*vec3(-0.5)*diff*shadow*occ;
    diffCol += col*vec3(1.0,1.0,0.9)*skyDiff*occ;
    diffCol += col*vec3(0.3,0.3,0.3)*bounceDiff*occ;
    diffCol += col*pow(max(dot(rd, reflect(lightDir, n)), 0.0), 20.)*occ; // spec

    return diffCol;
}

float Hash21(vec2 p) {
    p = fract(p*vec2(234.56,789.34));
    p+=dot(p,p+34.56);
    return fract(p.x+p.y);
}

