#define S2(d,b) smoothstep(antialiasing(1.0),b,d)

///----------Operacion de Distancia--------
float intersectSDF(float distA, float distB)
	{ return max(distA, distB);}
float unionSDF(float distA, float distB)
	{ return min(distA, distB);}
float differenceSDF(float distA, float distB)
	{ return max(distA, -distB);}

vec3 DrawFigBorde(vec3 pColObj, vec3 colOut, float distObj )
{
  colOut = mix(colOut,pColObj ,S2( distObj,0.0));
  //colOut = mix(colOut,vec3(0.0) ,S2(abs( distObj)-0.005,0.0));
  colOut = mix(colOut,vec3(0.0) ,S2(abs( distObj)-0.01,0.0));
  return colOut;
}

vec3 DrawFigBordeCol(vec3 pColObj, vec3 colOut, float distObj , vec3 colBorde )
{
  colOut = mix(colOut,pColObj ,S2( distObj,0.0));
  colOut = mix(colOut,colBorde ,S2(abs( distObj)-0.05,0.0));
  return colOut;
}

vec3 DrawFig(vec3 pColObj, vec3 colOut, float distObj )
{  colOut = mix(colOut,pColObj ,S2( distObj,0.0));
return colOut;
}

float sdBox( in vec2 p, in vec2 b )
{ vec2 d = abs(p)-b;   return length(max(d,0.0)) + min(max(d.x,d.y),0.0);}

float sdCircle( vec2 p, float r )
{ return length(p) - r;}

float ellipse(vec2 p, vec2 focus0, vec2 focus1, float radius) 
{   return distance(p, focus0) + distance(p, focus1) - distance(focus0, focus1) * radius; }

float HacerPeloDown1(vec2 p )
{   float d1= sdCircle(p-vec2(0.0,0.0), 0.5 );
    float d2= sdCircle(p-vec2(0.2,-0.35),0.5 );
    float dif1= differenceSDF(d1, d2);
    return dif1;
}
float HacerPeloUp1(vec2 p )
{   float d1= sdCircle(p-vec2(0.0,0.0), 0.5 );
    float d2= sdCircle(p-vec2(0.42,0.35),0.75 );
    float dif1= differenceSDF(d1, d2);
    return dif1;
}

float HacerRostro(vec2 p)
{   vec2 focus0=vec2(0.0,0.2);
    vec2 focus1=vec2(0.0,0.8);
    float radius=2.0;
    float d1= ellipse(p,focus0,focus1,radius);
    return d1;
}

float HacerOjo(vec2 p)
{   vec2 focus0=vec2(0.0,0.2);
    vec2 focus1=vec2(0.0,0.3);
    float radius=1.0;
    float d1= ellipse(p,focus0,focus1,radius);
    return d1;
}

float HacerBoca(vec2 p)
{   vec2 focus0=vec2(0.01,0.2);
    vec2 focus1=vec2(0.2,0.25);
    float radius=1.0;
    float d1= ellipse(p,focus0,focus1,radius);
    return d1;
}


vec3 sd2d(vec2 uv, vec3 col)
{   float d0=HacerRostro(uv-vec2(0.08,-0.7));
    col= DrawFigBorde(vec3(0.7,0.6,0.1)-uv.y*0.25, col, d0 );

    float d1,d2,d3,d4,d5,d6;
    d1= HacerPeloDown1(uv-vec2(0.05,0.0));
    d2= HacerPeloUp1(uv-vec2(0.3,0.48));
    d3= HacerPeloUp1(vec2(-uv.x, uv.y)-vec2(-0.2,0.25));
    d4= HacerPeloUp1(uv*1.5-vec2(-0.2,0.45));
    d5= HacerPeloDown1(vec2(-uv.x, uv.y)-vec2(-0.4,-0.1) );
    d6= HacerPeloUp1(vec2(-uv.x, uv.y)*1.2-vec2(0.6,0.25));

    float dtdo=unionSDF(d1, d2);
    dtdo=unionSDF(dtdo, d3);
    dtdo=unionSDF(dtdo, d4);
    dtdo=unionSDF(dtdo, d5);
    dtdo=unionSDF(dtdo, d6);
    col=DrawFigBorde(vec3(1.0,0.0,0.0)-uv.y, col,dtdo);

    float dojoI= HacerOjo(uv-vec2(0.0,-0.6));
    float dojoD= HacerOjo(uv-vec2(0.3,-0.6));
    col=DrawFigBorde(vec3(0.0,0.0,0.0), col,dojoI);
    col=DrawFigBorde(vec3(0.0,0.0,0.0), col,dojoD);

     float dboca= HacerBoca(uv-vec2(0.0,-0.7));
    col=DrawFigBorde(vec3(0.0,0.0,0.0), col,dboca);
 return col;
}   

