#define pi 3.1415926535897932384626433832795

float veiershrass (float x){
    float sum=0.0;
    float a=0.056;
    float b=0.044;
    for(int i=0; i<100; i++){
        // Time varying pixel color
        sum += pow(0.5,float(i))*cos(pow(a,float(i))*pi*x);
    }
    return sum;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec3 col = vec3(0.25*veiershrass(uv.x*iTime)+0.5);


    // Output to screen
    fragColor = vec4(col,1.0);
}

// optical illusion inspired from @coachly.de
// the discs are not changing in size at all.
// slight variation of this can give the illusion of movement

#define PI 3.14159

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 position = mod( fragCoord/iResolution.xy, vec2(.5,1.) );

	float angle = (atan(position.y-.5,position.x-.25)+PI)/2./PI;
	angle = mod(angle+iTime*2.4,1.);

	float r=.5,g=.5,b=.5,l=length(position-vec2(.25,.5));

	if(l>.13&&l<.2) {
		if ( angle<.25) {  // y to r
			g=angle*4.;
			r=1.;
			b=0.;
		}			
		else if(angle<.5) {  // c to y
			g=1.;
			b=(angle-.25)*4.;
			r=1.-b;
		}
		else if(angle<.75) { // b to c
			b=1.;
			g=1.-(angle-.5)*4.;
			r=0.;
		}
		else {		// r to b
			r=(angle-.75)*4.;
			b=1.-r;
			g=0.;
		}
	}

    if(l>.1975&&l<.2) {	// outer rim
		float s = .3 + abs(cos(PI*angle-.03*mod(iTime,1.)));
		s*=s; r*=s; g*=s; b*=s;
	}
	else if(l>.127&&l<.13) {	// inner rim
		float s = .3 + abs(sin(PI*angle-.03*mod(iTime,1.)));
		s*=s; r*=s; g*=s; b*=s;
	}

	/*if(l>.1975&&l<.2) {	// outer rim
		float s=1.3*abs(sin(PI*angle-sin(iTime)));
		r*=s; g*=s; b*=s;
	}
	else if(l>.127&&l<.13) {	// inner rim
		float s=1.3*abs(sin(PI*angle+sin(iTime*1.23456)));
		r*=s; g*=s; b*=s;
	}*/

	fragColor = vec4(r,g,b,1);
}

mat2 opRotate(float a) {
	float s = sin(a);
	float c = cos(a);
	return mat2(c, -s, s, c);
}
