float sdSphere( in vec3 p, in float r )
{
    return length(p)-r;
}


float sdBox( vec3 p, float b)
{
  vec3 d = abs(p) - b;
  return min(max(d.x,max(d.y,d.z)),0.0) + length(max(d,0.0));
}


float sdDiamond( vec3 p, float b)
{
  vec3 rot = p;
  rot.xy = rot.xy * opRotate(0.785);
  rot.xz = rot.xz * opRotate(.616);
  rot.yz = rot.yz * opRotate(-.616);
  vec3 d = abs(rot) - b;
  return min(max(d.x,max(d.y,d.z)),0.0) + length(max(d,0.0));
}

float sdOctahedron( vec3 p, float s)
{
  p = abs(p);
  float d = (p.x+p.y+p.z-s)*0.57735027;
  return d;
}

float opRepLimBox( in vec3 p, in float c, in vec3 l, float bb )
{
    vec3 q = p-c*clamp(round(p/c),-l,l);
    return sdBox(q, bb);
}

