// geometry controls
const float bothscale = 1.;
const float boxscale = 0.125 * bothscale;
const float diascale = 0.38 * bothscale;
const float timeoffset = 1.5; // -2 to 2
const int pattern = 999; // pattern picker, 1-whatever, 999 to test
const float dancespeed = 1.5;
// render controls
const float render_depth = 13.0; // range 9ish to 11, lower = goopy
const int render_steps = 64; // options: 4, 8, 16, 32. low = goopy
const float lines = 0.0; // 0 to ~3.5
const int colormode = 2;

float cubeDance(in float time, in vec3 pos) {
    float size = 0.0;
    if (pattern == 1) {
        size += sin(time + pos.y * 3.15 * -.75);
        if (mod(pos.x + pos.z,2.0) < 0.5) size *= -1.;
    } else if (pattern == 2)
        size += sin(time + pos.x * pos.y * (3.0 + 2.0 * pos.z));
    else if (pattern == 3) 
        size += pos.y*cos(time*0.5);    
    else {
        vec3 normpos = (pos+0.5) * 0.4 - 1.; // -1 to 1
        vec3 centerpos = abs(normpos) * 2.0 - 1.0;
        size = sin(centerpos.z + centerpos.x + centerpos.y + time);
    }
    return size; // -1 to 1
}

float map(in vec3 pos)
{
    float d = 1e10;

    pos -= vec3(1.,1.,1.);

    for(int x=0;x<5;x++)
    for(int y=0;y<5;y++)
    for(int z=0;z<5;z++)
    {
        vec3 cubepos = vec3(float(x),float(y),float(z));
        float temptime = iTime*dancespeed;

        float boxsize = cubeDance(temptime, cubepos);
        boxsize = clamp(boxsize*1.4+0.2, -1., 1.0);
        boxsize = sin(boxsize)*boxscale+boxscale;

        float diasize = cubeDance(temptime+timeoffset, cubepos);
        diasize = clamp(diasize*1.4+0.2, -1.1, 1.0);
        diasize = sin(diasize)*diascale+diascale;
        float d2 = sdBox(
            pos-vec3(
                -float(x) * 0.5,
                float(y) * 0.5,
                -float(z) * 0.5
            ), 
            boxsize
        ); 
        float d3 = sdOctahedron(
            pos-vec3(
                -float(x) * 0.5,
                float(y) * 0.5,
                -float(z) * 0.5
            ), 
            diasize
        );
        // d3 = 0.; // show box only
        // d2 = 0.; // show diamond only
        d = min( d, max( d2, d3 ));
    }

    return d;
}

// http://iquilezles.org/www/articles/normalsSDF/normalsSDF.htm
vec3 calcNormal( in vec3 pos )
{
    const float ep = 0.0001;
    vec2 e = vec2(1.0,-1.0)*0.5773;
    return normalize( e.xyy*map( pos + e.xyy*ep ) + 
					  e.yyx*map( pos + e.yyx*ep ) + 
					  e.yxy*map( pos + e.yxy*ep ) + 
					  e.xxx*map( pos + e.xxx*ep ) );
}

// http://iquilezles.org/www/articles/rmshadows/rmshadows.htm
float calcSoftshadow( in vec3 ro, in vec3 rd, float tmin, float tmax, const float k )
{
	float res = 1.0;
    float t = tmin;
    for( int i=0; i<50; i++ )
    {
		float h = map( ro + rd*t );
        res = min( res, k*h/t );
        t += clamp( h, 0.01, 1.00 );
        if( res<0.005 || t>tmax ) break;
    }
    return clamp( res, 0.0, 1.0 );
}


#define AA 1

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
   vec3 tot = vec3(0.0);

    #if AA>1
    for( int m=0; m<AA; m++ )
    for( int n=0; n<AA; n++ )
    {
        // pixel coordinates
        vec2 o = vec2(float(m),float(n)) / float(AA) - 0.5;
        vec2 p = (-iResolution.xy + 2.0*(fragCoord+o))/iResolution.y;
        #else    
        vec2 p = (-iResolution.xy + 2.0*fragCoord)/iResolution.y;
        #endif

        vec3 ro = vec3(.0,5.8,10.);
        vec3 rd = normalize(vec3(p-vec2(.0,2.05),-5.));

        ro.xz *= opRotate(iTime * .5 + p.y * 1.2);
        rd.xz *= opRotate(iTime * .5 + p.y * 1.2);

        float t = 8.5;
        for( int i=0; i<render_steps; i++ )
        {
            vec3 p = ro + t*rd;
            float h = map(p);
            if( abs(h)<0.005 || t>render_depth ) break;
            t += h;
        }

        vec3 col = vec3(0.0);

        if( t<render_depth + lines)
        {
            vec3 pos = ro + t*rd;
            vec3 nor = calcNormal(pos);
            vec3 lig = vec3(0.8);
            float dif = clamp(dot(nor,lig),0.0,1.0);
            float sha = calcSoftshadow( pos, lig, 0.001, 0.5, 12.0 );
            float amb = 0.5 + 0.5*nor.y;

            if (colormode == 1){
                float amb = 0.5 + 0.5*nor.y;
                col = vec3(sha + amb) / 2.;
                col -= 0.5;
                col *= 2.0;
                col = pow(col, vec3(0.5));
                col /= 2.0;
                col += 0.5;
                col = vec3(step(amb,0.3));
            } else if (colormode == 2){
                float amb = 0.5 + 0.5*nor.y;
                col = vec3(sha + amb) / 2.;
                col -= 0.5;
                col *= 2.0;
                col = pow(col, vec3(0.25));
                col /= 2.0;
                col += 0.5;
            } else {
                col = vec3(0.4)*amb + 
                      vec3(.80)*dif*sha;
            }

        }

        col = sqrt( col );

	    tot += col;
    #if AA>1
    }
    tot /= float(AA*AA);
    #endif

	fragColor = vec4( tot, 1.0 );
}

