void mainImage(out vec4 f,in vec2 g){
vec2 uv=(g*2.-iResolution.xy)/min(iResolution.x,iResolution.y);
float d=.25*(1.+cos(iTime*4.+uv.x*3.*6.28318))*(1.+cos(iTime*3.+uv.y*3.*6.28318));
float k=7.+3.*sin(iTime*8.);vec3 col=vec3(exp((d-.95)*k));f=vec4(col,1.);}

vec3 hash3( vec2 p )
{
    vec3 q = vec3( dot(p,vec2(100,300)), 
				   dot(p,vec2(250,150)), 
				   dot(p,vec2(400,500)) );
	return fract(sin(q) * 40000.54);
}

float voronoise( in vec2 p, float u, float v )
{
	float k = 1.0 + 63.0 * pow(1.0 - v,6.0);

    vec2 i = floor(p);
    vec2 f = fract(p);

	vec2 a = vec2(0.0,0.0);
    for( int y=-2; y<=2; y++ )
    for( int x=-2; x<=2; x++ )
    {
        vec2  g = vec2( y, x );
		vec3  o = hash3( i + g ) * vec3(u,u,1.0);
		vec2  d = g - f + o.xy;
		float w = pow( 1.0 - smoothstep(0.0,1.005, length(d)), k );
		a += vec2(o.z * w,w);
    }

    return a.x/a.y;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord / iResolution.xx;

    vec2 p = 0.5 - 0.5 * sin( iTime * vec2(1.0,0.5) );

	if( iMouse.w > 0.1 ) p = vec2(0.0,1.0) + vec2(1.0,-1.0) * iMouse.xy/iResolution.xy;

	p = p*p*(2.5 - 1.5 * p);
	p = p*p*(2.5 - 1.5 * p);
	p = p*p*(2.5 - 1.5 * p);

	float f = voronoise( 45.0 * uv, p.x, p.y );

	fragColor = vec4( f, f, f, 1.0 );
}

vec2 random2( vec2 p ) {
    return fract(tan(vec2(dot(p,vec2(50,150)),dot(p,vec2(300,150)))));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float amp = 0.15;



    float cellCount = 4.0;    

    float ampOfMove = 9.;
    float dist = 2.;

    float seperation = 1.4;
    vec2 mouse = vec2(iMouse)/vec2(iResolution);


    vec2 uv = fragCoord/iResolution.xy;
    vec3 color = vec3(1. * mouse.x, 0, 1.0 * mouse.y);
    uv *= cellCount;

    vec2 movement = fract(uv);
    vec2 intMove = floor(uv);


    for (int x = 0; x <3; x++) {
        for (int y = 0; y<3; y++)
        {
            vec2 neighbour = vec2(float(y), float(x));

            vec2 pt = random2(neighbour + intMove);
            pt =  amp*(cos(iTime + ampOfMove*pt));
            dist = min(dist, length(neighbour + pt - movement));
        }
    };

    color += seperation*dist;

    fragColor = vec4(color,3.0);
}

#define time iTime
#define PI     3.14159265

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

   vec2 uv = fragCoord.xy;

   float cols[3] = float[3](0.0,0.0,0.0);


   float rVariableSize = max(cos(iTime*0.4), 0.4);
   float gVariableSize = max(cos(PI + iTime*0.4), 0.4);
   float bVariableSize = max(cos(PI/2.0 + iTime*0.4), 0.4);


   float size = 1.0;
   float spinDistance = 150.0*cos(iTime);
   float spinDistance2 = 100.0*cos(iTime);
   float spinDistance3 = 50.0*cos(iTime);

   for (int i = 0; i < 4; i++) {
      float x = float(i);
      vec2 center;
      vec2 displacement;
      if (i % 3 == 0) {
          center = vec2(iResolution.x/2.0 + (float(i&1) * cos(iTime) * spinDistance), iResolution.y/2.0 + (float(i&1)-1.0 * sin(iTime) * spinDistance));
          displacement = uv - center;
          cols[0] += 1.0/(length(displacement)*0.1*size);
      } else {
          center = vec2(iResolution.x/2.0 + (float(i&1) * sin(iTime) * spinDistance), iResolution.y/2.0 + (float(i&1)-1.0 * -cos(iTime) * spinDistance));
          displacement = uv - center;
          cols[0] += 1.0/(length(displacement)*0.1*size);
      }
   }

   for (int i = 0; i < 4; i++) {
      float x = float(i);
      vec2 center;
      vec2 displacement;
      if (i % 3 == 0) {
          center = vec2(iResolution.x/2.0 + (float(i&1) * -cos(iTime) * spinDistance2), iResolution.y/2.0 + (float(i&1)-1.0 * sin(iTime) * spinDistance2));
          displacement = uv - center;
          cols[1] += 1.0/(length(displacement)*0.1*size);
      } else {
          center = vec2(iResolution.x/2.0 + (float(i&1) * sin(iTime) * spinDistance2), iResolution.y/2.0 + (float(i&1)-1.0 * cos(iTime) * spinDistance2));
          displacement = uv - center;
          cols[1] += 1.0/(length(displacement)*0.1*size);
      }
   }

   for (int i = 0; i < 4; i++) {
      float x = float(i);
      vec2 center;
      vec2 displacement;
      if (i % 3 == 0) {
          center = vec2(iResolution.x/2.0 + (float(i&1) * cos(iTime + PI/4.0) * spinDistance3), iResolution.y/2.0 + (float(i&1)-1.0 * -sin(iTime + PI/4.0) * spinDistance3));
          displacement = uv - center;
          cols[2] += 1.0/(length(displacement)*0.1*size);
      } else {
          center = vec2(iResolution.x/2.0 + (float(i&1) * -sin(iTime + PI/4.0) * spinDistance3), iResolution.y/2.0 + (float(i&1)-1.0 * cos(iTime + PI/4.0) * spinDistance3));
          displacement = uv - center;
          cols[2] += 1.0/(length(displacement)*0.1*size);
      }
   }

   for (int i = 1; i <= 4; i++) {
      float x = float(i);
      vec2 center;
      vec2 displacement;
      if (i % 2 == 0) {
          center = vec2(iResolution.x/2.0 + cos(iTime * 1.0/x) * spinDistance, iResolution.y/2.0 + cos(iTime * 1.0/x) * spinDistance);
          displacement = uv - center;
      } else {
          center = vec2(iResolution.x/2.0 + sin(iTime * 1.0/x) * spinDistance, iResolution.y/2.0 + sin(iTime * 1.0/x) * spinDistance);
          displacement = uv - center;
      }

      cols[0] += 1.0/(length(displacement)*0.1*size*rVariableSize);
   }

   for (int i = 1; i <= 4; i++) {
      float x = float(i);
      vec2 center;
      vec2 displacement;
      if (i % 2 == 0) {
          center = vec2(iResolution.x/2.0 + cos(iTime * 1.0/x) * spinDistance2, iResolution.y/2.0 + -cos(iTime * 1.0/x) * spinDistance);
          displacement = uv - center;
      } else {
          center = vec2(iResolution.x/2.0 + sin(iTime * 1.0/x) * spinDistance2, iResolution.y/2.0 + -sin(iTime * 1.0/x) * spinDistance);
          displacement = uv - center;
      }

      cols[1] += 1.0/(length(displacement)*0.1*size*gVariableSize);
   }

   for (int i = 1; i <= 4; i++) {
      float x = float(i);
      vec2 center;
      vec2 displacement;
      if (i % 2 == 0) {
          center = vec2(iResolution.x/2.0 + -cos(iTime * 1.0/x) * spinDistance3, iResolution.y/2.0 + cos(iTime * 1.0/x) * spinDistance);
          displacement = uv - center;
      } else {
          center = vec2(iResolution.x/2.0 + -sin(iTime * 1.0/x) * spinDistance3, iResolution.y/2.0 + sin(iTime * 1.0/x) * spinDistance);
          displacement = uv - center;
      }

      cols[2] += 1.0/(length(displacement)*0.1*size*bVariableSize);
   }


   fragColor = vec4(cols[0],cols[1],cols[2],0.0);

}

#define PI 3.14159265

float rewrap(float r, float rep) {

    if (r < PI) {
        r = -r;
    }

    r = mod(r*rep, PI);

    return r/(PI);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 px = gl_FragCoord.xy / iResolution.xy;

    const int size = 24;

    float ar = iResolution.x / iResolution.y;

    px.y /= ar;

    vec2 pos = px-vec2(0.5, 0.5/ar);

    float len = length(pos);

	vec3 color = vec3(0,0,0);

    float rep = 1.;

    float delta = rewrap(atan(pos.x, pos.y), rep);

    float div = 500./float(size);

    float modifier = sin(iTime*0.15);

    float index = float(int(mod(iTime*0.+delta+modifier, 3.0) * 100. / div));

	float f = texelFetch(iChannel0, ivec2(index/float(size)*256.,0.1), 0).x;

    float base = texelFetch(iChannel0, ivec2(1.*256.,0.1), 0).x;

    float cpi = smoothstep((0.1 + base*0.05), 0.15 + f * 0.01, len);

	if (len < 0.13 + f * 0.13 && len > (0.1 + base*0.001) && mod(delta + modifier, 1./float(size)) < 0.038 && (delta) > 0.0)
		color = vec3(0.95-delta, delta*0.1, delta);

    color *= cpi;

    if (len < 0.1 + base * 0.06 && len > 0.09 + base * 0.03)
      color = vec3(1,1,1);

    float eq = texelFetch(iChannel0, ivec2(px.x*128.,1), 0).x;

    if (abs(px.y*ar-0.54+eq*0.1) < 0.01 && 
        px.x > 0.5-(0.1+base * 0.05-1.08) &&
        px.x < 0.5+(0.1+base * 0.05-0.02)) {
        color = vec3(1);
    }

	fragColor = vec4(color, 0.1);
}

mat2 rot(float a) {
    return mat2(cos(a),sin(a),-sin(a),cos(a));
}

float circle(vec2 uv) {
    return length(uv);
}

float ss(float s, float size) {
    float w = fwidth(s);
    return smoothstep(size+w,size-w,s);
}

float concentricCircles(vec2 uv, vec2 center, float thickness, float phase, vec2 id) {
    return ss(cos((distance(uv, vec2(center))/(thickness*length(id))*5.)+phase),thickness);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2. * fragCoord - iResolution.xy) / iResolution.x;

    uv *= 5.;
    uv += .5;
    vec2 guv = fract(uv) - .5;
    vec2 id = floor(uv);

    vec3 col = clamp(vec3(concentricCircles(guv, vec2(0.), 0.05, iTime, id),0.,0.),0.,1.);
    col -= ((length(id)*.1))*2.;

    // Output to screen
    fragColor = vec4(col,1.0);
}

