// Determines the size of the dither "pixels".
#define SQUARE_SIZE (2)

// Firstly, we define all the usual raymarching necessities.
const int   MAX_MARCH = 1024; // The number of steps after which we stop marching.
const int   MAX_LIGHT_MARCH = 256; // The number of steps after which we stop calculating shadows.
const float CLOSE     = 0.00005; // "Close enough" distance. Arbitrary here.
const float FAR       = 5.0; // "Far enough" distance. Arbitrary here.
const float PI = 3.1415926535897; // Just useful.

// Sunlight. We'll change this eventually.
const vec3 SUNLIGHT = vec3(0.4, -1.0, 1.3)/1.6881943;

/* We track the maximum number of objects in the scene.
 * We could do way more than 16, but it really doesn't matter.
 * This will just be used to keep track of the IDs of objects.
 */
#define MAX_OBJECTS (4)

// Now we define some signed distance functions, with ADDITIONAL PARAMETERS!
struct SDFData
{
    float dist;
    int id;
    vec3 normal;
};
/* These parameters will become important in future layers, since we will use
 * the same scene definition multiple times.
 */
#define SPHERE(center, radius, id, v) (SDFData( distance(v,center) - radius, id, normalize(v-center) ))
#define SOLIDPLANE(point, normal, id, v) (SDFData( dot(v-point, normal), id, normal ))
SDFData UNION(SDFData sdf1, SDFData sdf2)
{
    if (sdf1.dist <= sdf2.dist)
        return sdf1;
    return sdf2;
}
SDFData INTERSECTION(SDFData sdf1, SDFData sdf2)
{
    if (sdf1.dist <= sdf2.dist)
        return sdf2;
    return sdf1;
}

// Objects' information in the scene.
struct ObjectInfo {
    int ditherer;
};

// We record the object information for each object.
const ObjectInfo objectList[MAX_OBJECTS] = ObjectInfo[MAX_OBJECTS](
    ObjectInfo(2),
    ObjectInfo(0),
    ObjectInfo(2),
    ObjectInfo(0)
);

// The scene. Outputs the object ID as well as the distance and the normal.
SDFData sceneSDF(vec3 v)
{
    // We will have a sphere and a cube lying on a flat plane, each a different object.
    return UNION(UNION(UNION(
    // First object: the plane.
        SOLIDPLANE(vec3(0.0), vec3(0.0, 1.0, 0.0), 1, v)
    ,
    // Second object: the sphere.
        SPHERE(vec3(1.2, 0.4, -0.1), 0.4, 2, v)
    ),
    // Third object: the smaller sphere.
        SPHERE(vec3(0.1, 0.2, -0.75), 0.2, 3, v)
    ),
    // Fourth object: the cube with rounded corners.
        // To build a cube, we intersect 6 solid planes. This will get a bit messy!
        INTERSECTION(INTERSECTION(INTERSECTION(
        // X sides
            INTERSECTION(
                SOLIDPLANE(vec3(0.5, 0.0, 0.0), vec3(1.0, 0.0, 0.0), 4, v),
                SOLIDPLANE(vec3(-0.5, 0.0, 0.0), vec3(-1.0, 0.0, 0.0), 4, v)
            )
        ,
        // Y sides
            INTERSECTION(
                SOLIDPLANE(vec3(0.0, 1.0, 0.0), vec3(0.0, 1.0, 0.0), 4, v),
                SOLIDPLANE(vec3(0.0, 0.0, 0.0), vec3(0.0, -1.0, 0.0), 4, v)
            )
        ),
        // Z sides
            INTERSECTION(
                SOLIDPLANE(vec3(0.0, 0.0, 0.5), vec3(0.0, 0.0, 1.0), 4, v),
                SOLIDPLANE(vec3(0.0, 0.0, -0.5), vec3(0.0, 0.0, -1.0), 4, v)
            )
        ),
        // We intersect a cube with a sphere to round the corners.
            SPHERE(vec3(0.0, 0.5, 0.0), 0.75, 4, v)
        )
    );
}

// Different dithering methods
float calculateDither(vec2 coord, float brightness, int dithererID) {
    float ditherTexture;
    if (dithererID == 1) {
        ditherTexture = texture(iChannel1, coord/256.0).x;
    } else if (dithererID == 2) {
        ditherTexture = texture(iChannel2, coord/1024.0).x;
    } else if (dithererID == 3) {
        ditherTexture = texture(iChannel2, coord/1024.0).z;
    } else {
        ditherTexture = texture(iChannel0, coord/8.0).x;
    }

    return step(ditherTexture+0.001, brightness);
}

// The result of raymarching
struct RayMarch {
    float depth; // How far away from the camera are we?
    int id;      // Which object have we hit?
    vec3 normal; // What's the normal to this surface?
    float light; // Does this object recieve direct light?
};

// The raymarching algorithm!
RayMarch raymarch(mat4 camera, vec2 relCoord)
{

    // Calculate the position of the eye.
    vec4 eye_homog = camera * vec4(0.0, 0.0, 0.0, 1.0);
    vec3 eye = eye_homog.xyz / eye_homog.w;

    // Calculate the direction that we'll be looking in.
    vec4 offsetRay_homog = camera * vec4(relCoord, 1.0, 1.0);
    vec3 ray = normalize((offsetRay_homog.xyz/offsetRay_homog.w) - eye);

    // We begin raymarching -- yippee!
    int steps = 0;
    vec3 v = eye;
    float totalDist = 0.0;
    bool shouldContinue = true;
    SDFData depthInfo;
    do {
        depthInfo = sceneSDF(v);
        v += depthInfo.dist * ray;
        totalDist += depthInfo.dist;
        steps += 1;
        shouldContinue =
            abs(depthInfo.dist) >= CLOSE
         && depthInfo.dist <= FAR
         && steps <= MAX_MARCH;
    } while (shouldContinue);

    // What if we're just looking at the sky?
    if (steps >= MAX_MARCH || depthInfo.dist >= FAR)
        // For now, ignore the normal vector for the sky.
         return RayMarch(0.0, 0, vec3(0.0), 0.0);

    // Now, we do reflection by raymarching AGAIN.
    // TODO: make this dependent on the material of the object.
    ray = -SUNLIGHT;
    v += (1.001)*CLOSE*depthInfo.normal;
    steps = 0;
    shouldContinue = true;
    float dist;
    do {
        dist = sceneSDF(v).dist;
        v += dist * ray;
        steps += 1;
        shouldContinue =
            dist >= CLOSE
         && dist <= FAR
         && steps <= MAX_LIGHT_MARCH;
    } while (shouldContinue);


    // If we're really looking at an object.
    return RayMarch(totalDist, depthInfo.id, depthInfo.normal, step(CLOSE, dist));
}

// Calculates the brightness of a point.
float calculateBrightness(RayMarch info, vec3 gaze)
{
    float diffusion = dot(-SUNLIGHT, info.normal);
    const float diffuse = 0.7;

    float speculation = abs(dot(SUNLIGHT, reflect(normalize(gaze), info.normal)));
    speculation = clamp(speculation, 0.0, 1.0);
    speculation = pow(speculation, 6.0);
    const float specular = 0.7;

    float brightness = diffusion*diffuse + speculation*specular;
    brightness = clamp(brightness, 0.0, 1.0);
    brightness *= info.light;
    return brightness;
}

// View matrix calculation.
mat4 viewMatrix(vec3 eye, vec3 gaze, vec3 up)
{
    // By Jamie Wong, based on the gluLookAt man page.
    // Lightly edited.
    vec3 f = normalize(gaze);
    vec3 s = normalize(cross(f, up));
    vec3 u = cross(s, f);
    return mat4(
        vec4(s, 0.0),
        vec4(u, 0.0),
        vec4(f, 0.0),
        vec4(eye, 1)
    );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 coord;
    #if defined(PIXELLATE) || defined(SHOW_DITHER)
        vec2 sqCoord = floor(fragCoord/float(SQUARE_SIZE));
        coord = float(SQUARE_SIZE)*sqCoord;
    #else
        coord = fragCoord;
    #endif

    // We get the signed distance info.
    vec2 relCoord = (coord - 0.5*iResolution.xy)/iResolution.y;
    vec3 eye = vec3(2.1 + 0.5*cos(iTime/1.5), 0.8 + 0.4*cos(iTime/1.5), -1.8+0.8*cos(iTime/1.5));
    mat4 view = viewMatrix(
        eye,
        vec3(0.3 + 0.1*sin(iTime/2.0), 0.5 + 0.1*cos(iTime/2.0), 0.0) - eye,
        vec3(0.0, 1.0, 0.0));
    RayMarch distinfo = raymarch(view, relCoord);
    ObjectInfo objinfo = objectList[distinfo.id-1];

    // Brightness
    float brightness = calculateBrightness(distinfo, eye);

    // The various different layers of information
    #if defined(SHOW_ID)
        float colorAngle = 2.0 * PI * float(distinfo.id) / float(MAX_OBJECTS);
        vec2 colorVector = vec2(cos(colorAngle), sin(colorAngle));
        vec2 rV, gV, bV;
        rV = vec2(1.0, 0.0);
        gV = vec2(cos(2.0*PI/3.0), sin(2.0*PI/3.0));
        bV = vec2(cos(-2.0*PI/3.0), sin(-2.0*PI/3.0));
        vec3 idColor =
              dot(colorVector, rV)*vec3(1.0,0.0,0.0)
            + dot(colorVector, gV)*vec3(0.0,1.0,0.0)
            + dot(colorVector, bV)*vec3(1.0,0.0,1.0);
        if (distinfo.id == 0)
             fragColor = vec4(0.0);
        else fragColor = vec4(vec3(0.5) + 0.5*idColor, 1.0);
    #elif defined(SHOW_DEPTH)
        if (distinfo.id == 0)
             fragColor = vec4(0.0);
        else fragColor = vec4(vec3(1.0 - distinfo.dist/FAR), 1.0);
    #elif defined(SHOW_NORMAL)
        if (distinfo.id == 0)
             fragColor = vec4(0.0);
        else fragColor = vec4(0.5 + 0.5*distinfo.normal, 1.0);
    #elif defined(SHOW_BRIGHTNESS)
        if (distinfo.id == 0)
             fragColor = vec4(0.0);
        else fragColor = vec4(vec3(brightness), 1.0);
    #elif defined(SHOW_DITHER)
        float dither = calculateDither(sqCoord, brightness, objinfo.ditherer);
        if (distinfo.id == 0)
             fragColor = vec4(0.0);
        else fragColor = vec4(vec3(dither), 1.0);
    #endif
}

