//
// intersect primitive shapes
//
vec4 inter_sphere (vec3 p, vec3 d, float r) {
	float bsign = length(p) > r ? -1. : 1.;
	vec3 bar = -p;
	float h = length(bar);
	float a = h*dot(normalize(bar), d);
	float o = sqrt(h*h-a*a);
	if (dot(d, bar) > 0. && o <= r) {
		float bevel = sqrt(r*r-o*o);
		vec3 hitp = p+d*(a+bsign*bevel);
		return vec4(
			normalize(hitp)*-bsign,
			a+bsign*bevel
		);
	}
	return inter_sky;
}
