vec4 inter_plane (vec3 p, vec3 d, vec3 n) {
    vec4 inter_plane1 = inter_plane_short(p, d, n);
    if (inter_plane1.w < inter_sky.w && inter_plane1.w > 0.) {
        return inter_plane1;
    }
    inter_plane1 = inter_plane_short(p, d, -n);
    if (inter_plane1.w < inter_sky.w && inter_plane1.w > 0.) {
        return inter_plane1;
    }
    return inter_sky;
}
