const float CHROMATIC_ABBERATION = .02;
const float ITERATIONS = 20.;
const float INITIAL_LUMA = .5;

mat2 rotate2d(float _angle){
    return mat2(cos(_angle),-sin(_angle),
                sin(_angle),cos(_angle));
}


float getColorComponent(in vec2 st, in float modScale, in float blur, in float shapeSize) {
    vec2 modSt = mod(st, 1. / modScale) * modScale * 2. - 1.;
    float dist = length(modSt);
    float angle = atan(modSt.x, modSt.y);
    float shapeMap = smoothstep(shapeSize + blur, shapeSize - blur, dist);
    return shapeMap;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 st =
        (2.* fragCoord - iResolution.xy)
        / min(iResolution.x, iResolution.y);
    st *= rotate2d(sin(iTime * .6 + st.x + st.y) * .3);
    st *= (sin(iTime * .3) + 2.) * .3;

    st *= log(length(sin(st * 5.18)) * cos(iTime * .1) + 1.2) * 5.;
    float modScale = 1.;

    vec3 color = vec3(0);
    float luma = INITIAL_LUMA;
    float blur = .2;
    float shapeSize = .2 + (sin(iTime * .7) + 1.) * .2;
    for (float i = 0.; i < ITERATIONS; i++) {
        vec2 center = st + vec2(sin(iTime * .5), cos(iTime * .3)) * 2.;
        //center += pow(length(center), 1.);
        vec3 shapeColor = vec3(
            getColorComponent(center - st * CHROMATIC_ABBERATION, modScale, blur, shapeSize),
            getColorComponent(center, modScale, blur, shapeSize),
            getColorComponent(center + st * CHROMATIC_ABBERATION, modScale, blur, shapeSize)        
        ) * luma;
        st *= 1.1;
        st *= rotate2d(sin(iTime  * .05) * .3);
        color += shapeColor;
        color = clamp(color, 0., 1.);
        if (color == vec3(1)) break;
        luma *= .8;
        blur *= .63;
    }
    fragColor = vec4(color, 1.0);
}

vec3 lerp(vec3 a, vec3 b, float alpha) {
    return a*(1.0 - alpha) + b*alpha;
}

float normalizedDistance(vec2 p1, vec2 p2, float hwRatio) {
    // Fix normalized coords
    p1.y *= hwRatio;
    p2.y *= hwRatio;

    return sqrt(pow(p1.x - p2.x, 2.0) + pow(p1.y - p2.y, 2.0));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 source = vec2(0.8, 0.8);
    float wavesNum = 4.0;

    float animationSpeed = 0.05;

    vec3 startColor = vec3(0.0, 0.0, 0.0);
    vec3 endColor = vec3(1.0, 0.528, 0.0);

    bool directionFromSource = true;

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec2 resolution = iResolution.xy;

    float hwRatio = resolution.y / resolution.x;

    // Generate waves
    float dist = normalizedDistance(source, uv, hwRatio);
    float space = (1.0 - max(source.x, source.y)) / wavesNum;
    float animCoof = iTime * animationSpeed;
    float wave = mod(dist - animCoof, space) / space; 

    // Invert wave if needed
    if (directionFromSource) wave = 1.0 - wave;

    // Convert wave height to a color
    vec3 col = lerp(endColor, startColor, wave);

    // Get strength ( from distance to the source )
    float strength = 1.0 - dist;
    col *= strength;

    // Output to screen
    fragColor = vec4(col, 1.0);
}

float pyramid(vec3 position, float halfWidth, float halfDepth, float halfHeight) {
    position.xz = abs(position.xz);

    // bottom
    float s1 = abs(position.y) - halfHeight;
    vec3 base = vec3(max(position.x - halfWidth, 0.0), abs(position.y + halfHeight), max(position.z - halfDepth, 0.0));
    float d1 = dot(base, base);

    vec3 q = position - vec3(halfWidth, -halfHeight, halfDepth);
    vec3 end = vec3(-halfWidth, 2.0 * halfHeight, -halfDepth);
    vec3 segment = q - end * clamp(dot(q, end) / dot(end, end), 0.0, 1.0);
    float d = dot(segment, segment);

    // side
    vec3 normal1 = vec3(end.y, -end.x, 0.0);
    float s2 = dot(q.xy, normal1.xy);
    float d2 = d;
    if (dot(q.xy, -end.xy) < 0.0 && dot(q, cross(normal1, end)) < 0.0) { 
        d2 = s2 * s2 / dot(normal1.xy, normal1.xy);
    }
    // front/back
    vec3 normal2 = vec3(0.0, -end.z, end.y);
    float s3 = dot(q.yz, normal2.yz);
    float d3 = d;
    if (dot(q.yz, -end.yz) < 0.0 && dot(q, cross(normal2, -end)) < 0.0) { 
        d3 = s3 * s3 / dot(normal2.yz, normal2.yz);
    }
    return sqrt(min(min(d1, d2), d3)) * sign(max(max(s1, s2), s3));
}

mat3 lookAtMatrix(vec3 from, vec3 to) {
    vec3 forward = normalize(to - from);
    vec3 right = normalize(cross(forward, vec3(0.0, 1.0, 0.0)));
    vec3 up = cross(right, forward);
    return mat3(right, up, forward);
}

float sdf(in vec3 position) { 
    float cornerRadius = 0.01;
    float halfWidth = 0.35; 
    float halfDepth = 0.35;
    float halfHeight = 0.25;
    float d = pyramid(position, halfWidth, halfDepth, halfHeight);
    d -= cornerRadius;
    return d;
}

vec3 normal(vec3 position) {
    float epsilon = 0.001;
    vec3 gradient = vec3(
        sdf(position + vec3(epsilon, 0, 0)) - sdf(position + vec3(-epsilon, 0, 0)),
        sdf(position + vec3(0, epsilon, 0)) - sdf(position + vec3(0, -epsilon, 0)),
        sdf(position + vec3(0, 0, epsilon)) - sdf(position + vec3(0, 0, -epsilon))
    );
    return normalize(gradient);
}

float raycast(vec3 rayOrigin, vec3 rayDirection) {
    int stepCount = 128;
    float maximumDistance = 5.0;
    float t = 0.0;
    for (int i = 0; i < stepCount; i++) {
        if (t > maximumDistance) {
            break;
        }
        vec3 currentPosition = rayOrigin + rayDirection * t;
        float d = sdf(currentPosition);
        if (d < 0.0001) {
            return t;
        }
        t += d;
    }
    return 0.0;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
	float angle = 0.5 * (iTime - 6.0);
	vec3 rayOrigin = vec3(1.0 * cos(angle), 0.4, 1.0 * sin(angle));
    vec3 targetPosition = vec3(0.0);
    mat3 cameraTransform = lookAtMatrix(rayOrigin, targetPosition);
    vec3 result = vec3(0.0);
    ivec2 sampleCount = ivec2(3.0, 3.0);
    for (int y = 0; y < sampleCount.y; y++) {
        for (int x = 0; x < sampleCount.x; x++) {
            vec2 uv = fragCoord + (vec2(float(x), float(y)) / vec2(sampleCount) - 0.5);
            uv = uv / iResolution.xy;
            uv = (uv * 2.0) - 1.0;
            uv.x *= iResolution.x / iResolution.y;
            vec3 rayDirection = normalize(vec3(uv, 1.5));
            rayDirection = cameraTransform * rayDirection;
            float t = raycast(rayOrigin, rayDirection);
            vec3 color = vec3(0.0);
            if (t > 0.0) {
                // same style that Inigo Quilez uses in his shaders
                vec3 position = rayOrigin + rayDirection * t;
                vec3 lightDirection = vec3(0.57735);
                vec3 n = normal(position);
                float diffuseAngle = max(dot(n, lightDirection), 0.0);
                // diffuse
                color = vec3(0.8, 0.7, 0.5) * diffuseAngle;
                // ambient
                color += vec3(0.2, 0.3, 0.4) * ((n.y + 1.0) * 0.5);
            }
            // gamma        
            color = sqrt(color);
            result += color;
        }
    }
    result /= float(sampleCount.x * sampleCount.y);
	fragColor = vec4(result, 1.0);
}

