int idObj, idObjGrp;
const float pi = 3.14159, sqrt3 = 1.7320508;

#define DMINQ(id) if (d < dMin) { dMin = d;  idObj = id;  qHit = q; }

vec3 TrackPath (float t)
{
  return vec3 (7. * cos (0.06 * t) + 0.1 * t, 0., t);
}

vec3 TrackVel (float t)
{
  return vec3 (-0.06 * 7. * sin (0.06 * t) + 0.1, 0., 1.);
}

float GrndHt (vec2 p)
{
  float h, s;
  h = Fbm2 (0.5 * p) - 0.45;
  s = 1. - abs (p.x - TrackPath (p.y).x) / 2.5;
  if (s > 0.) h = max (h - s * s, -0.5);
  return h;
}

float GrndHtN (vec2 p)
{
  float h;
  h = GrndHt (p);
  if (h > 0.) h = 0.3 * h + 0.3 * Fbm2 (2. * p);
  return h;
}

vec3 GrndNf (vec3 p)
{
  vec2 e;
  e = vec2 (0.001, 0.);
  return normalize (vec3 (GrndHtN (p.xz) - vec2 (GrndHtN (p.xz + e.xy),
     GrndHtN (p.xz + e.yx)), e.x)).xzy;
}

void SetMilParms ()
{
  vec2 g, w, cs0;
  g = HexToPix (gId * hgSize);
  if (GrndHt (g) < 0. || Hashfv2 (37. * gId + 1.1) < 0.15) milSz = 0.;
  else {
    milSz = 0.18 * hgSize * 0.5 * sqrt3 * (1. - 0.1 * Hashfv2 (17. * gId + 1.1));
    w = Hashv2v2 (73. * gId + 1.1);
    cs0 = vec2 (0.5 * pi, 0.);
    gShift = hgSize * max (0., 0.1 * sqrt3 - milSz) * w.x * sin (2. * pi * w.y + cs0);
    csBldAng = sin (0.2 * (0.2 + w.x) * pi * (tCur + 10.) + cs0);
    csMilAx = sin (0.2 * pi * sin (0.1 * pi * tCur) + 0.2 * pi * w.y + 0.01 * pi * tCur + cs0);
    csBldPch = sin ((0.15 + 0.2 * w.x) * pi + cs0);
  }
}

float BladeDf (vec3 p, float dMin)
{
  vec3 q;
  vec2 b;
  float d, bLen, bxMax;
  bLen = 4.;
  bxMax = 2.;
  q = p;
  q.x += 0.3;
  d = PrRoundCylDf (q.yzx, 0.25, 0.05, 0.25);
  DMINQ (2);
  q.zy = Rot2Cs (q.zy, csBldPch);
  q.xz += vec2 (bLen + 0.2, -0.03);
  b = vec2 (q.x, sign (q.y));
  if (q.z > 0.) q.zx = Rot2D (q.zx - vec2 (0., bxMax), mix (-0.02, 0.011,
     step (q.x, bxMax)) * pi) + vec2 (0., bxMax);
  q.yx = Rot2D (q.yx, 0.003 * pi * b.y);
  d = PrRoundBoxDf (vec3 (b.x, q.y + 0.05 * b.y, q.z), vec3 (bLen, 0.02, 0.2), 0.12);
  DMINQ (3);
  return dMin;
}

float GObjDf (vec3 p)
{
  vec3 q;
  float dMin, d, tLen, bScl, s;
  dMin = dstFar;
  if (milSz > 0.) {
    p.xz -= HexToPix (gId * hgSize) + gShift;
    dMin /= milSz;
    p /= milSz;
    tLen = 3.5;
    q = p;
    q.xz = Rot2Cs (q.xz, csMilAx);
    q.yz -= vec2 (2. * tLen + 0.1, 0.1);
    d = PrRoundBoxDf (q, vec3 (0.18, 0.2, 0.375), 0.1); 
    DMINQ (1);
    q.z += 0.55;
    q.xy = Rot2Cs (q.xy, csBldAng);
    d = PrCapsDf (q, 0.2, 0.45);
    DMINQ (2);
    q.z += 0.27;
    q.xy = Rot2D (q.xy, 2. * pi * ((floor (3. * atan (q.y, - q.x) / (2. * pi)) + 0.5) / 3.));
    bScl = 0.65;
    dMin = bScl * BladeDf (q / bScl, dMin / bScl);
    q = p;
    q.y -= tLen;
    s = abs (q.y / tLen - 1.);
    d = PrCylDf (q.xzy, 0.15 * (0.8 + 0.125 * s * s * s), tLen);
    DMINQ (4);
    dMin *= milSz;
  }
  return dMin;
}

float GObjRay (vec3 ro, vec3 rd)
{
  vec3 vri, vf, hv, p;
  vec2 edN[3], pM, gIdP;
  float dHit, d, s, eps;
  eps = 0.01;
  edN[0] = vec2 (1., 0.);
  edN[1] = 0.5 * vec2 (1., sqrt3);
  edN[2] = 0.5 * vec2 (1., - sqrt3);
  for (int k = 0; k < 3; k ++) edN[k] *= sign (dot (edN[k], rd.xz));
  vri = hgSize / vec3 (dot (rd.xz, edN[0]), dot (rd.xz, edN[1]), dot (rd.xz, edN[2]));
  vf = 0.5 * sqrt3 - vec3 (dot (ro.xz, edN[0]), dot (ro.xz, edN[1]),
     dot (ro.xz, edN[2])) / hgSize;
  pM = HexToPix (PixToHex (ro.xz / hgSize));
  gIdP = vec2 (-999.);
  dHit = 0.;
  for (int j = VAR_ZERO; j < 220; j ++) {
    hv = (vf + vec3 (dot (pM, edN[0]), dot (pM, edN[1]), dot (pM, edN[2]))) * vri;
    s = Minv3 (hv);
    p = ro + dHit * rd;
    gId = PixToHex (p.xz / hgSize);
    if (gId != gIdP) {
      gIdP = gId;
      SetMilParms ();
    }
    d = GObjDf (p);
    if (dHit + d < s) {
      dHit += d;
    } else {
      dHit = s + eps;
      pM += sqrt3 * ((s == hv.x) ? edN[0] : ((s == hv.y) ? edN[1] : edN[2]));
    }
    if (d < eps || dHit > dstFar || p.y < 0.) break;
  }
  if (d >= eps) dHit = dstFar;
  return dHit;
}

vec3 GObjNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = vec2 (0.001, -0.001);
  for (int j = VAR_ZERO; j < 4; j ++) {
    v[j] = GObjDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

float GObjSShadow (vec3 ro, vec3 rd)
{
  vec3 p;
  vec2 gIdP;
  float sh, d, h;
  sh = 1.;
  d = 0.05;
  gIdP = vec2 (-999.);
  for (int j = VAR_ZERO; j < 30; j ++) {
    p = ro + d * rd;
    gId = PixToHex (p.xz / hgSize);
    if (gId != gIdP) {
      gIdP = gId;
      SetMilParms ();
    }
    h = GObjDf (p);
    sh = min (sh, smoothstep (0., 0.02 * d, h));
    d += h;
    if (sh < 0.05 || d > dstFar) break;
  }
  return 0.8 + 0.2 * sh;
}

float BoatDf (vec3 p, float dMin)
{
  vec3 q;
  float d;
  dMin /= boatSz;
  p /= boatSz;
  p.y -= 0.7;
  d = PrCylDf (p, 1.2, 3.5);
  if (d < 0.1 / boatSz) {
    q = p;
    d = max (max (PrRoundCylDf (q, -0.8, 2., 1.5), - max (PrRoundCylDf (q - vec3 (0., 0.1, 0.),
       -0.85, 2., 1.5), abs (q.y) - 0.1)), abs (q.y + 0.1) - 0.1);
    q.y -= -0.2;
    d = max (SmoothMin (d, max (PrRoundCylDf (q, -1., 2., 1.3 ), q.y), 0.1), q.z - 2.);
    DMINQ (idObjGrp + 1);
    q = p;
    q.yz -= vec2 (-0.5, -0.2);
    d = max (PrRoundCylDf (q, -0.1, 1.1, 1.2), max (0.4 - q.y, q.z - 1.2));
    DMINQ (idObjGrp + 2);
    q = p;
    q.yz -= vec2 (0.8, 0.5);
    d = PrCylDf (q.xzy, 0.04, 0.3);
    DMINQ (idObjGrp + 3);
  } else dMin = min (dMin, d);
  return boatSz * dMin;
}

float ObjDf (vec3 p)
{
  float dMin;
  dMin = dstFar;
  for (int k = 0; k < N_BOAT; k ++) {
    idObjGrp = (k + 1) * 256;
    dMin = BoatDf (boatMat[k] * (p - boatPos[k]), dMin);
  }
  return dMin;
}

float ObjRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = VAR_ZERO; j < 100; j ++) {
    d = ObjDf (ro + dHit * rd);
    dHit += d;
    if (d < 0.001 || dHit > dstFar) break;
  }
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = vec2 (0.001, -0.001);
  for (int j = VAR_ZERO; j < 4; j ++) {
    v[j] = ObjDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

vec3 SkyCol (vec3 ro, vec3 rd)
{
  vec3 col, clCol;
  vec2 q;
  float f, fd, ff;
  q = 0.01 * (ro.xz + tCur * vec2 (0.5, 2.) + ((200. - ro.y) / rd.y) * rd.xz);
  ff = Fbm2 (q);
  f = smoothstep (0.1, 0.8, ff);
  fd = smoothstep (0.1, 0.8, Fbm2 (q + 0.01 * sunDir.xz)) - f;
  clCol = (0.8 + 0.5 * ff) * (vec3 (0.7) - 0.7 * vec3 (0.3, 0.3, 0.2) * sign (fd) *
     smoothstep (0., 0.05, abs (fd)));
  fd = smoothstep (0.01, 0.1, rd.y);
  col = mix (mix (vec3 (0.8, 0.8, 0.75), vec3 (0.4, 0.5, 0.8), 0.3 + 0.7 * fd), clCol,
     0.1 + 0.9 * f * fd);
  return col;
}

float WakeFac (vec3 row)
{
  vec2 tw[N_BOAT], twa;
  float twLen[N_BOAT], wkFac, ba;
  for (int k = 0; k < N_BOAT; k ++) {
    tw[k] = row.xz - (boatPos[k].xz - Rot2D (vec2 (0., 2.), boatAng[k]));
    twLen[k] = length (tw[k]);
  }
  if (twLen[0] < twLen[1]) {
    twa = tw[0];
    ba = boatAng[0];
  } else {
    twa = tw[1];
    ba = boatAng[1];
  }
  twa = Rot2D (twa, - ba);
  wkFac = 0.;
  if (length (twa * vec2 (2., 0.5)) < 1.) wkFac =
     clamp (1. - 4. * abs (twa.x), 0., 1.) * clamp (0.5 + twa.y, 0., 1.);
  return wkFac;
}

vec4 BoatCol ()
{
  vec4 col4, c4;
  int ig, id;
  ig = idObj / 256;
  id = idObj - 256 * ig;
  c4 = (ig == 1) ? vec4 (0.9, 0.9, 0.3, 0.2) : vec4 (0.3, 0.9, 0.3, 0.2);
  if (id == 1) col4 = (abs (qHit.y - 0.1) < 0.01) ? vec4 (0.7, 0.4, 0.2, 0.1) * (1. -
     0.2 * SmoothBump (0.42, 0.58, 0.05, mod (7. * qHit.x, 1.))) :
     ((qHit.y > -0.3) ? c4 : vec4 (0.7, 0.7, 0.8, 0.1));
  else if (id == 2) col4 = (abs (abs (qHit.x) - 0.4) < 0.36 && qHit.y > 0.45 && 
     length (vec2 (abs (qHit.x) - 0.1, qHit.y - 0.2)) < 0.7 || abs (abs (qHit.z + 0.2) -
     0.6) < 0.5 && abs (qHit.y - 0.65) < 0.2) ? vec4 (0.6, 0.5, 0.2, -1.) : c4;
  else if (id == 3) col4 = vec4 (1., 0., 0., 0.2);
  return col4;
}

vec4 MillCol ()
{
  vec4 col4;
  if (idObj == 1) col4 = vec4 (1., 0.9, 0.7, 0.1) * (0.5 +
     0.5 * smoothstep (0., 0.02, abs (qHit.y) - 0.02)) * (0.5 +
     0.5 * smoothstep (0., 0.02, length (vec2 (qHit.x, qHit.z + 0.1))- 0.18));
  else if (idObj == 2) col4 = vec4 (0.9, 1., 0.8, 0.2);
  else if (idObj == 3) col4 = mix (vec4 (0.85, 0.85, 0.9, 0.2), vec4 (1., 0.2, 0.2, 0.1), 
     smoothstep (0., 0.02, step (qHit.x, -3.5)));
  else if (idObj == 4) col4 = vec4 (0.9, 0.8, 0.7, 0.05) * (0.9 +
     0.1 * smoothstep (0., 0.02, abs (mod (2. * qHit.y + 0.5, 1.) - 0.5) - 0.02));
  return col4;
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec4 col4;
  vec3 col, vn, row;
  vec2 vf;
  float dstObj, dstObjG, dstObjM, dstGrnd, wkFac, sh;
  int idObjG;
  bool wtRefl;
  boatSz = 0.15;
  dstObjG = GObjRay (ro, rd);
  idObjG = idObj;
  dstObjM = ObjRay (ro, rd);
  dstGrnd = (rd.y < 0.) ? - ro.y / rd.y : dstFar;
  wtRefl = false;
  wkFac = 0.;
  dstObj = min (dstObjM, dstObjG);
  if (dstGrnd < min (dstObj, dstFar)) {
    row = ro + dstGrnd * rd;
    if (GrndHt (row.xz) < 0.) {
      ro = row;
      wkFac = WakeFac (row);
      row.xz += 0.2 * tCur;
      vf = (wkFac > 0.) ? vec2 (16., 2. * wkFac) : vec2 (2., 0.05 * (1. - smoothstep (0.1, 0.4,
          dstGrnd / dstFar)));
      vn = VaryNf (vf.x * row, vec3 (0., 1., 0.), vf.y);
      row = ro;
      rd = reflect (rd, vn);
      ro += 0.01 * rd;
      dstObjG = GObjRay (ro, rd);
      idObjG = idObj;
      dstObjM = ObjRay (ro, rd);
      dstGrnd = dstFar;
      dstObj = min (dstObjM, dstObjG);
      wtRefl = true;
    }
  }
  sh = 1.;
  if (min (dstObj, dstGrnd) < dstFar) {
    if (dstObj < dstGrnd) {
      if (dstObjM < dstObjG) {
        ro += dstObjM * rd;
        vn = ObjNf (ro);
        col4 = BoatCol ();
     } else {
        ro += dstObjG * rd;
        idObj = idObjG;
        vn = GObjNf (ro);
        col4 = MillCol ();
        if (idObj == 4) vn = VaryNf (64. * ro, vn, 0.3);
      }
    } else {
      ro += dstGrnd * rd;
      vn = GrndNf (ro);
      gId = PixToHex (ro.xz / hgSize);
      SetMilParms ();
      col4 = vec4 (vec3 (0., 0.4, 0.) * (1. - 0.2 * Fbm2 (8. * ro.xz)), 0.05);
      if (milSz > 0.) col4 *= 0.5 + 0.5 * smoothstep (0., 0.03,
         milSz * length (ro.xz - HexToPix (gId * hgSize)) - 0.05);
      sh = GObjSShadow (ro, normalize (vec3 (1., 5., 1.)));
    }
    if (col4.a >= 0.) {
      col = col4.rgb * (0.2 + 0.2 * max (dot (normalize (vec3 (- sunDir.xz, 0.)).xzy, vn), 0.) +
         0.8 * sh * max (dot (vn, sunDir), 0.)) +
         col4.a * step (0.95, sh) * pow (max (dot (sunDir, reflect (rd, vn)), 0.), 32.);
      col = mix (col, SkyCol (ro, rd), (wtRefl ? 0.2 + 0.8 * smoothstep (0.4, 0.6,
         dstObj / dstFar) : smoothstep (0.8, 0.9, min (dstGrnd, dstObj) / dstFar)));
    } else col = mix (col4.rgb, 0.5 * SkyCol (ro, reflect (rd, vn)), 0.8);
  } else col = SkyCol (ro, rd);
  col *= wtRefl ? 0.85 : 1.;
  if (wkFac > 0.) col = mix (col, vec3 (0.8, 0.83, 0.8), wkFac * clamp (0.1 +
     Fbm2 (16. * row.xz), 0., 1.));
  col *= vec3 (1.05, 0.95, 0.95);
  return clamp (col, 0., 1.);
}

void BoatPM (float t, out vec3 bPos, out mat3 bMat, out float btAng)
{
  vec3 v;
  vec2 cs;
  float bAz;
  bPos = TrackPath (t);
  bPos.y = 0.;
  bMat[2] = vec3 (Rot2D (vec2 (1., 0.), 0.4 * (Fbm1 (0.2 * t) - 0.5)), 0.);
  bMat[0] = normalize (vec3 (0., 0.1, 1.));
  bMat[1] = cross (bMat[0], bMat[2]);
  v = TrackVel (t);
  bAz = atan (v.z, - v.x);
  btAng = 0.5 * pi - bAz;
  cs = sin (bAz + vec2 (0.5 * pi, 0.));
  bMat *= mat3 (cs.x, 0., cs.y, 0., 1., 0., - cs.y, 0., cs.x);
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr, dateCur;
  vec3 ro, rd, vd, col;
  vec2 canvas, uv, uvv, uvf, mMid, ut, mSize, msw;
  float el, az, asp, winHt, zmFac, sr, spd;
  int vuId, regId;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  dateCur = iDate;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  asp = canvas.x / canvas.y;
  winHt = 0.9;
  uvf = vec2 (asp, winHt) - abs (uv);
  mSize = (1./5.) * vec2 (asp, 1.) * winHt;
  mMid = vec2 (asp, winHt - mSize.y) * vec2 (1. - mSize.y, -1.);
  ut = abs (uv - mMid) - mSize;
  regId = -1;
  if (mPtr.z > 0.) {
    regId = 0;
    msw = 2. * mPtr.xy - mMid / vec2 (asp, 1.);
    if (Maxv2 (abs (msw)) < mSize.y) {
      regId = 1;
      msw /= 2. * mSize.y;
    }
    if (regId == 0) msw = mPtr.xy;
  }
  vuId = 0;
  if (Maxv2 (ut) < 0.) {
    uv = (uv - mMid) / mSize.y;
    vuId = 1;
  }
  if (regId > 0 && (vuId == 0 || vuId == regId)) vuId = regId - vuId;
  tCur = mod (tCur, 2400.) + 30. * floor (dateCur.w / 7200.);
  hgSize = 3.;
  spd = 0.3;
  ro = TrackPath (spd * tCur);
  if (vuId == 0) {
    ro.x += 0.1 * sin (0.05 * 2. * pi * tCur);
    ro.y = 1. + 0.3 * Fbm1 (0.1 * tCur);
    vd = TrackVel (spd * tCur);
    el = -0.03 * pi;
    az = atan (vd.x, vd.z);
    if (mPtr.z > 0.) {   
      el += 0.4 * pi * msw.y;
      az += 2. * pi * msw.x;
    }
    el = clamp (el, -0.4 * pi, 0.4 * pi);
    zmFac = 3.;
  } else {
    ro.y = 30.;
    el = -0.3 * pi + 0.1 * pi * msw.y;
    az = 0.2 * pi * msw.x;
    zmFac = 5.;
  }
  for (int k = 0; k < N_BOAT; k ++) {
    BoatPM (spd * tCur + ((vuId == 0) ? (((abs (az) > 0.5 * pi) ? -1. : 1.) *
       (7. * float (k) + 5.)) : 7. * float (k) + 15.), boatPos[k], boatMat[k], boatAng[k]);
    boatPos[k].y = -0.02 + 0.04 * Fbm1 (0.5 * tCur + float (k));
  }
  vuMat = StdVuMat (el, az);
  dstFar = 120.;
  sunDir = normalize (vec3 (1., 3., -1.));
#if ! AA
  const float naa = 1.;
#else
  const float naa = 3.;
#endif  
  if (uvf.y > 0.) {
    col = vec3 (0.);
    sr = 2. * mod (dot (mod (floor (0.5 * (uv + 1.) * canvas), 2.), vec2 (1.)), 2.) - 1.;
    for (float a = float (VAR_ZERO); a < naa; a ++) {
      uvv = (uv + step (1.5, naa) * Rot2D (vec2 (0.5 / canvas.y, 0.), sr * (0.667 * a + 0.5) *
         pi)) / zmFac;
      rd = vuMat * normalize (vec3 (2. * tan (0.5 * atan (uvv.x / asp)) * asp, uvv.y, 1.));
      col += (1. / naa) * ShowScene (ro, rd);
    }
    if (Maxv2 (ut) < 0. && Minv2 (abs (ut)) * canvas.y < 2. ||
       Minv2 (uvf) * canvas.y < 2.) col = vec3 (0.6, 0.3, 0.2);
  } else col = vec3 (0.85);
  fragColor = vec4 (col, 1.);
}

float PrRoundBoxDf (vec3 p, vec3 b, float r)
{
  return length (max (abs (p) - b, 0.)) - r;
}

float PrCylDf (vec3 p, float r, float h)
{
  return max (length (p.xy) - r, abs (p.z) - h);
}

float PrRoundCylDf (vec3 p, float r, float rt, float h)
{
  return length (max (vec2 (length (p.xy) - r, abs (p.z) - h), 0.)) - rt;
}

float PrCapsDf (vec3 p, float r, float h)
{
  return length (p - vec3 (0., 0., clamp (p.z, - h, h))) - r;
}

vec2 PixToHex (vec2 p)
{
  vec3 c, r, dr;
  c.xz = vec2 ((1./sqrt3) * p.x - (1./3.) * p.y, (2./3.) * p.y);
  c.y = - c.x - c.z;
  r = floor (c + 0.5);
  dr = abs (r - c);
  r -= step (dr.yzx, dr) * step (dr.zxy, dr) * dot (r, vec3 (1.));
  return r.xz;
}

vec2 HexToPix (vec2 h)
{
  return vec2 (sqrt3 * (h.x + 0.5 * h.y), (3./2.) * h.y);
}

float Maxv2 (vec2 p)
{
  return max (p.x, p.y);
}

float Minv2 (vec2 p)
{
  return min (p.x, p.y);
}

float Minv3 (vec3 p)
{
  return min (p.x, min (p.y, p.z));
}

float SmoothMin (float a, float b, float r)
{
  float h;
  h = clamp (0.5 + 0.5 * (b - a) / r, 0., 1.);
  return mix (b - h * r, a, h);
}

float SmoothBump (float lo, float hi, float w, float x)
{
  return (1. - smoothstep (hi - w, hi + w, x)) * smoothstep (lo - w, lo + w, x);
}

mat3 StdVuMat (float el, float az)
{
  vec2 ori, ca, sa;
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  return mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
         mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
}

vec2 Rot2D (vec2 q, float a)
{
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

vec2 Rot2Cs (vec2 q, vec2 cs)
{
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

const float cHashM = 43758.54;

float Hashfv2 (vec2 p)
{
  return fract (sin (dot (p, vec2 (37., 39.))) * cHashM);
}

vec2 Hashv2f (float p)
{
  return fract (sin (p + vec2 (0., 1.)) * cHashM);
}

vec2 Hashv2v2 (vec2 p)
{
  vec2 cHashVA2 = vec2 (37., 39.);
  return fract (sin (dot (p, cHashVA2) + vec2 (0., cHashVA2.x)) * cHashM);
}

float Noiseff (float p)
{
  vec2 t;
  float ip, fp;
  ip = floor (p);
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = Hashv2f (ip);
  return mix (t.x, t.y, fp);
}

float Noisefv2 (vec2 p)
{
  vec2 t, ip, fp;
  ip = floor (p);  
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = mix (Hashv2v2 (ip), Hashv2v2 (ip + vec2 (0., 1.)), fp.y);
  return mix (t.x, t.y, fp.x);
}

float Fbm1 (float p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    f += a * Noiseff (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

float Fbm2 (vec2 p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    f += a * Noisefv2 (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

float Fbmn (vec3 p, vec3 n)
{
  vec3 s;
  float a;
  s = vec3 (0.);
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    s += a * vec3 (Noisefv2 (p.yz), Noisefv2 (p.zx), Noisefv2 (p.xy));
    a *= 0.5;
    p *= 2.;
  }
  return dot (s, abs (n));
}

vec3 VaryNf (vec3 p, vec3 n, float f)
{
  vec4 v;
  vec3 g;
  vec2 e;
  e = vec2 (0.1, 0.);
  for (int j = VAR_ZERO; j < 4; j ++)
     v[j] = Fbmn (p + ((j < 2) ? ((j == 0) ? e.xyy : e.yxy) : ((j == 2) ? e.yyx : e.yyy)), n);
  g = v.xyz - v.w;
  return normalize (n + f * (g - n * dot (n, g)));
}

#define MAX_STEPS 100
#define MAX_DIST 100.0
#define SURF_DIST 0.01

float GetDist(vec3 p)
{
    vec4 s = vec4(0, 1, 6, 1);

    float sphereDist = length(p-s.xyz) - s.w;
    float planeDist = p.y;

    float d = min(sphereDist, planeDist);
    return d;    
}

float RayMarch(vec3 ro, vec3 rd)
{
    float dO = 0.0;
    for(int i = 0; i< MAX_STEPS; i++)
    {
        vec3 p = ro + rd*dO;
        float dS = GetDist(p);
        dO += dS;
        if(dO > MAX_DIST || dS < SURF_DIST)
            break;
    }

    return dO;
}

vec3 GetNormal(vec3 p)
{
    float d = GetDist(p);
    vec2 e = vec2(0.01, 0.0);

    vec3 n = d - vec3(
        GetDist(p - e.xyy),
        GetDist(p - e.yxy),
        GetDist(p - e.yyx));

    return normalize(n);
}

float GetLight(vec3 p)
{
    vec3 lightPos = vec3(0.0, 5.0, 6.0);
    lightPos.xz += vec2(sin(iTime), cos(iTime))* 2.0;
    vec3 l = normalize(lightPos - p);
    vec3 n = GetNormal(p);

    float dif = clamp(dot(n, l), 0.0, 1.0);
    float d = RayMarch(p+n*SURF_DIST*2.0, l);
    if(d < length(lightPos-p))
        dif *= (-1.0);   //0.1 for black shadow

    return dif;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = (fragCoord - 0.5 * iResolution.xy)/iResolution.y;   

    vec3 ro = vec3(0.0, 1, 0);
    vec3 rd = normalize(vec3(uv.x, uv.y, 1));

    float d = RayMarch(ro, rd);
    vec3 p = ro + rd * d;

    float dif = GetLight(p);    
    vec3 difV = vec3(dif);
    if(dif < 0.0)
    {
        difV = vec3(0, -dif, 0);   //color shadow
    }

    vec3 col = vec3(difV);


    fragColor = vec4(col,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 p  = vec2 (-1.0 + 2.0*uv);
    p.x *= iResolution.x/iResolution.y;

    float anV = 0.50;
    float div = 0.40;
    float rad = 0.20;

    p = vec2 (p.x*cos(iTime) - p.y*sin(iTime),
              p.x*sin(iTime) + p.y*cos(iTime));
    p = abs(p);
    vec2 tolen = (p + (vec2 (-iResolution.x/iResolution.y, -1.0 )));
    float len = length(tolen);

    float anm = len + iTime*anV; 
    vec2 mo = mod(vec2(anm), div);
    vec3 col = vec3(0.0);
    if (mo.x < rad)
    {
        col = vec3(1.0);
    }


    fragColor = vec4(col,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //All constants have been eyeballed, so nothing is really physically accurate.


    vec2 uv = fragCoord/iResolution.xy;
    float zoom = 0.5;
    float time = sin(iTime*0.1); //sin(uv.x*3.14); for time on the x axis.
    float terminator = 7.0;
    vec3 timeMult = vec3(1.0-pow(2.0,-(terminator*5.0)*time),1.0-pow(2.0,-(terminator*2.0)*time),1.0-pow(2.0,-terminator*time));
    //    ^^ How each color should fade depending on time.

    //The "height" of each gradient on each channel.
    float r = pow(max(1.0-uv.y*zoom,0.0),6.0)*0.95 * timeMult.x;
    float g = pow(max(1.0-uv.y*zoom,0.0),3.0) * timeMult.y;
    float b = pow(max(1.0-uv.y*zoom,0.0),1.5) * timeMult.z;

    // Output to screen
    fragColor = vec4(r,g,b,1.0);
}

float count = 40.0;
vec2 Size = vec2(30.0, 40.0);
vec3 colorBg = vec3(0.0);
vec3 colorIn = vec3(1.0, 0.9, 0.16);
vec3 colorOut = vec3(0.12, 0.59, 0.21);
vec3 getPowerColor(vec2 power) {
    float pMax = pow(1.12,3.2);
    float pMin = 1.0 / pMax;
    vec3 color = mix(colorBg, colorOut, smoothstep(pMin, pMax, power.y));
    color = mix(color, colorIn, smoothstep(pMin, pMax, power.x));
    return color;
}
vec2 getPower(vec2 st, vec2 pos, vec2 size) {
    vec2 radius = (size * size) / dot(st - pos, st - pos);
    radius = pow(radius, vec2(1.5));
    return radius;
}
void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord;
    vec2 pows = vec2(0.0);
    vec2 pos;
    vec3 col;
    for(float i = 1.0; i < count; i++) {
        pos = 0.5 *iResolution.xy *
            vec2(sin(iTime*0.05 * fract(0.246 * i) + i * 3.6) *
                          cos(iTime*0.05 * fract(0.374 * i) - i * fract(0.6827 * i))
                          + 1.,
                          cos(iTime*0.05 * fract(0.246 * i) + i * 3.6) *
                          sin(iTime*0.05 * fract(.374 * i) - i * fract(0.6827 * i))
                          + 1.);
        pows += getPower(uv, pos, Size * (.5 + fract(0.2834 * i) * .5));
    }
    col = getPowerColor(pows);
    // Output to screen

    fragColor = vec4(col, 1.0);
}

float pi = 3.14;

float step2(float x){return x * x;}

float radius(vec2 pos){return sqrt(step2(pos.x) + step2(pos.y));}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;

    // PARAMETERS //
    float size = .27*iResolution.y;
    float speed = 2.;
    float ripples = 25.;
    float ripplesScale = .1;

    vec3 baseCol = vec3(0.);
    vec3 outBottom = vec3(255., 105., 180.) / 255.;
    vec3 outTop = vec3(255., 255., 0.) / 255.;
    vec3 inBottom = vec3(127., 255., 212.) / 255.;
    vec3 inTop = vec3(0., 255., 255.) / 255.;

    vec3 outMix = mix(outBottom, outTop, smoothstep(0., 1.5, uv.y));
    vec3 inMix = mix(inBottom, inTop, smoothstep(0.1, .9, uv.y));

    float rad = size * (1. + ripplesScale*sin(fract(fragCoord.y/ripples + iTime*speed)*2.*pi));

    //pink flame

    vec2 center = vec2(iResolution.x*.5, iResolution.y*.35);
    vec2 pos = fragCoord - center;
    float mul = 1. - (uv.y)*.5;
    bool outMask = radius(vec2(pos.x, pos.y*mul)) < rad;
    bool inMask = radius(vec2(pos.x, pos.y*mul)) < rad*.7;


    fragColor = outMask ? inMask ? vec4(inMix, 1.) : vec4(outMix, 1.) : vec4(baseCol,1.0);
}

// BASIC RAY-MARCHING CLASS DEMO!
//
// Numerous methods borrowed from iq articles:
//   https://iquilezles.org/www/index.htm
// 
// 3D signed distance functions:
//   https://iquilezles.org/www/articles/distfunctions/distfunctions.htm
// 
// Shadows:
//   https://www.iquilezles.org/www/articles/rmshadows/rmshadows.htm
// 
// Ray-marching SDFs:
//   https://www.iquilezles.org/www/articles/raymarchingdf/raymarchingdf.htm
// 
// Normals for SDFs:
//   https://www.iquilezles.org/www/articles/normalsSDF/normalsSDF.htm

float opUnion( float d1, float d2 ) {  return min(d1,d2); }
vec4  opUnion(vec4 d1, vec4 d2) {
 return (d1.x < d2.x ? d1 : d2);   
}

float opIntersection( float d1, float d2 ) { return max(d1,d2); }
vec4  opIntersection( vec4  d1, vec4  d2 ) {
 return (d1.x > d2.x ? d1 : d2);   

}

// subtracts d1 from d2
float opSubtraction( float d1, float d2 ) { return max(-d1,d2); }
vec4  opSubtraction( vec4  d1, vec4  d2 ) { 
  return opIntersection(vec4(-d1.x,d1.yzw), d2);
}

float opSmoothUnion( float d1, float d2, float k ) {
    float h = clamp( 0.5 + 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) - k*h*(1.0-h); }

float opSmoothSubtraction( float d1, float d2, float k ) {
    float h = clamp( 0.5 - 0.5*(d2+d1)/k, 0.0, 1.0 );
    return mix( d2, -d1, h ) + k*h*(1.0-h); }

float opSmoothIntersection( float d1, float d2, float k ) {
    float h = clamp( 0.5 - 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) + k*h*(1.0-h); }

float opOnion( in float sdf, in float thickness )
{
    return abs(sdf)-thickness;
}

// 3D gradient noise methods. 
// https://www.shadertoy.com/view/Xsl3Dl
vec3 hash( vec3 p ) // replace this by something better
{
	p = vec3( dot(p,vec3(127.1,311.7, 74.7)),
			  dot(p,vec3(269.5,183.3,246.1)),
			  dot(p,vec3(113.5,271.9,124.6)));

	return -1.0 + 2.0*fract(sin(p)*43758.5453123);
}
float noise( in vec3 p )
{
    vec3 i = floor( p );
    vec3 f = fract( p );

	vec3 u = f*f*(3.0-2.0*f);

    return mix( mix( mix( dot( hash( i + vec3(0.0,0.0,0.0) ), f - vec3(0.0,0.0,0.0) ), 
                          dot( hash( i + vec3(1.0,0.0,0.0) ), f - vec3(1.0,0.0,0.0) ), u.x),
                     mix( dot( hash( i + vec3(0.0,1.0,0.0) ), f - vec3(0.0,1.0,0.0) ), 
                          dot( hash( i + vec3(1.0,1.0,0.0) ), f - vec3(1.0,1.0,0.0) ), u.x), u.y),
                mix( mix( dot( hash( i + vec3(0.0,0.0,1.0) ), f - vec3(0.0,0.0,1.0) ), 
                          dot( hash( i + vec3(1.0,0.0,1.0) ), f - vec3(1.0,0.0,1.0) ), u.x),
                     mix( dot( hash( i + vec3(0.0,1.0,1.0) ), f - vec3(0.0,1.0,1.0) ), 
                          dot( hash( i + vec3(1.0,1.0,1.0) ), f - vec3(1.0,1.0,1.0) ), u.x), u.y), u.z );
}
const mat3 m = mat3( 0.00,  0.80,  0.60,
