    x = rotate_y(q, cos(iTime));
    x = rotate_z(q, sin(iTime));
    x = rotate_x(q, iTime);
    float outerTorusSd = sdTorus(x, vec2(1.2,0.05));
    vec4 outerTorus = vec4(outerTorusSd, vec3(0.9,0.9,0.4));
    sdf = opUnion( sdf, outerTorus );


    float sphereSD = sdSphere(p, 0.8);
    vec4 sphere = vec4(sphereSD, vec3(0.9,0.4,0.6));
    sdf = opUnion( sdf, sphere );

    return sdf;
}


// Scalar SDF implementation for our scene
float map(in vec3 p)
{
    return mapV4(p).x;// just sdf value
}

