// http://iquilezles.org/www/articles/normalsSDF/normalsSDF.htm
vec3 calcNormal( in vec3 pos )
{

#if 0
    vec2 e = vec2(1.0,-1.0)*0.5773*0.001;
    return normalize( e.xyy*map( pos + e.xyy ) + 
					  e.yyx*map( pos + e.yyx ) + 
					  e.yxy*map( pos + e.yxy ) + 
					  e.xxx*map( pos + e.xxx ) );
#else
    // inspired by tdhooper and klems - a way to prevent the compiler from inlining map() 4 times
    vec3 n = vec3(0.0);
    for( int i=0; i<4; i++ )
    {
        vec3 e = 0.5773*(2.0*vec3((((i+3)>>1)&1),((i>>1)&1),(i&1))-1.0);
        n += e*map(pos+0.001*e);
    }
    return normalize(n);
#endif    
}

// Vector raymarch: 
// .x   : t* value of surface, or -1 if didn't hit anything.
// .yzw : pass-thru from mapV4.yzw
// 
vec4 raymarchV4(in vec3 ro, in vec3 rd) 
{
    //int steps = int(round(200.0*iMouse.y/iResolution.y));
    float t = 0.0;
    for(int i=0; i<513; i++) {
        vec3  rt = ro + rd*t;
        vec4  f  = mapV4(rt);
		float d  = f.x;

        if(d<0.001) {// We hit it or went too far
            f.x = t; // replace .x's sdf with t*
            //f.yzw = vec3(1.,0.,0.);
            return f;
        }
        else {// keep marching
         	t += 0.95*d; // conservative/smaller step for inexact/warped SDFs
        }

        if(t > 100.) {
            f.x = -1.;// -ve t* --> didn't hit anything
            return f;
        }
    }

	return vec4(-1., vec3(0.)); // didn't hit anything
}

/// Returns t* value of surface, or -1. if didn't hit anything.
float raymarch(in vec3 ro, in vec3 rd) 
{
    return raymarchV4(ro,rd).x;  
}

// Raymarch SDF: 
// Returns: t* value of surface, or -1.0 if didn't hit anything.
float raymarchVanilla(in vec3 ro, in vec3 rd) 
{
    float t = 0.0;
    for(int i=0; i<200; i++) {
        vec3  rt = ro + rd*t;
        float d  = map(rt); // SDF of scene

        if(d<0.001) {// We hit it (or went too far)
            return t;
        }
        else {// keep marching
         	t += d; // Assumes exact SDFs
        }

        if(t > 100.) {
            return -1.0;
        }
    }

	return -1.0; // didn't hit anything
}

float shadow( in vec3 ro, in vec3 rd, float mint, float maxt )
{
    for( float t=mint; t<maxt; )
    {
        float h = map(ro + rd*t);
        if( h<0.001 )
            return 0.;
        t += 0.95*h;//can do slightly less for not-quite-SDFs
    }
    return 1.0;
}
float softshadow( in vec3 ro, in vec3 rd, float mint, float maxt, float k )
{
    float res = 1.0;
    float ph = 1e20;
    for( float t=mint; t<maxt; )
    {
        float h = map(ro + rd*t);
        if( h<0.001 )
            return 0.0;

        float y = h*h/(2.0*ph);
        float d = sqrt(h*h-y*y);
        res = min( res, k*d/max(0.0,t-y) );
        ph = h;
        t += 0.95*h;//slightly less for not-quite-SDFs
    }
    return res;
}

vec3 render(in vec3 ro, in vec3 rd) 
{
    vec3  col = vec3(0.,0.,0.);    // init color to background
    vec4  ray = raymarchV4(ro, rd);// compute distance along ray to surface
    float t   = ray.x;
    vec3  Cd  = ray.yzw;// can passthru other shading values, e.g., objectID, but we did color.

    vec2  mouse = iMouse.xy/iResolution.xy;        


    if(t>0.0) {//hit surface --> shade it:   

	    vec3  p  = ro + rd*t; // point on surface
        vec3  N  = calcNormal(p); // sdf normal

        // DETERMINE MATERIAL COLOR: (todo: all orange for now)
        //vec3  Cd = vec3(231./255., 111./255., 3./255.); // diffuse color

        // DIRECTIONAL LIGHT:
        vec3  posL   = ro+ 30.*vec3(mouse.x-0.5, mouse.y-0.5,0.);// light at eye (safe!)
        vec3  L      = normalize(posL - p);
        vec3  CL     = vec3(1.);// directional light color
        float LdotN  = clamp(dot(L,N), 0., 1.);
        //float shadL  = shadow(p, L, 0.01, 10.);
        float sshadL = softshadow(p, L, 0.01, length(posL-p), 2.);
	    col = Cd * CL * LdotN * sshadL;// * sshadL; // * occ;

        // CANDLE LIGHT (#2):
        //vec3  dp     = 2.*vec3(0., mouse.y-0.5, mouse.x-0.5);
        vec3  posL2  = pumpkinCenter + .15*vec3(sin(20.*iTime),cos(7.*iTime),cos(14.*iTime));
        vec3  L2     = normalize(posL2 - p);
        vec3  CL2    = vec3(1.,1.,.2);// candle light color
        float L2dotN = clamp(dot(L2,N), 0., 1.);
        float shadL2  = shadow(p, L2, 0.02, length(posL2-p));
        //float sshadL2 = softshadow(p, L2, 0.01, length(posL2-p), 8.);
	    col += Cd * CL2 * L2dotN * shadL2; // * occ;       
    }

    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 pXY = (fragCoord - 0.5*iResolution.xy)/iResolution.y; // normalized coordinates (unit height)
    vec3 pix = vec3(pXY,    0.);    // position of virtual pixel (Z=0 depth)
    vec3 ro  = vec3(0., 0., 3.);    // ray origin (EYE POSITION).. controls field-of-view
    vec3 rd  = normalize(pix - ro); // ray direction

    vec3 col = render(ro, rd); // ray-march and evaluate color

    // Output to screen
    fragColor = vec4(col,1.0);
}

#define BLUR_RADIUS 0.015

float sdCircle(vec2 p, vec2 c, float r) {
    return length(p - c) - r;
}

float sdLine(vec2 p, vec2 a, vec2 b) {
    vec2 pa = p-a, ba = b-a;
    float h = clamp(dot(pa, ba)/dot(ba, ba), 0.0, 1.0);
    return length(pa - ba*h);
}

float sdRoundedLine(vec2 p, vec2 a, vec2 b, float r) {
    return sdLine(p, a, b) - r;
}

float sdBox(vec2 p, vec2 b) {
    vec2 d = abs(p) - b;
    return length(max(d, 0.0)) + min(max(d.x, d.y),0.0);
}

float sdRoundedBox(vec2 p, vec2 b, float r) {
    return sdBox(p, b) - r;
}

vec4 shapeColor(vec3 fillColor, float dist, float gradience) {
    float smoothDist = smoothstep(0.00, BLUR_RADIUS, dist);
    float opacity = 1.0 - smoothDist;
    return vec4(fillColor * opacity + abs(dist * gradience), opacity);
}

vec4 combine(vec4 color1, vec4 color2) {
    return min(color1, color2);
}

mat2 scale(vec2 scale) {
    return mat2(scale.x, 0.0, 0.0, scale.y);
}

vec3 white = vec3(0.8, 0.8, 0.8);
vec3 black = vec3(0.0, 0.0, 0.0);
vec3 green = vec3(0.0, 0.4, 0.0);
vec3 red = vec3(0.5, 0.0, 0.0);
float dotRadius = 0.06;
int numberOfValues = 7;

vec4 draw1Dot(vec3 color, vec2 center, vec4 background) {
    vec4 dot = shapeColor(color, sdCircle(center, vec2(0.0, -0.0), dotRadius), 5.0);
    return mix(background, dot, dot.a);
}

vec4 draw2Dots(vec3 color, vec2 center, vec4 background) {
    vec4 dot = shapeColor(color, sdCircle(center, vec2(0.18, 0.18), dotRadius), 5.0);
    vec4 blendedDots = mix(background, dot, dot.a);
    dot = shapeColor(color, sdCircle(center, vec2(-0.18, -0.18), dotRadius), 5.0);
    blendedDots = combine(blendedDots, mix(background, dot, dot.a));
    return blendedDots;
}


