vec4 drawBlackCircle(vec2 center, float radius, float thickness) {
    vec4 circle = vec4(1.0-step(sdCircle(center, vec2(0.0, -0.0), radius),  radius));
    circle += vec4(step(sdCircle(center, vec2(0.0, -0.0), radius - thickness), radius - thickness));
    circle.a = 1.0-step(sdCircle(center, vec2(0.0, -0.0), radius), radius);
    return circle;
}

vec4 drawSagie(vec2 center) {
    vec4 hair = drawBlackCircle(center - vec2(0.07, 0.19), 0.01, 0.008);
    hair *= drawBlackCircle(center - vec2(-0.07, 0.19), 0.01, 0.008);
    hair *= drawBlackCircle(center - vec2(0.055, 0.195), 0.01, 0.008);
    hair *= drawBlackCircle(center - vec2(-0.055, 0.195), 0.01, 0.008);
    hair *= drawBlackCircle(center - vec2(0.04, 0.2), 0.01, 0.008);
    hair *= drawBlackCircle(center - vec2(-0.04, 0.2), 0.01, 0.008);
    hair *= drawBlackCircle(center - vec2(0.015, 0.205), 0.01, 0.008);
    hair *= drawBlackCircle(center - vec2(-0.015, 0.205), 0.01, 0.008);
    hair *= drawBlackCircle(center - vec2(0.085, 0.175), 0.01, 0.008);
    hair *= drawBlackCircle(center - vec2(-0.085, 0.175), 0.01, 0.008);
    hair *= drawBlackCircle(center - vec2(0.1, 0.165), 0.01, 0.008);
    hair *= drawBlackCircle(center - vec2(-0.1, 0.165), 0.01, 0.008);
    hair *= drawBlackCircle(center - vec2(0.115, 0.15), 0.01, 0.008);
    hair *= drawBlackCircle(center - vec2(-0.115, 0.15), 0.01, 0.008);
    hair *= drawBlackCircle(center - vec2(0.13, 0.141), 0.01, 0.008);
    hair *= drawBlackCircle(center - vec2(-0.13, 0.141), 0.01, 0.008);
    hair *= drawBlackCircle(center - vec2(-0.00, 0.21), 0.01, 0.008);

    vec4 beard = drawBlackCircle(center - vec2(-0.0, -0.19), 0.01, 0.02);
    beard *= drawBlackCircle(center - vec2(-0.07, -0.19), 0.01, 0.02);
    beard *= drawBlackCircle(center - vec2(0.055, -0.195), 0.01, 0.02);
    beard *= drawBlackCircle(center - vec2(-0.055, -0.195), 0.01, 0.02);
    beard *= drawBlackCircle(center - vec2(0.04, -0.2), 0.01, 0.02);
    beard *= drawBlackCircle(center - vec2(-0.04, -0.2), 0.01, 0.02);
    beard *= drawBlackCircle(center - vec2(0.015, -0.205), 0.01, 0.02);
    beard *= drawBlackCircle(center - vec2(-0.015, -0.205), 0.01, 0.02);
    beard *= drawBlackCircle(center - vec2(0.085, -0.175), 0.01, 0.02);
    beard *= drawBlackCircle(center - vec2(-0.085, -0.175),0.01, 0.02);
    beard *= drawBlackCircle(center - vec2(0.1, -0.165), 0.01, 0.02);
    beard *= drawBlackCircle(center - vec2(-0.1, -0.165), 0.01, 0.02);
    beard *= drawBlackCircle(center - vec2(0.115,- 0.15), 0.01, 0.02);
    beard *= drawBlackCircle(center - vec2(-0.115, -0.15), 0.01, 0.02);
    hair *= drawBlackCircle(center - vec2(0.13, -0.141), 0.01, 0.02);
    beard *= drawBlackCircle(center - vec2(-0.13, -0.141), 0.01, 0.02);

    vec4 face = drawBlackCircle(center, 0.1, 0.01);
    vec4 leftEye = drawBlackCircle(center - vec2(0.07, 0.06), 0.01, 0.003);
    leftEye *= drawBlackCircle(center - vec2(0.07, 0.06), 0.005, 0.1);
    face = mix(face, leftEye, 1.0-leftEye.a);
    vec4 rightEye = drawBlackCircle(center- vec2(-0.07, 0.06), 0.01, 0.003);
    rightEye *= drawBlackCircle(center - vec2(-0.07, 0.06), 0.005, 0.1);
    face = mix(face, rightEye, 1.0-rightEye.a);
    return mix(hair,face, 1.0-face.a) * beard;
}

vec4 drawDots(vec3 color, int number, vec2 center, vec4 background) {
    vec4 aggregate = vec4(0);   
    aggregate += draw1Dot(color, center, background) * float(number == 1);
    aggregate += draw2Dots(color, center, background) * float(number == 2);
    aggregate += draw3Dots(color, center, background) * float(number == 3);
    aggregate += draw4Dots(color, center, background) * float(number == 4);
    aggregate += draw5Dots(color, center, background) * float(number == 5);
    aggregate += draw6Dots(color, center, background) * float(number == 6);
    aggregate += drawSagie(center) * float(number == 0);
    return aggregate;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 pos = 2.0 * fragCoord/iResolution.xy - 1.0;
    float aspectRatio = iResolution.x / iResolution.y;
    pos.x *= aspectRatio;
    pos = scale(vec2(0.5 + 0.5 * cos(iTime))) * pos;

    vec2 pieceSize = vec2(0.22, 0.54);
    float pieceRadius = 0.08;
    vec4 piece = shapeColor(white, sdRoundedBox(pos, pieceSize, pieceRadius), 0.65);

    int counter = int((iTime + 3.15) / 6.25) % numberOfValues;
    vec4 blendedRedDots = drawDots(red, counter, pos + vec2(0.0, 0.32), piece);

    vec4 blendedGreenDots = drawDots(green, (counter + 1) % numberOfValues , pos - vec2(0.0, 0.32), piece);

    vec2 divStart = vec2(-0.24, 0.0);
    vec2 divEnd = vec2(0.24, 0.0);
    float divRadius = 0.02;
    vec4 divider = shapeColor(black, sdRoundedLine(pos, divStart, divEnd, divRadius), 15.0);
    vec4 blendedDivider = mix(piece, divider, divider.a);

    fragColor = combine(combine(blendedRedDots, blendedGreenDots), blendedDivider);
}

