    float t2 = floor((iTime/2.+uv.x)/10.0);
    vec3 random1 = (hash31(3.+t2));
    vec3 random2 = (hash31(4.+t2));
    vec3 random3 = random1*3.;
    vec3 random4 = random2*3.;
    vec3 random5 = random2*3.;

    float offset = .5;
    float scale2 = 1.5;
    //float bend = scale2;
    float bend = 1.+1./3.;
    for(int c=0;c<3;c++){
        float scale = c1.z;
        //float scale1 = 1.0;
