#define pi 3.14159

float thc(float a, float b) {
    return tanh(a * cos(b)) / tanh(a);
}

float ths(float a, float b) {
    return tanh(a * sin(b)) / tanh(a);
}

vec3 pal( in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d )
{
    return a + b*cos( 6.28318*(c*t+d) );
}

float h21 (vec2 a) {
    return fract(sin(dot(a.xy, vec2(12.9898, 78.233))) * 43758.5453123);
}

// smoothstep from a point p
float sp(vec2 uv, float t) {
    vec2 p = 0.35 * vec2(thc(0.5, t * 0.713), ths(0.5, t));
    float d = length(uv - p);
    float k = 0.5 * d + 0.035 * length(uv);
    return smoothstep(-k, k, k - d);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;

    // warp uv slightly
    uv /= 0.85 * cosh(1.1 * length(uv));

    float t = 2.5 * iTime;

    // length of trail (0.05 -> blobs, 3. -> noisy trail)
    float sc = 0.1; //mix(0.05, 3., .5 + .5 * cos(2. * iTime));

    // flick between blobs seperated/together
    float e = .5 + .5 * thc(4., -0.8 * iTime);

    // make 12 blobs (sc * h21 "stretches" using noise)
    float s = sp(uv, t + sc * h21(uv));
    for (float i = 1.; i < 12.; i++) {
        s = max(s, sp(uv, e * i + t + sc * h21(uv + i)));
    }

    vec3 col = s * pal(s, vec3(1.), vec3(1.), vec3(1.), s * vec3(0.,0.33,0.66)); 
    col += 2. * vec3(s) * vec3(0.2,0.6,0.9);
    //col = vec3(s);
    fragColor = vec4(col,1.0);
}

void mainImage( out vec4 c, in vec2 o )
{
    o.y-=iResolution.y/2.0;o.x=float(int(o.x));
    vec4 ox=vec4((mod(o.x+float(iFrame),11.0)-3.0),(mod(o.x+float(iFrame),13.0)-5.0),(mod(o.x+float(iFrame),17.0)-7.0),1)/2.0;
    o.x+=float(iFrame);
    vec2 old=o;
    o.x=(mod(floor(o.x),2.0));
    o.y=(old.y)+floor((sin(float(int(floor(old.x))/4)-iTime)*85.0));
    c=vec4(0.0);
    vec4 color=1.0-vec4(sin(float(int(floor(old.x))/4)-iTime));
    float r = (o.x*o.x+o.y*o.y);
    vec2 oo=old;
    old.x-=1.0;
    oo.x=(mod(floor(oo.x+float(iFrame)),2.0));
    oo.y=(old.y)+floor((sin(float(int(floor(old.x+1.0))/4)-iTime+3.1416)*85.0));
    float r2 = oo.x*oo.x+oo.y*oo.y;
    vec4 color2=1.0-vec4(sin(float(int(floor(old.x+1.0))/4)-iTime+3.1416));
    if((sqrt(r))<=2.0) c=color*color2+ox;
    else if((sqrt(r2))<=2.0) c=color2*color+ox;
    //if(c.x>0.0){c+=0.75;c/=2.0;}
}

//change this constant to get different patterns!
#define c2 0.

#define c1 vec4(3.0+c2,2.5+c2,1.5,0)

vec2 triangle_wave(vec2 a,float scale){
    return abs(fract((a+c1.xy)*scale)-.5);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0.0);
    vec3 col;  
    float t1 = 9.;

    vec2 uv = (fragCoord-iResolution.xy)/iResolution.y/t1/2.0;
    uv += vec2(iTime/2.0,iTime/3.0)/t1/8.0;
    float offset = .5;
    float scale2 = 1.5;
    for(int c=0;c<3;c++){
        float scale = c1.z;
        for(int i=0;i<3;i++)
        {
            float bend = 1.0; 
            for(int k = 0; k < 9; k++){       
                uv /= scale2;
                uv = triangle_wave(uv.yx+offset,scale)/bend+triangle_wave(uv+offset/scale2,scale)*bend;
                //bend = 1.+uv.x/2.;
            }
            scale /= 1.+(scale2)*col.x/(8.);
            scale2 -= (col.x-1.)/4.;
            col[c] = abs(col[c] - ((uv.x)-(uv.y)));
        }
	}
    fragColor = vec4(vec3(col),1.0);
}

// multilayer variant of https://shadertoy.com/view/NlG3Dw
// cylindrical variant of https://shadertoy.com/view/flyGDw

#define H(v)      fract(1e4*sin((v)*mat2(R,R-71.)))
#define S(v)      smoothstep(30./R.y,0.,v)

void mainImage( out vec4 O, vec2 u )
{
    vec2  R = iResolution.xy, P,D,_P,
          U = 10.* ( u+u - R ) / R.y, U0=U;
    float m = 9.,d, t = iTime, k=8., z=8., n, _n;

  for( n = 2.; n >= 1.; n-=.5 ) {                           // several layers
    U = U0;
    U = k*vec2( .995*atan(U.y,U.x), z/length(U) );          // go to cylinder space
    U.y += n*t;                                             // animation

    for ( int k=0; k < 9; k++ )                             // jittered point grid distribution
        D = vec2(k%3,k/3), P = floor(U)+D - H(mod(floor(U)+D,25.)+n/13.),  // NB: cyclical hash
        d = length(P-U), 
        d < m ? _P=P, _n = n, m = d : m;
 }   
    P = _P;

    U = U0;
    P = k*z/(P.y-_n*t) * cos( P.x/.995/k + vec2(0,-1.57) ); // back to screen space
    m = length(P-U);

                                                            // draw disk - dark disk
    O = vec4( clamp( S(m-.4) - S( length(P-U+ U*length(U)/1e3 ) -.4 )  ,0.,1.) );
}

// perspective variant of https://shadertoy.com/view/NtGGWm
// multilayer variant of https://shadertoy.com/view/NlG3Dw
// cylindrical variant of https://shadertoy.com/view/flyGDw
// 2D variant of lambmeow's https://www.shadertoy.com/view/7lVGWW

#define H(v)      fract(1e4*sin((v)*mat2(R,R-71.)))
#define S(v)      smoothstep(30./R.y,0.,v)
#define hue(v)  ( .6 + .6 * cos( 6.3*(v)  + vec4(0,23,21,0)  ) )

void mainImage( out vec4 O, vec2 u )
{
    vec2  R = iResolution.xy, P,D,_P, C,
          U = 10.* ( u+u - R ) / R.y, U0=U;
    float m = 9.,d, t = iTime, k=8., z=8., r = .4, n=1., _n;
    O-=O; 
    for( z=32.; z >= 4.; z/=2. ) {                              // several layers
//  for( n = 2.; n >= 1.; n-=.5 ) {                             // several layers
        U = U0;
        U = k*vec2( .995*atan(U.y,U.x), z/length(U) );          // go to cylinder space
        U.y += n*t;                                             // animation
        m = 9.;
        for ( int k=0; k < 9; k++ )                             // jittered point grid distribution
            D = vec2(k%3,k/3), P = floor(U)+D - H(mod(floor(U)+D,25.)+n/13.),  // NB: cyclical hash
            d = length(P-U), 
            d < m ? _P=P, _n = n, m = d : m;

        P = _P;

        U = U0;
        P = k*z/(P.y-_n*t) * cos( P.x/.995/k + vec2(0,-1.57) ); // back to screen space
        m = length(P-U);

        r = .4*length(U)/10.;                                   // draw disk
        C = vec2( S(m-r) );                                           // mask
        C.x = clamp( C.x - .9*S( length(P-U+ U*r/50. ) -r ), 0.,1.); // color = disc - dark disc
        O =  ( iMouse.z <= 0. ? C.xxxy 
                              : C.xxxy*exp(-vec4(0,1,1,1)/10.*log2(z/4.)) ) // if click, fog
            + (1.-C.y)*O;                                       // blend
    }
}

