#define t iTime
#define r iResolution.xy

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
	vec3 c;
	float l,z=t;
	for(int i=0;i<3;i++) {
		vec2 uv,p=fragCoord.xy/r;
		uv=p;
		p-=.5;
		p.x*=r.x/r.y;
		z+=.07;
		l=length(p);
		uv+=p/l*(sin(z)+1.)*abs(sin(l*9.-z-z));
		c[i]=.01/length(mod(uv,1.)-.5);
	}
	fragColor=vec4(c/l,t);
}

mat2x2 rotate(float a)
{
    float c = cos(a);
    float s = sin(a);

    return mat2x2(c, s, -s, c);
}

uint wang_hash(uint seed)
{
	seed = uint(seed ^ uint(61)) ^ uint(seed >> uint(16));
    seed *= uint(9);
    seed = seed ^ (seed >> 4);
    seed *= uint(0x27d4eb2d);
    seed = seed ^ (seed >> 15);

    return seed;
}

// Semi-random float [0, 1) from 1D position
float randomFloat(float state)
{
	return float(wang_hash(uint(state))) / 4294967296.0;
}

// Semi-random floats from 2D position
vec2 randomFloat(vec2 state)
{
	return vec2(
        randomFloat(state.x + state.y * 1024.0), 
        randomFloat(state.x * 512.0 + state.y * 2048.0)
    );
}

float perlinNoiseOctave(vec2 p)
{
	float offsetStep = 1.0;
    float gridSize = 10.0;

    vec2 id = floor(p * gridSize);

    // Random corners
    float upperLeft  = randomFloat(id + vec2(0.0, 0.0)).x;
    float upperRight = randomFloat(id + vec2(offsetStep, 0.0)).x;
    float lowerLeft  = randomFloat(id + vec2(0.0, offsetStep)).x;
    float lowerRight = randomFloat(id + vec2(offsetStep, offsetStep)).x;

    // Bicubic interpolation
    vec2 st = smoothstep(0.0, 1.0, fract(p * gridSize));
    float upperMix = mix(upperLeft, upperRight, st.x);
    float lowerMix = mix(lowerLeft, lowerRight, st.x);
    float finalMix = mix(upperMix, lowerMix, st.y);

	return finalMix;
}

float perlinNoise(vec2 p)
{
    float currentNoise = 0.0;

    // Add octaves
    for(float i = 0.0; i < 5.0; i += 1.0)
        currentNoise += perlinNoiseOctave(p*pow(2.0, i))/pow(2.0, i+1.0);

    // Returns value from 0 to 0.96875    
    return currentNoise;
}

vec3 getCol(vec3 viewDir)
{
    vec3 col = vec3(0.0);

    vec2 uv = viewDir.xy;
    float lenUV = length(uv);

    // Red falloff
    col = mix(vec3(0.8, 0.05, 0.05), col, lenUV);

    // Red shine
    float redShineRadius = lenUV + perlinNoise(vec2((atan(uv.y, uv.x) + 4.0) * 1.0, iTime * 0.01)) * 0.8 + 0.1;
    col = mix(vec3(0.8, 0.05, 0.05), col, clamp(redShineRadius, 0.0, 1.0));

    // Yellow shine
    float yellowHaloRadius = 
        perlinNoise(vec2(cos(atan(uv.y, uv.x)), sin(atan(uv.y, uv.x))) + vec2(4.0, 2.0 + iTime * 0.05)) * 
        0.02 + 0.42;
    vec3 shineCol = mix(vec3(0.95, 0.95, 0.3), vec3(0.7, 0.0, 0.0), clamp(lenUV * 7.0 - 2.7, 0.0, 1.0));
    col += shineCol * smoothstep(yellowHaloRadius, yellowHaloRadius-0.03, lenUV);

    // Overinterpolate to keep colors around origin
    col = mix(vec3(0.50, 0.98, 0.98), col, lenUV * 2.0);

    // Red sun halo
    col = mix(col, vec3(0.8, 0.05, 0.05), smoothstep(0.41, 0.39, lenUV));

    // Moon shadow
    float moonRadius = 0.38 + perlinNoise(vec2(sin(atan(uv.y, uv.x)) + 3.0, 0.0)) * 0.02;
    col = mix(col, vec3(0.1, 0.02, 0.02), smoothstep(moonRadius, moonRadius - 0.04, lenUV));

    // Fog
    uv *= rotate(0.3);
    col = mix(col, vec3(0.6, 0.2, 0.2), perlinNoise(uv + vec2(iTime * 0.05, 0.0) + vec2(4.0)) * pow(1.0 - uv.y*uv.y, 4.0) * 0.8 * min(lenUV*1.5, 1.0));
    col = mix(col, vec3(0.6, 0.2, 0.2), perlinNoise(uv + vec2(iTime * 0.01, 0.0) + vec2(2.0)) * pow(1.0 - uv.y*uv.y, 4.0) * 0.8 * min(lenUV*1.5, 1.0));

    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - iResolution.xy * 0.5) / iResolution.y;

    vec3 viewDir = normalize(vec3(uv, 0.3));
    vec3 col = getCol(viewDir);

    fragColor = vec4(col, 1.0);
}

//#define ORIGINAL

void mainImage( out vec4 O, vec2 U )
{
    vec2 R = iResolution.xy; U = (U+U-R)/R.y; 

    // Number of divisions of the circle
    #ifdef ORIGINAL
        float Q = 6.;
    #else
        float Q = 10. + 5.*sin(iTime);
    #endif

    U = vec2((1.+atan(U.y,U.x)/3.1416)*Q/2.,log(length(U))); // conformal polar
    // multiply U for smaller tiles

    U.y += U.x/Q; // comment for concentric circles instead of spiral

    // N is the counter - increasing towards the center
    float N = floor(1.-U.y) * Q + floor(U.x);

    O.x = floor(N)/32.; // for display purposes only
}

#define R vec3(1,0,0)
#define G vec3(0,1,0)
#define B vec3(0,0,1)
#define W vec3(1,1,1)

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

    // number of hex cells on the screen
    float resolution = 100.0f;

    // cartesian coordinates
    vec2 xy = fragCoord / iResolution.xy - 0.5;    

    // axial coordinates [https://www.redblobgames.com/grids/hexagons/]
    vec3 qrs = vec3(
        resolution * (xy.x + xy.y),
        resolution * (xy.x - xy.y),
        -resolution * xy.x * 2.0f
    );

    // star tessellation
    vec3 grid = cos(qrs);
    float magnitude = dot(grid, grid) / 3.0f;

    // random wave generator
    vec3 col;

    // number of centers
    int n = 20;

    // for each center...
    for (int i = 0; i < n; ++i) {

        // sample random Q and R coordinates from noise buffer
        float randQ = resolution * (texture(iChannel0, vec2(i,0)/float(n)).r - 0.5);
        float randR = resolution * (texture(iChannel0, vec2(0,i)/float(n)).r - 0.5);

        // sample random frequency modifier
        float randF = texture(iChannel0, vec2(i,i)/float(n)).r - 0.5;

        // Q + R + S = 0
        vec3 center = vec3(randQ, randR, -randQ - randR);

        // cycle colors
        vec3 C;
        switch(i % 3) {
            case 0:
                C = R; break;
            case 1:
                C = G; break;
            case 2:
                C = B; break;
        }


        // compute wavefront at current point
        col += C * sin(randF * length(qrs - center) + iTime);

    }

    // multiply waves with hex grid for nice effect
    fragColor = vec4(col * magnitude, 1.0);


}

float speed = .2;

vec2 rotateUV(vec2 uv, float rotation, vec2 mid)
{
    return vec2(
      cos(rotation) * (uv.x - mid.x) + sin(rotation) * (uv.y - mid.y) + mid.x,
      cos(rotation) * (uv.y - mid.y) - sin(rotation) * (uv.x - mid.x) + mid.y
    );
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = rotateUV(fragCoord, 0.5, iResolution.xy/2.0)/iResolution.xy;

    // Output to screen
    if (uv.y + sin(iTime) * 0.05 > .85 + 0.1 * (cos((uv.x + iTime * speed) * 3.15 * 5.0))) {
        vec3 col = 0.5 + 0.5*cos(1.01 * iTime+uv.xyx+vec3(0,2,4));
        fragColor = vec4(col,1.0);
    } else if (uv.y + cos(iTime) * 0.05 > .6 + 0.1 * (cos((uv.x + iTime * speed * 0.998) * 3.15 * 5.0))) {
        vec3 col = 0.5 + 0.5*cos(1.02 * iTime+uv.xyx+vec3(0,2,4));
        fragColor = vec4(col * .8,1.0);
    } else if (uv.y + sin(iTime) * 0.05 > .40 + 0.1 * (cos((uv.x + iTime * speed * 0.996) * 3.15 * 5.0))) {
        vec3 col = 0.5 + 0.5*cos(1.03 * iTime+uv.xyx+vec3(0,2,4));
        fragColor = vec4(col * .4,1.0);
    } else if (uv.y + sin(iTime) * 0.05 > .15 + 0.1 * (cos((uv.x + iTime * speed * 0.994) * 3.15 * 5.0))) {
        vec3 col = 0.5 + 0.5*cos(1.03 * iTime+uv.xyx+vec3(0,2,4));
        fragColor = vec4(col * .2,1.0);
    }
    else {
        vec3 col = 0.5 + 0.5*cos(1.04 * iTime+uv.xyx+vec3(0,2,4));
        fragColor = vec4(col * .0,1.0);
    }
}

mat2 RotZ(float a) {
    float s = sin(a), c = cos(a);
    return mat2(c, -s, s, c);
}

float Hash21(vec2 p) {
    p = fract(p*vec2(137.337, 13333.37));
    p += dot(p, p+45.138);
    return fract(p.x*p.y);
}

float ray(vec2 uv) {
    return max(0.0, 1.0-abs(uv.x*uv.y*5000.0));
}

vec3 star(vec2 uv, vec2 id) {
    float n = Hash21(id)/3.0;
    uv -= vec2(n, fract(n*33.3))-.5 ;
    float d = length(uv);
    float size = (1.0+sin((n*iTime)/5.0));
    float m = .001/d * size;

    for(int i=0; i < 3; i++) {
        uv *= RotZ(0.33*iTime+float(i)+id.x*id.y);
        float f = (1.0+sin(iTime+id.x*id.y+float(i)/4.0))/2.0;
        f = smoothstep(0.59, 0.05, f);
        m += ray(uv)*f*0.3;
    }
  //  m = 1.0-smoothstep(0.5, 0.1, m);

    vec3 color = sin(vec3(.2, .3, .9)*fract(n*1337.37)*333.7)*.5+.5;
    float glow = 0.33+sin(id.x*id.y*313.37+6.0*iTime/4.0)/2.0;
    glow = smoothstep(0.9, 0.1, glow)*0.1;
    color = color*vec3(0.7,.4,1.+size) +
        (glow*vec3(1.0));
    return color*m;
//    return vec3(m)*fract(n*1772.0);//+vec3(id*0.1, 0.0);
}


vec3 starLayer(vec2 uv) {
    vec3 col = vec3(0);

    vec2 gv = fract(uv)-0.5;
    vec2 id = floor(uv);
    for(int y=-1; y <= 1; y++) {
        for(int x=-1; x <= 1; x++) {
            vec2 offs = vec2(float(x), float(y));
            col += star(gv-offs, id+offs);
        }
    }
    return col;
}

#define NUM_LAYERS 5.0

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;
    uv*=34.0;

    uv.x += sin(iTime/4.0)-cos(iTime/3.0)*2.0;
    uv.y += cos(iTime/4.0)-sin(iTime/3.0)*2.0;

    uv *= RotZ(-iTime*0.1);

    vec3 col = vec3(0);
    float t = cos(iTime*0.1);

    for(float i=0.0; i < 1.0; i+=1./NUM_LAYERS) {
        uv *= RotZ(i*8.0);
        float depth = fract(i+t);
        float scale = mix(0.5, 0.01, depth);
        float fade = pow(depth, 2.1)+0.1;
        col += starLayer(uv*scale+i*1.37)*fade;
        //uv *= i;
    }

//    col.rg += id*0.1;

    fragColor = vec4(col,1.0);
}

mat4 ortho( 
    float b, float t, float l, float r, 
    float n, float f){ 
    mat4 m = mat4(1.0);

    m[0][0] = 2.0 / (r - l); 
    m[0][1] = 0.0; 
    m[0][2] = 0.0; 
    m[0][3] = 0.0; 

    m[1][0] = 0.0; 
    m[1][1] = 2.0 / (t - b); 
    m[1][2] = 0.0; 
    m[1][3] = 0.0; 

    m[2][0] = 0.0; 
    m[2][1] = 0.0; 
    m[2][2] = -2.0 / (f - n); 
    m[2][3] = 0.0; 

    m[3][0] = -(r + l) / (r - l); 
    m[3][1] = -(t + b) / (t - b); 
    m[3][2] = -(f + n) / (f - n); 
    m[3][3] = 1.0;

    return m;
} 

mat4 lookAt(vec3 eye, vec3 center, vec3 up) {
    vec3 f = vec3(normalize(center - eye));
    vec3 s = vec3(normalize(cross(f, up)));
    vec3 u = vec3(cross(s, f));

    mat4 m = mat4(1.0);
    m[0][0] = s.x;
    m[1][0] = s.y;
    m[2][0] = s.z;
    m[0][1] = u.x;
    m[1][1] = u.y;
    m[2][1] = u.z;
    m[0][2] =-f.x;
    m[1][2] =-f.y;
    m[2][2] =-f.z;
    m[3][0] =-dot(s, eye);
    m[3][1] =-dot(u, eye);
    m[3][2] = dot(f, eye);

    return m;
}


float sphere( vec3 p, vec3 center, float rad ) {
    return length(p - center) - rad;
}

float roundBox( vec3 p, vec3 b, float r ) {
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0) - r;
}

float torus( vec3 p, vec2 t ) {
    vec2 q = vec2(length(p.xz)-t.x,p.y);
    return length(q)-t.y;
}

float boxFrame( vec3 p, vec3 b, float e ) {
  p = abs(p  )-b;
  vec3 q = abs(p+e)-e;
  return min(min(
      length(max(vec3(p.x,q.y,q.z),0.0))+min(max(p.x,max(q.y,q.z)),0.0),
      length(max(vec3(q.x,p.y,q.z),0.0))+min(max(q.x,max(p.y,q.z)),0.0)),
      length(max(vec3(q.x,q.y,p.z),0.0))+min(max(q.x,max(q.y,p.z)),0.0));
}

float smoothUnion( float d1, float d2, float k ) {
    float h = max(k-abs(d1-d2),0.0);
    return min(d1, d2) - h*h*0.25/k;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec3 eyePos = vec3(sin(iTime), cos(iTime), sin(iTime)) * 0.5;
    vec3 viewDir = vec3(cos(iTime), 0.0, sin(iTime));
    vec3 upVector = vec3(0.0, sin(iTime), cos(iTime));

    mat4 view = lookAt(eyePos, eyePos + viewDir, upVector);
    float dim = 1.0;
    mat4 projection = ortho(-dim, dim, -dim, dim, -dim, dim);
    vec2 p = (-iResolution.xy + 2.0*fragCoord)/iResolution.y;
    vec4 world = projection * view * vec4(p, 0.0, 1.0);

    float d = 1.e10;

    float s1 = sphere(world.xyz, vec3(0.0), 0.1);
    float s2 = sphere(world.xyz, vec3(0.0, 0.0, -0.5 + sin(iTime) * 0.3), 0.1);
    float u = smoothUnion(s1, s2, 0.1);
    d = min(u, d);

    float rb1 = roundBox(world.xyz - vec3(0.5), vec3(0.6, 0.2, 0.7), 0.1);
    u = smoothUnion(rb1, d, 0.1);
    d = min(u, d);

    float t1 = torus(world.xyz - vec3(0.0), vec2(0.1, 0.1));
    u = smoothUnion(t1, d, 0.1);
    d = min(u, d);

    if (d < 0.3) {
        fragColor = vec4(d * (1.0 / 0.3));
    }
    else {
        fragColor = vec4(0.0, 0.0, 0.0, 1.0);
    }
}

float tube(vec3 position, float innerRadius, float outerRadius, float halfHeight, float cornerRadius) {
   vec2 d = vec2(length(position.xz) - (outerRadius + innerRadius) * 0.5, position.y);
   d = abs(d) - vec2((outerRadius - innerRadius) * 0.5, halfHeight) + cornerRadius;
   return length(max(d, 0.0)) + min(max(d.x, d.y), 0.0) - cornerRadius;
}

mat3 lookAtMatrix(vec3 from, vec3 to) {
    vec3 forward = normalize(to - from);
    vec3 right = normalize(cross(forward, vec3(0.0, 1.0, 0.0)));
    vec3 up = cross(right, forward);
    return mat3(right, up, forward);
}

mat3 rotationMatrixAxisAngle(vec3 axis, float angle) {
    float s = sin(angle);
    float c = cos(angle);
    vec3 column1 = axis * axis.x * (1.0 - c) + vec3(c, axis.z * s, -axis.y * s);
    vec3 column2 = axis * axis.y * (1.0 - c) + vec3(-axis.z * s, c, axis.x * s);
    vec3 column3 = axis * axis.z * (1.0 - c) + vec3(axis.y * s, -axis.x * s, c);
    return mat3(column1, column2, column3);
}

float sdf(in vec3 position) { 
    float innerRadius = 0.25; 
    float outerRadius = 0.4;
    float halfHeight = 0.1;
    float cornerRadius = 0.05;
    position = rotationMatrixAxisAngle(vec3(1.0, 0.0, 0.0), 2.094) * position;
    float d = tube(position, innerRadius, outerRadius, halfHeight, cornerRadius);
    return d;
}

vec3 normal(vec3 position) {
    float epsilon = 0.001;
    vec3 gradient = vec3(
        sdf(position + vec3(epsilon, 0, 0)) - sdf(position + vec3(-epsilon, 0, 0)),
        sdf(position + vec3(0, epsilon, 0)) - sdf(position + vec3(0, -epsilon, 0)),
        sdf(position + vec3(0, 0, epsilon)) - sdf(position + vec3(0, 0, -epsilon))
    );
    return normalize(gradient);
}

float raycast(vec3 rayOrigin, vec3 rayDirection) {
    int stepCount = 128 * 3;
    float maximumDistance = 5.0;
    float t = 0.0;
    for (int i = 0; i < stepCount; i++) {
        if (t > maximumDistance) {
            break;
        }
        vec3 currentPosition = rayOrigin + rayDirection * t;
        float d = sdf(currentPosition);
        if (d < 0.0001) {
            return t;
        }
        t += d;
    }
    return 0.0;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
	float angle = 0.5 * (iTime - 5.0);
	vec3 rayOrigin = vec3(1.0 * cos(angle), 0.4, 1.0 * sin(angle));
    vec3 targetPosition = vec3(0.0);
    mat3 cameraTransform = lookAtMatrix(rayOrigin, targetPosition);
    vec3 result = vec3(0.0);
    ivec2 sampleCount = ivec2(3.0, 3.0);
    for (int y = 0; y < sampleCount.y; y++) {
        for (int x = 0; x < sampleCount.x; x++) {
            vec2 uv = fragCoord + (vec2(float(x), float(y)) / vec2(sampleCount) - 0.5);
            uv = uv / iResolution.xy;
            uv = (uv * 2.0) - 1.0;
            uv.x *= iResolution.x / iResolution.y;
            vec3 rayDirection = normalize(vec3(uv, 1.5));
            rayDirection = cameraTransform * rayDirection;
            float t = raycast(rayOrigin, rayDirection);
            vec3 color = vec3(0.0);
            if (t > 0.0) {
                // same style that Inigo Quilez uses in his shaders
                vec3 position = rayOrigin + rayDirection * t;
                vec3 lightDirection = vec3(0.57735);
                vec3 n = normal(position);
                float diffuseAngle = max(dot(n, lightDirection), 0.0);
                // diffuse
                color = vec3(0.8, 0.7, 0.5) * diffuseAngle;
                // ambient
                color += vec3(0.2, 0.3, 0.4) * ((n.y + 1.0) * 0.5);
            }
            // gamma        
            color = sqrt(color);
            result += color;
        }
    }
    result /= float(sampleCount.x * sampleCount.y);
	fragColor = vec4(result, 1.0);
}

// Hash without Sine
// Creative Commons Attribution-ShareAlike 4.0 International Public License
// Created by David Hoskins.
// https://www.shadertoy.com/view/4djSRW
vec2 hash22(vec2 p)
{
	vec3 p3 = fract(vec3(p.xyx) * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yzx+33.33);
    return fract((p3.xx+p3.yz)*p3.zy);

}
float hash12(vec2 p)
{
	vec3 p3  = fract(vec3(p.xyx) * .1031);
    p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.x + p3.y) * p3.z);
}


vec3 plate(vec2 id, vec2 uv){
    vec3 col = vec3(-1);


    vec2 r = hash22(id);
    float r2 = hash12(id);

    vec2 p = uv;

    p += r2;

    uv = abs(uv);

    vec2 dim = vec2(.6,.6) + r * .4;

    if(uv.x < dim.x && uv.y < dim.y){
        col = mix(vec3(.3, .3, .3), vec3(.7, .7, .7), r2) * 1. + 0.1 * texture(iChannel0, p).x;


        if(length(uv - dim + vec2(.1)) < .05){
            col = vec3(.4);
        }

    }

    return col;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 col = vec3(0);

    vec2 uv = (fragCoord-.5*iResolution.xy) / iResolution.y;
    uv *= 10.;

    uv += iTime;


    vec2 off = floor(uv);

    float depth = 0.;
    for(int x = -1; x <= 1; x++){
        for(int y = -1; y <= 1; y++){
            vec2 id = off + vec2(x,y);
            float plateDepth = hash12(id + vec2(.5,.5));
            vec3 c = plate(id, uv - id);
            if(c != vec3(-1) && plateDepth > depth){
                depth = plateDepth;
                col = c;
            }

        }

    }










    fragColor = vec4(col,1.0);
}

