}


vec3 vollight ( vec3 lightpos, vec3 s, vec3 r, vec2 uv, float limit) {

  float rand=rnd(uv);


  const int volsteps=120; //50
  float voldist = 15.0;
  float stepsize = voldist/float(volsteps);
  vec3 lr=r*stepsize;
  vec3 lp=s + lr*rand;
  float stepdist=rand*stepsize;
  vec3 atcol=vec3(0);

  for(int i=0; i<volsteps; ++i) {

    if(stepdist>limit) {
      break;
    }

    vec3 lv = lightpos-lp;
    float ldistvol = length(lv);
    lv = normalize(lv);
    float shadvol = intersect(lp, lv, ldistvol, 10, 0.01);
    atcol += 5./(0.01+(pow(ldistvol,2.))) * shadvol;


    lp+=lr;
    stepdist+=stepsize;

  }
  return atcol;
}





