#define rot(a) mat2(cos(a), sin(a), -sin(a), cos(a)) 

float bx(vec3 p, vec3 s)
{
    vec3 q = abs(p) - s;
    return min(max(q.x,max(q.y,q.z)),0.) + length(max(q,0.));
}

float mp(vec3 p)
{

    float xID1 = p.x; //Calculate ID for rotation direction
    float xR1 = xID1 > 0. ? 1. : -1.;

    p.x = abs(p.x) - 8.5; //Domain repeat

    float b4 = bx(p, vec3(8.3,14,14)); //Bound 4 (32-cuboid sets)
    b4 = abs(b4) + 0.1;

    p.yz *= rot(iTime * xR1); //Rotate

    //--------

    float zID0 = p.z; //Calculate ID for rotation direction
    float zR0 = zID0 > 0. ? 1. : -1.;

    p.z = abs(p.z) - 6.5; //Domain repeat

    float b3 = bx(p, vec3(9,9,6.3));  //Bound 3 (16-cuboid sets)
    b3 = abs(b3) + 0.1;

    p.xy *= rot(iTime * zR0); //Rotate

    //--------

    float yID1 = p.y; //Calculate ID for rotation direction
    float yR1 = yID1 > 0. ? 1. : -1.;

    p.y = abs(p.y) - 3.5; //Domain repeat

    float b2 = bx(p, vec3(7,3.3,7)); //Bound 2 (8-cuboid sets)
    b2 = abs(b2) + 0.1;

    p.xz *= rot(iTime * yR1); //Rotate

    //--------

    float xID0 = p.x; //Calculate ID for rotation direction
    float xR0 = xID0 > 0. ? 1. : -1.;

    p.x = abs(p.x) - 3.5; //Domain repeat

    float b1 = bx(p, vec3(3.3,5,5)); //Bound 1 (4-cuboid sets)
    b1 = abs(b1) + 0.1;

    p.yz *= rot(iTime * xR0); //Rotate

    //--------

    float yID0 = p.y; //Calculate ID for rotation direction.
    float yR0 = yID0 > 0. ? 1. : -1.;

    p.y = abs(p.y) - 1.5; //Domain repeat.

    float b0 = bx(p, vec3(3,1.3,3)); //Bound 0 (2-cuboid pairs)
    b0 = abs(b0) + 0.1;

    p.xz *= rot(iTime * yR0); //Rotate

    //--------

    float c0 = bx(p, vec3(2,1,1)); //Actual cuboid that's being rendered
    c0 = min(b0, c0); //bound it..
    c0 = min(b1, c0); //bound it...
    c0 = min(b2, c0); //bound it again..
    c0 = min(b3, c0); //and again...
    c0 = min(b4, c0); //and again....

    return c0; //return cube distance
}

float tr(vec3 ro, vec3 rd) //ultra basic raymarcher
{
    float d = 0.;
    for(int i = 0; i < 512; i++)
    {
        float s = mp(ro + rd * d);
        d += s;
        if(s < 0.01 || d > 64.) break;
    }
    return d;
}

vec3 nm(vec3 p) //standard 3-tap normal calculation, inlined
{
    vec2 e = vec2(0.001,0); return normalize(mp(p) - vec3(mp(p - e.xyy),mp(p - e.yxy),mp(p - e.yyx)));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5 * iResolution.xy)/iResolution.y;

    vec3 ro = vec3(0,0,-25);
    ro.xz *= rot(iTime/2.);

    //some camera look direction code i grabbed real quick so I can easily move the camera around
    vec3 lk = vec3(0,0.,0);
    vec3 f = normalize(lk-ro);
    vec3 r = normalize(cross(vec3(0,1,0),f));
    vec3 rd = normalize(f*(0.99)+uv.x*r+uv.y*cross(f,r));    

    float h = tr(ro,rd); //distance
    vec3 p = ro + rd * h; //position

    if(h < 64.) //lighting
    {
        vec3 n = nm(p);
        vec3 l = vec3(0.5, 0.6, 0.7);

        float diff = length(n*l); //ultra basic tri-planer-axis-lighting-stuff idk but it works well

        fragColor = vec4(diff);
    }

}

