void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float pixelsPerSegment = pow(1.2, iTime+ 15.0);
    float axisThickness = 2.0;
    float gridThickness = 3.0;
    float graficThickness = 5.0;

    vec2 inp = (fragCoord - iResolution.xy * 0.5)/ pixelsPerSegment ;
    vec2 center = iResolution.xy * 0.5;

    float x = inp.x;
    float y = sin(x);


    vec3 col = vec3(1.0);
    graficThickness *= 0.5;
    // draw grid
    float startX = fragCoord.x - center.x + gridThickness * 0.5;
    if (startX - (float(int((startX) / pixelsPerSegment))
        * pixelsPerSegment)<= gridThickness &&
        fragCoord.x > center.x)
        col = vec3(0.9);
    startX = fragCoord.x - center.x - gridThickness * 0.5;
    if (abs(startX - (float(int((startX) / pixelsPerSegment)))
        * pixelsPerSegment) <= gridThickness &&
        fragCoord.x < center.x)
        col = vec3(0.9);
    float startY = fragCoord.y - center.y + gridThickness * 0.5;
    if (startY - (float(int((startY) / pixelsPerSegment))
        * pixelsPerSegment)<= gridThickness &&
        fragCoord.y > center.y)
        col = vec3(0.9);
    startY = fragCoord.y - center.y - gridThickness * 0.5;
    if (abs(startY - (float(int((startY) / pixelsPerSegment)))
        * pixelsPerSegment) <= gridThickness &&
        fragCoord.y < center.y)
        col = vec3(0.9);



    //draw axis
    if (abs(iResolution.x * 0.5 - fragCoord.x) <= axisThickness ||
        abs(iResolution.y * 0.5 - fragCoord.y) <= axisThickness )
    {
        col = vec3(0.0);
    }



    // draw function
    if (pow((pow(inp.y - y, 2.0) + pow(inp.x - x, 2.0)), 0.5) < graficThickness / pixelsPerSegment)
    {
        col = vec3(0.0);
    }
    // Output to screen
    fragColor = vec4(col,1.0);
}

#define PI         3.14159265358979
#define SAMPLES    2048
#define ITERATIONS 4

float Sign(vec2 p, vec3 abc, float t)
{
  float a  = abc.y * t;
  float b  = abc.z + t;
  float e  = exp(-abc.x * t);

  vec2  A  = vec2(e     , -abc.x * e     );
  vec2  B  = vec2(cos(a), -abc.y * sin(a));
  vec2  C  = vec2(sin(b),          cos(b));

  vec2  q0 = vec2(A.x * B.x            , A.x * C.x            );
  vec2  q1 = vec2(A.y * B.x + A.x * B.y, A.y * C.x + A.x * C.y);
  vec2  pq = p - q0;

  return sign(dot(pq, q1));
}

vec3 Func(vec2 p, vec3 abc, float t)
{
  float a  = abc.x, aa = a * a;
  float b  = abc.y, bb = b * b;
  float c  = abc.z, cc = c * c;
  float d  = exp(-a * t);
  vec2  e  = vec2(sin(b * t), cos(b * t));
  vec2  f  = vec2(sin(t + c), cos(t + c));

  vec3  A  = vec3(d  , -a * d  ,  aa * d  );
  vec3  B  = vec3(e.y, -b * e.x, -bb * e.y);
  vec3  C  = vec3(f.x,      f.y, -     f.x);

  vec2  q0 = vec2(A.x * B.x                             , A.x * C.x                             );
  vec2  q1 = vec2(A.y * B.x +      A.x * B.y            , A.y * C.x +      A.x * C.y            );
  vec2  q2 = vec2(A.z * B.x + 2. * A.y * B.y + A.x * B.z, A.z * C.x + 2. * A.y * C.y + A.x * C.z);
  vec2  pq = p - q0;

  return vec3(dot(pq, q1), dot(pq, q2) - dot(q1, q1), length(pq));
}

float Lissajous(vec2 p, vec3 abc)
{
  float dt, t, s0, s1, dist;
  vec3  func;
  int   i, j;

  dt   = 64. * PI / float(SAMPLES - 1);
  s0   = Sign(p, abc, 0.);
  dist = Func(p, abc, 0.).z;

  for(i = 1; i < SAMPLES; ++i)
  {
    s1 = Sign(p, abc, float(i) * dt);

    if(s0 == s1)
      continue;

    s0   = s1;
    func = Func(p, abc, t = (float(i) - .5) * dt);

    for(j = 0; j < ITERATIONS; ++j)
      func = Func(p, abc, t -= func.x / func.y);

    if(func.z < dist)
      dist = func.z;
  }

  return dist;
}

void mainImage(out vec4 C, in vec2 P)
{
    vec2 r = iResolution.xy * .5;
    vec2 p = (P - r)/r.y;

    float intensity = 2e-3 / Lissajous(1.1 * p, vec3(1./81., 2./3., iTime));
    float gamma     = pow(intensity, 1.0/2.2);

    C = vec4(vec3(0., gamma, 0.), 1.);
}

float blurredSphere(in vec2 uv, in float size)
{
    return pow(max(size - length(uv), 0.), 1.2);
}

vec2 journey(in vec2 uv, in vec2 start, in vec2 delta)
{
    return uv + fract(start + (delta * iTime)) - 0.5;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    ivec2 p = ivec2(fragCoord-0.5);

    vec3 noise = texelFetch( iChannel0, p % ivec2(iChannelResolution[0].xy), 0 ).xyz;

    vec3 tex = vec3(0.2, 0.1, 0.4) + (noise / 9.);

    // Non-aspected corrected:
    vec2 uv = fragCoord/iResolution.xy; // 0..1

    // Aspect-corrected
    // vec2 uv = (fragCoord - iResolution.xy*.5)/iResolution.y;


    uv -= 0.5; // -0.5 .. 0.5

    tex += vec3(0.3, 0.6, 0.8) * blurredSphere(journey(uv, vec2(0.3, 0.3), vec2(0.015, -0.2)), 0.4);

    tex += vec3(1., 0.5, 0.9) * blurredSphere(journey(uv, vec2(0., 0.1), vec2(-0.01, 0.015)), 0.3);

    tex += vec3(0.8, 1., 0.8) * blurredSphere(journey(uv, vec2(-0.3, -0.3), vec2(-0.02, -0.02)), 0.35);

    tex += vec3(1., 0.4, 0.0) * blurredSphere(journey(uv, vec2(0.2, 0.5), vec2(0.03, 0.009)), 0.25);

    fragColor = vec4(tex, 1.);
}

