// For visualizing the distance mnimizing quintic
float sdMinimizerPoly(in vec2 pos, in vec2 p, in vec2 v1, in vec2 v2, in vec2 v3, in vec2 v4) {
    // Convert to power basis
    vec2 a = v4 + 3.0 * (v2 - v3) - v1;
    vec2 b = 3.0 * (v1 - 2.0 * v2 + v3);
    vec2 c = 3.0 * (v2 - v1);
    vec2 d = v1 - p;

    // Quintic coefficients (derivative of distance-for-t with 2 factored out)
    float t5 = 3.0 * dot(a, a);
    float t4 = 5.0 * dot(a, b);
    float t3 = 4.0 * dot(a, c) + 2.0 * dot(b, b);
    float t2 = 3.0 * (dot(b, c) + dot(d, a));
    float t1 = dot(c, c) + 2.0 * dot(d, b);
    float t0 = dot(d, c);

    float y = ((((t5 * pos.x + t4) * pos.x + t3) * pos.x + t2) * pos.x + t1) * pos.x + t0;
    float dx = (((5.0 * t5 * pos.x + 4.0 * t4) * pos.x + 3.0 * t3) * pos.x + 2.0 * t2) * pos.x + t1;

    return abs(pos.y - y) / sqrt(1.0 + dx * dx);
}

float sdLine(in vec2 p, in vec2 a, in vec2 b) {
    vec2 pa = p - a, ba = b - a;
    return length(pa - ba * clamp(dot(pa, ba) / dot(ba, ba), 0.0, 1.0));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 center = 0.5 * iResolution.xy;
    vec2 uv = (fragCoord - center) / iResolution.y * 3.0;
    vec2 mouse = (iMouse.xy - center) / iResolution.y * 3.0;
    float unit = 6.0 / iResolution.y;
    float time = iTime;//610.94;

    float t1 = time * 0.5, t2 = time, t3 = time * 1.25;

    float c1 = cos(t1), s1 = sin(t1);
    float c2 = cos(t2), s2 = sin(t2);
    float c3 = cos(t3), s3 = sin(t3);

    /*
    vec2 a = vec2(-1.0, -0.75);
    vec2 b = vec2(s1, s2);
    vec2 c = vec2(c3, c1);
    vec2 d = vec2(1.0, -0.75);
    */

    vec2 a = vec2(c2 * 0.5, s3);
    vec2 b = vec2(-2.0, 1.0);
    vec2 c = vec2(2.0, 1.0);
    vec2 d = vec2(s2 * 0.5, c3);

    float sdf = sdCubicBezier(uv, a, b, c, d).w - 0.1;

    // Nice colors from iq
    vec3 color = 1.0 - sign(sdf) * vec3(0.1, 0.4, 0.7);
	color *= 1.0 - exp(-4.0 * abs(sdf));
	color *= 0.8 + 0.2 * cos(140.0 * sdf);
	color = mix(color, vec3(1.0), smoothstep(unit, 0.0, abs(sdf)));

    // Hull
    color = mix(color, vec3(1.0, 0.0, 0.0), smoothstep(unit, 0.0, sdLine(uv, a, b) - 0.01));
    color = mix(color, vec3(1.0, 0.0, 0.0), smoothstep(unit, 0.0, sdLine(uv, b, c) - 0.01));
    color = mix(color, vec3(1.0, 0.0, 0.0), smoothstep(unit, 0.0, sdLine(uv, c, d) - 0.01));

    // Control points
    color = mix(color, vec3(1.0, 1.0, 0.0), smoothstep(unit, 0.0, length(uv - a) - 0.03));
    color = mix(color, vec3(1.0, 1.0, 0.0), smoothstep(unit, 0.0, length(uv - b) - 0.03));
    color = mix(color, vec3(1.0, 1.0, 0.0), smoothstep(unit, 0.0, length(uv - c) - 0.03));
    color = mix(color, vec3(1.0, 1.0, 0.0), smoothstep(unit, 0.0, length(uv - d) - 0.03));

    // Draw shortest segment between the mouse and curve
    vec3 nearest = sdCubicBezier(mouse, a, b, c, d).xyz;
    color = mix(color, vec3(0.0, 1.0, 0.0), smoothstep(unit, 0.0, sdLine(uv, mouse, nearest.xy) - 0.01));
    color = mix(color, vec3(1.0, 0.8, 0.0), smoothstep(unit, 0.0, length(uv - mouse) - 0.05));
    color = mix(color, vec3(1.0, 0.8, 0.0), smoothstep(unit, 0.0, length(uv - nearest.xy) - 0.05));

    // Draw the distance minimizing quintic for the mouse position
    //color = mix(color, vec3(0.0, 0.5, 1.0), smoothstep(unit, 0.0, sdMinimizerPoly(uv, mouse, a, b, c, d) - 0.001));
    //color = color = mix(color, vec3(0.0), smoothstep(unit, 0.0, length(uv - vec2(nearest.z, 0.0)) - 0.03));

    fragColor = vec4(color, 1.0);
}

#define PI 3.14159265
#define ut (iTime * 1.)
#define n(x) normalize(x)
#define l(x) length(x)
#define f(x) fract(x)
#define v3 vec3(0)
#define sb 0.5
#define rv(x) (vec2(cos(x),sin(x)))

#define USE_TEXTURE

float t;
vec2 m, tc1, tc2, tc3;

float pD(vec3 ro,vec3 rd,vec4 p) {
	return -(dot(ro,p.xyz)+p.w)/dot(rd,p.xyz);
}

vec4 pI(vec3 ro,vec3 rd,vec4 p) {
	float d=pD(ro,rd,p);
	return vec4(ro+rd*d,d);
}

float sD(vec3 ro,vec3 rd,vec4 sph) {
	vec3 oc=ro-sph.xyz;
	float b=dot(oc,rd), c=dot(oc,oc)-sph.w*sph.w, h=b*b-c;
	if(h<0.0) return -1.0;
	return -b-sqrt(h);
}

vec4 sI(vec3 ro,vec3 rd,vec4 sph) {
	vec4 i=vec4(-1.);
	float d=sD(ro,rd,sph);
	if(d > 0.) { i.xyz=ro+rd*d; i.w=d; }
	return i;
}

vec4 pl, pl2, s, s2;

void cv(inout vec3 pos,inout vec3 vel) {
	vec3 h=s.xyz-pos,h2=s2.xyz-pos;
	vel+=n(h)/dot(h,h)+n(h2)/dot(h2,h2); pos+=vel;
}

vec4 i2p(vec3 ro,inout vec3 v) {
	vec3 pos=ro;
	cv(pos,v); cv(pos,v); cv(pos,v);
	cv(pos,v); cv(pos,v); cv(pos,v);
	return pI(ro,n(v),pl);
}

vec3 pal(float t) {
	// return vec3(1.);
	t=(t-ut*0.08);
	return abs(vec3(0.5,0,0.5)+0.5*cos(15.*(1.*t+vec3(0,0.33,0.67))));
}

// vec3 pal(float t) {
// 	return (0.5+0.5*cos(3.*(1.*t+vec3(0,0.33,0.67))));
// }

float tex(vec2 uv) {
#ifdef USE_TEXTURE
    return dot(texture(iChannel0,uv*0.25).rgb,vec3(0.333));
#else    
	return mix(1.-l(f(uv)*2.-1.),0.25,min(l(fwidth(uv)),1.));
#endif
}

vec3 pC(vec4 pi, vec4 p) {
	vec3 c=vec3(0);
	if(pi.w<0.) return c;
	c += tex(pi.xy+vec2(t,tc1.y)*vec2(sign(pi.z)));
	pi.w=dot(n(vec3(0,0,1)),n(vec3(pi.xy,1)));
	c *= pal(f(-pi.w));
	float sl=1./min(pow(sD(pi.xyz,n(s.xyz-pi.xyz),s),2.),10.);
	float sl2=1./min(pow(sD(pi.xyz,n(s2.xyz-pi.xyz),s2),2.),10.);
	c *= (sl+sl2)*1.;
	if(p==pl) c+=pow(dot(n(vec3(pi.xy,1)),pl.xyz),16.);
	// c *= pow(pi.w,0.5);
	return pow(c,vec3(0.666));
}

void pR(inout vec2 p,float a) {
	p=cos(a)*p+sin(a)*vec2(p.y,-p.x);
}

vec3 sC(vec4 sp,vec4 si,vec3 ro) {
	vec3 sc=v3, c, c2;
	vec3 n=n(si.xyz-sp.xyz),ci=n(si.xyz-ro);
	vec3 lp=0.-(pl.xyz*pl.w),l=n(lp-si.xyz);
	sc += max(0.,dot(l,n)) * 0.5;

	vec3 r=reflect(ci,n);
	vec4 pi=pI(si.xyz,r,pl), pi2=pI(si.xyz,r,pl2);
	c = mix(sc,pC(pi,pl),sb);
	c2 = mix(sc,pC(pi2,pl2),sb);
	float md = pi.w;
	if( pi2.w > pi.w ) { c = c2; md = pi2.w; }

	vec4 so=s;
	if(sp.x == s.x) so = s2;
	sp=so; ro=si.xyz; si=sI(si.xyz,n(r),so);

	if( si.w > 0. && si.w < md ) {
		vec3 osc=v3, osc2 = v3;
		n=n(si.xyz-sp.xyz),ci=n(si.xyz-ro);
		lp=-(pl.xyz*pl.w),l=n(lp-si.xyz);
		osc += max(0.,dot(l,n)) * 0.5;
		r=reflect(ci,n);
		pi=pI(si.xyz,r,pl),pi2=pI(si.xyz,r,pl2);
		osc = mix(osc,pC(pi,pl),sb);
		osc2 = mix(osc,pC(pi2,pl2),sb);
		if( pi2.w > pi.w ) { osc = osc2; }
		c = mix(sc,osc,sb);
	}
	return c;
}

vec3 gPC(vec2 uv) {
	vec3 ro=vec3(0,7.2,0), rd=n(vec3(uv*2.,-1.0));
	vec3 c=v3;
	pR(rd.yz,PI*(m.y-0.5));
	pR(rd.xy,2.*PI*(m.x-0.5));

	vec4 pi=i2p(ro,rd), pi2=pI(ro,n(rd),pl2);
	vec4 sip=sI(ro,n(rd),s2), si=sI(ro,n(rd),s);
	float md=1e10;
	if(sip.w>0. && sip.w<md) {md=sip.w; c=sC(s2,sip,ro);}
	if(si.w>0. && si.w<md) {md=si.w; c=sC(s,si,ro);}
	if(pi2.w>0. && pi2.w<md) {md=pi2.w; c+=pC(pi2,pl2);}
	if(pi.w>0. && pi.w<md) {md=pi.w; c+=pC(pi,pl);}
	c *= exp( -0.05*md );

	return c;
}

vec3 hash3( float n ) { return fract(sin(vec3(n,n+1.0,n+2.0))*43758.5453123); }

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  m=iMouse.xy / iResolution.xy * vec2(1,-1);
  t=ut*0.2;
  tc1=rv(t*PI*0.5);
  tc2=rv(t*PI);
  tc3=rv(ut*PI*0.125);
  pl=vec4(n(vec3(0, 0, 3)),3.9);
  pl2=vec4(n(vec3(0,0,-1)),1.9);
  s=vec4(5.5*tc3.x,5.5*-tc3.y,0.+4.*tc2.x,1.9);
  s2=vec4(-s.x,-s.y,0.-4.*tc2.x,1.9);


  vec2 uv=(fragCoord.xy-.5*iResolution.xy)/iResolution.y;
  vec2 q = fragCoord.xy / iResolution.xy;
  vec3 col = gPC(uv.xy);
	//-----------------------------------------------------
  // postprocess
  //-----------------------------------------------------

  // gama
  col = pow( col, vec3(0.44,0.5,0.55) );

  // contrast
  col = mix( col, smoothstep( 0.0, 1.0, col ), 0.5 );

  // saturate
  col = mix( col, vec3(dot(col,vec3(0.333))), -0.2 );

  // vigneting
  col *= 0.2 + 0.8*pow(16.0*q.x*q.y*(1.0-q.x)*(1.0-q.y),0.2);

  // dithering
  col += (1.0/255.0)*hash3(q.x+13.3214*q.y);

	fragColor=vec4(col,1.);
}

