    float focus=2.;
    vec3 cam = vec3(0,0,-5.*focus);
    vec3 dir = normalize(vec3(uv,focus));

    float dist = ray(cam, dir);
    float G = glow; 
    vec3 hit = cam+dir*dist;
    vec3 norm = normalize(grad(hit));

    vec3 refl = reflect(dir,norm);
    float refldist = ray(hit+refl*.1, refl);
    float reflglow = glow;

    vec3 col;
    if (dist < 1000.) {
      // thanks blackle for the awesome fake image lighting :3
      float diff = length(sin(norm)*.5+.5)/sqrt(3.);
      col = diff*diff*vec3(.5,.9,1);
    } else {
      float moonspeed = 40.;
      vec2 p = uv*8.+.5*texture(iChannel0,uv).r;
      col = vec3(1,.5,.1)*(1.-length(p+vec2(4.*sin(moonspeed*sin(time)),-1.+cos(moonspeed*sin(time)))));
      col = max(col,-mouse.y);
      col = abs(col);
    }


    // lower numbers are a denser, more uniform fog
    // high numbers are very glowy and a little glitchy
    float UNIFORMITY = 0.8;
    col += pow(G, UNIFORMITY) *mouse.x * vec3(.9, .3,.1);;
    col += reflglow * vec3(.9,.2,.1)*.8;
    // Output to screen
    fragColor = vec4(sqrt(col),1.0);
}

vec3 Uncharted2ToneMapping(vec3 color)
{
    float gamma = 2.2;
	float A = 0.15;
	float B = 0.50;
	float C = 0.10;
	float D = 0.20;
	float E = 0.02;
	float F = 0.30;
	float W = 11.2;
	float exposure = 2.;
	color *= exposure;
	color = ((color * (A * color + C * B) + D * E) / (color * (A * color + B) + D * F)) - E / F;
	float white = ((W * (A * W + C * B) + D * E) / (W * (A * W + B) + D * F)) - E / F;
	color /= white;
	color = pow(color, vec3(1. / gamma));
	return color;
}

