#define rad(x) (radians(360.*(x)))
#define time (iTime*.1)
#define mouse (iMouse/iResolution.y)

float box(vec3 p, vec3 a) {
  vec3 q = abs(p)-a;
  return length(max(q,0.))+min(0.,max(q.x,max(q.y,q.z)));
}

float sphere(vec3 p, float r) { return length(p) - r; }

mat2 rot(float a) { float c=cos(a),s=sin(a); return mat2(c,s,-s,c); }
float glow;

#define tri(x) (abs(fract((x)*.5)-.5)*2.)
#define nsin(x) (.5+.5*sin(x))
float scene(vec3 p) {
  float bgsize = 2.5;
  vec3 bgp = p.xyz;
  bgp.xz *= rot(time);
  float bg = box(bgp+vec3(0,bgsize+1.2,0),vec3(bgsize))-.3;//p.y + 2.;
  vec3 orig_p = p.xyz;
  p.xz *= rot(rad(.2*sin(time)));
  p.xy *= rot(rad(.3*time*sqrt(2.)));
  p.yz *= rot(rad(2./6.));

  p = orig_p;
  p.y -= 1.;
  p.xy += .2*mouse.y*nsin(tan(time*10.)*.01)*rot(time*300.)*vec2(1,0);// sin(time*6.);
  p.yz *= rot(rad(.25)+.5*cos(10.*time));
  p.xy *= rot(rad(.25)+.1*sin(20.*time));
  float yzrot = 3.14+sin(tan(time*12.))*.1;
  p.yz *= rot(yzrot);

  float squeeze = 4.8;
  float sph = sphere(p,2.);
  vec2 samp = vec2(atan(p.x,p.z)/3.141592654, p.y*.2);
  float solid = mix(sph,box(p,vec3(1.5)-.2),.8+.5*sin(time*150.));

  solid = max(solid,.2-box(p-vec3(0,.6,.4),.5*vec3(5.,.05,.2)));
  solid = max(solid,.2-box(p-vec3(0,-.6,.4),.5*vec3(5.,.05,.2)));
  solid = max(solid,.2-box(p*(sin(time*13.)*abs(p.y)-2.)-vec3(0,0,1.2),vec3(5.,.6,.2*nsin(tan(time)))));

  vec2 pole = abs(p.xz)- vec2(1,0)*rot(15.*time);
  solid = min(solid, max(p.z,length(pole)-p.y*.001));
  solid += (.1+.2*nsin(time))*mix(texture(iChannel0, samp).r, texture(iChannel0, samp+1.).r, .5);

  float glowr = .5;
  float glowbase = mix(solid,bg,.1+.5*sin(time*20.));
  float glowbox = max(glowbase - glowr, -glowbase+glowr-.4);
  // a buffer between the solid and the fog to prevent
  // super-bright areas on the solid as the raymarcher
  // slows down. try setting this to 0 and see what happens
  // should be at least DENSITY
  // raising this is the best way to deal with edge glow around
  // the solid
  float BUFFER_ZONE = 0.05;


  // lower = more dense fog. should be at least 1/stepcount
  // (100 here)
  float FOGSTEP = .03;

  // ==== and here's the 2-line fog effect ====
  // * if we're inside the glowbox, increase the glow by how far
  // we have penetrated into the box.
  // * however, if we're passing close to the solid object, we have to
  // not increase the glow because it will overcount as we step closer
  // and closer to the solid. so we zero it out below the buffer zone
  glow += max(0., -glowbox)*smoothstep(0.,BUFFER_ZONE*2.,solid);

  // step towards the glowbox by the distance we are inside the cube,
  // plus a little buffer to get us inside the box. if we're *in* the
  // box, we want to keep moving, and abs(glowbox) will make sure we're
  // approximately inside the box still. note that we will *never* detect
  // a surface at any point of the glowbox, because the minimum distance is
  // always positive.
  glowbox = abs(glowbox) + FOGSTEP;
  return min(bg,min(solid,glowbox));
}


float ray(vec3 start, vec3 dir) {
  float dist=0.;
  glow=0.;

  for (int i=0;i<200;i++) {
    float c = scene(start+dir*dist);
    dist += c;
    if (c < .001) return dist;
    if (dist > 10000.) return 100001.;
  }

  return 1001.;//min(dist,1000.);
}

vec3 grad(vec3 p) {
  mat3 K = mat3(p,p,p) - mat3(.01);
  return scene(p)-vec3(scene(K[0]),scene(K[1]),scene(K[2]));
}

float N(float x) {
  return fract(tan(x*1234.-2345.));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv -= .5;
    uv.x *= iResolution.x/iResolution.y;

    float band = floor(uv.x*20.+time*200.);
    uv.y = fract(uv.y+.5 + 0.01*mouse.y*N(band+7.)*time*N(band))-.5;

    float focus=2.;
    vec3 cam = vec3(0,0,-5.*focus);
    vec3 dir = normalize(vec3(uv,focus));

    float dist = ray(cam, dir);
    float G = glow; 
    vec3 hit = cam+dir*dist;
    vec3 norm = normalize(grad(hit));

    vec3 refl = reflect(dir,norm);
    float refldist = ray(hit+refl*.1, refl);
    float reflglow = glow;

    vec3 col;
    if (dist < 1000.) {
      // thanks blackle for the awesome fake image lighting :3
      float diff = length(sin(norm)*.5+.5)/sqrt(3.);
      col = max(0., pow(diff, 2.))*vec3(.1,0,0);
