      vec2 p = uv*8.+.5*texture(iChannel0,uv).r;
      col = vec3(1,.5,.1)*(1.-length(p+vec2(4.*sin(moonspeed*sin(time)),-1.+cos(moonspeed*sin(time)))));
      col = max(col,-mouse.y);
      //col = tan(col);
    }


    // lower numbers are a denser, more uniform fog
    // high numbers are very glowy and a little glitchy
    float UNIFORMITY = 0.8;
    col += pow(G, UNIFORMITY) *mouse.x * vec3(.9, .3,.1);;
    col += reflglow * vec3(.9,.2,.1)*.8;
    // Output to screen
    fragColor = vec4(sqrt(col),1.0);
}

#define ITERATIONS 200

vec2 zoomC = vec2(-0.648705, 0.115672);
float zoomrate = 2.;

#define N 5
vec2 roots[N] = vec2[N](vec2(-1,0), vec2(0,1), vec2(0,-1), vec2(0.7, 0.7), vec2(0.7,-0.7));
vec3 colors[N] = vec3[N](vec3(1., .7, .7), vec3(1., .3, .3), vec3(1., 1., 0.), vec3(.3, .3, 1.), vec3(.7, 1., .7) );
                        // pink, red, yellow, blue, green

int closest(vec2 z){
    int k = 0;
    for (int i = 0; i < N; i++) {
        if (length(z - roots[k]) > length(z - roots[i])) k = i;
    }
    return k;
}

vec2 mul(vec2 a, vec2 b) {
    return vec2(a.x * b.x - a.y * b.y, a.x * b.y + a.y * b.x);
}

vec2 div(vec2 a, vec2 b) {
    float d = b.x * b.x + b.y * b.y;
    if (d == 0.0) {
        d = 0.001;
    }
    return vec2 (a.x * b.x + a.y * b.y, a.y * b.x - a.x * b.y) / d;
}

vec2 f(vec2 x) {
    vec2 total = vec2(1.0, 0.0);
    for (int i = 0; i < N; i++) {
        total = mul(total, x - roots[i]);
    }
    return total;
}

vec2 f_deriv(vec2 x) {
    vec2 total = vec2(0.0, 0.0);
    for (int i = 0; i < N; i++) {
        vec2 subtotal = vec2(1.0, 0.0);
        for (int j = 0; j < N; j++) {
            if (i != j) subtotal = mul(subtotal, x - roots[j]);
        }
        total += subtotal;
    }
    return total;
}

vec2 scale( vec2 x ) {
    float zoom = exp(-zoomrate * iTime);

    x -= iResolution.xy/2.;
    x /= iResolution.y;
    x *= 2.;

    x *= zoom;
    x += zoomC * (1. - zoom);
    return x;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{   
    vec2 z = scale(fragCoord);
    vec4 m = vec4(scale(iMouse.xy), iMouse.zw);

    if ( m.z > 0.0 ) {
        roots[0] = m.xy;
    }

    for (int i = 0; i < ITERATIONS; i++) {
        z -= div(f(z), f_deriv(z));
    }

    fragColor = vec4(colors[closest(z)], 1.0);
}

#define PI 3.1415
#define ASPECT_RATIO iResolution.y / iResolution.x
#define FOV PI / 4.0
#define UP vec3(0.0, 1.0, 0.0)
#define MAX_IT 5
#define INFINITE 9999999999999999.0

//x is in front of you, y is up, z is right.

struct Hit
{
    bool hit;
    vec3 pos;
    float t;
    vec3 normal;
    vec3 ray;
    vec3 rgb;
    int mode;
};
struct Camera
{
    vec3 Position;
    vec3 Direction;
};

Hit PlaneHit(vec3 origin, vec3 ray, float height, int mode)
{
    if(ray.y == 0.0){
        if(height == origin.y)
            return Hit(true, origin, 0.0, UP, ray, vec3(0.0, 1.0, 0.0), mode);
        return Hit(false, vec3(0.0), 0.0, vec3(0.0), vec3(0.0), vec3(0.0), mode);
    }

    float t = (height - origin.y) / ray.y;
    if(t < 0.0)
        return Hit(false, vec3(0.0), 0.0, vec3(0.0), vec3(0.0), vec3(0.0), mode);

    float dev = texture(iChannel1, mod(t*ray.xz, 1.0)).x * 0.2;
    vec3 ndev = vec3(texture(iChannel1, mod(t*ray.xz, 1.0)).y, 1.0, texture(iChannel1, mod(t*ray.xz, 1.0)).z);
    return Hit(true, origin + (t)*ray + dev*UP, t, UP + 0.9*ndev, ray, vec3(1.0), mode);
}
Hit SphereHit(vec3 origin, vec3 ray, vec3 sphpos, float radius, int mode, vec3 color)
{
    float a = dot(ray, ray);
    float b = dot(ray, origin - sphpos);
    float c = dot(origin - sphpos, origin - sphpos) - radius * radius;
    float d = b*b - a*c;
    if(d < 0.0)
    {
        return Hit(false, vec3(0.0), 0.0, vec3(0.0), vec3(0.0), vec3(0.0), mode);
    }

    float fr = (-b - sqrt(d))/a;
    float sr = (-b + sqrt(d))/a;

    if(fr < 0.0)
        return Hit(false, vec3(0.0), 0.0, vec3(0.0), vec3(0.0), vec3(0.0), mode);

    vec3 n = vec3(origin + fr*ray - sphpos);
    n /= length(n);


    float dev = texture(iChannel0, vec2(mod(fr * 0.8*sin(iTime*0.05), 1.0))).x;
    dev *= 1.1 * float(1 - mode);

    return Hit(true, origin + (fr + dev) * ray, fr, n, ray, color, mode);

}
Hit GetHit(vec3 origin, vec3 ray)
{
    Hit h1 = SphereHit(origin, ray, vec3(8.0 + sin(iTime), cos(iTime), cos(iTime)), 0.9, 0, vec3(1.0, 1.0, 1.0));
    Hit h2 = PlaneHit(origin, ray, -2.0, 0);  
    Hit h3 = SphereHit(origin, ray, vec3(8.0, -1.48, 0.5), 0.5, 0, vec3(1.0, 0.0, 0.0));
    Hit h4 = SphereHit(origin, ray, vec3(6.1, -1.70, -1.5), 0.4, 0, vec3(0.0, 0.7, 0.3));
    Hit h5 = SphereHit(origin, ray, vec3(7.8, -1.78, 1.5), 0.2, 0, vec3(1.0, 0.0, 1.0));
    Hit h6 = SphereHit(origin, ray, vec3(5.5, -0.48, 2.8), 1.5, 0, vec3(1.0, 0.0, 1.0));
    Hit h7 = SphereHit(origin, ray, vec3(12.9, -1.48, -2.5), 0.5, 0, vec3(0.0, 0.1, 0.5));
    Hit h8 = SphereHit(origin, ray, vec3(9.8, -1.5, -1.3), 0.2, 0, vec3(0.0, 0.0, 0.0));
    Hit h9 = SphereHit(origin, ray, vec3(3.8, -1.78, 2.9), 0.2, 0, vec3(1.0, 0.0, 1.0));
    Hit h10 =SphereHit(origin, ray, vec3(6.8, -1.78,-1.5), 0.2, 0, vec3(1.0, 0.0, 1.0));



    Hit HitList[] = Hit[](h1, h2, h3, h4, h5, h6, h7, h8, h9, h10);

    Hit h = Hit(false, vec3(0.0), INFINITE, vec3(0.0), vec3(0.0), vec3(0.0), 0);
    for(int i = 0; i < HitList.length(); i++)
    {
        if(HitList[i].hit && HitList[i].t < h.t)
            h = HitList[i];
    }
    return h;

}
vec4 GetColor(Hit h)
{
    vec3 lightdir = vec3(0.0, -0.7, 0.5 + 0.5*cos(iTime));
    float inten = 1.0;
    float base = 0.5;
    float t = 0.7;
    lightdir /= length(lightdir);
    if(h.hit)
    {
         vec4 c = (t * base + (1.0 - t)*dot(-lightdir, h.normal) * inten) * vec4(h.rgb,0.0);
         vec3 dir = - lightdir;
         vec3 pos = h.pos + dir * 0.005;
         Hit new_hit = GetHit(pos, dir);
           if(new_hit.hit)
               return vec4(0.0);
        return c;
    }
    else{
        float skyi = 1.0;
        return (1.0 - skyi)*vec4(1.0) + skyi*vec4(0.1, 0.7, 1.0, 0.0);
    }
}
vec3 GetRayFromUV(vec3 direction, vec2 uv)
{
    direction /= length(direction);
    vec3 r = direction - uv.x * tan(FOV) * cross(direction, UP) + ASPECT_RATIO * uv.y * UP;
    r /= length(r);
    return r;   
}

vec4 pixel(Camera cam, vec2 uv)
{
    vec3 r = GetRayFromUV(cam.Direction, uv);
    Hit h;
    vec4 color = vec4(0.0);

    vec3 origin = cam.Position;
    vec3 dir = r;

    float intensity = 0.5;

    for(int i = 0; i < MAX_IT; i++)
    {
        Hit h = GetHit(origin, dir);
        if(!h.hit || h.mode == 1)
        {
            color = color + intensity * GetColor(h);
            break;
        }
        if(h.mode == 0){
            dir = reflect(dir, h.normal);
            dir /= length(dir);
            origin = h.pos + dir*0.1;
        }
        if(h.mode == 2)
        {
            dir = reflect(dir, h.normal);
            dir /= length(dir);
            origin = h.pos + dir*0.1;

            Hit new_hit = GetHit(origin, dir);
            color = color + intensity*intensity*intensity * GetColor(new_hit);

            dir = h.ray;
            origin = h.pos + h.ray * 1.1;
        }
        color = color + intensity * GetColor(h);
        intensity = intensity * 0.5;
    }
    return color;
}
