vec2 sdf(vec3 p){
  p.y -=.2;
    p.xy *=rot(-.785*.5);
  float bump = fract(iTime);
  vec2 h;
  vec3 hp = p;
  hp.y -=1.;
  hp.x +=1.;
  h.x =  max(abs(hp.z)-.1-bump,box2(hp.xy,vec2(.5)));
  h.y = 1.;

  vec2 t;
  vec3 tp = p;
  tp.x +=1.;
  tp.y +=1.;
  t.x =  max(abs(tp.z)-.1-bump,box2(tp.xy,vec2(.5,1.)));
  t.y = 1.;

  h = t.x < h.x ? t:h;

  tp = p;
  tp.y+=.3;
  tp.x +=.1;
  t.x = max(abs(tp.z)-.1-bump,length(tp.xy)-1.75);
  t.x = max(-tp.x+.1,t.x);

  float tt  = max(abs(tp.z)-.2-bump,length(tp.xy)-1.);
  tt = max(-tp.x,tt);
  t.x= max(-tt,t.x);
  t.y = 2.;

    h = t.x < h.x ? t:h;
  return h;
  }
