vec3 txt(vec2 uv){
  uv*=4.;
  uv = vec2(log(length(uv)),atan(uv.y,uv.x))*3.1415;
  uv.x -=iTime;
  vec2 id = floor(uv);
  uv = fract(uv)-.5;
     vec3 col = vec3(.8,.2,.2);
  if(mod(id.x,2.)==0.){
       uv*=4.;
       uv.y +=iTime;
       uv = fract(uv)-.5;
    col = vec3(.2,.8,.2);
    }
  float d = diam2(uv,.2);
  d=  mix(d,abs(d)-.01,asin(sin(uv.x+uv.y))); // NOT SQUIDGAME 
  d=  smoothstep(1.7*fwidth(d),0.,d);

  return .5*col*d;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

  vec3 col = txt(uv);

  vec3 ro = vec3(sin(iTime),0.,-5.);
  vec3 rt = vec3(0.);
  vec3 rp = ro;
  vec3 z = normalize(rt-ro);
  vec3 x = normalize(cross(z,vec3(0.,-1.,0.)));
  vec3 y = normalize(cross(z,x));

  vec3 rd = mat3(x,y,z)*normalize(vec3(uv,1.));
  vec3 light = vec3(1.,2.,-3.);
  vec3 acc = vec3(0.);
  float time = mod(iTime,10.);
for(float i=0.;i<=128.;i++){
    vec2 d = sdf(rp);


     if(time < 5. && length(rp-vec3(.25,.1,.0)) >.6 ) acc += (d.y ==1. ? vec3(.2,.9,.2):vec3(.9,.2,.2))*exp(-abs(d.x))/(50.-min(40.,fract(iTime)*50.));
     if(time < 5. &&  fract(iTime+ length(rp)*.5)<0.5){  d.x = max(.001,abs(d.x));}

    if(d.x <.0001){
        vec3 n = norm(rp,.001);
        vec3 nn = norm(rp,.01);
        float diff = max(0.,dot(normalize(light-rp),n));
        float spc = max(0.,dot(normalize(light-ro),reflect(-normalize(light),n)));
      spc  = pow(spc,32.);
        if(d.y == 1.){
             col = vec3(.2,.9,.2)*diff;
        }  else {
             col = vec3(.9,.2,.2)*diff;

          }
         col = time < 5. ?col : mix(col, col*step(.2,length(nn-n)),floor(asin(sin(rp.y+iTime)*.5)*10.)*.5+.5);
         col +=spc*vec3(1.);
      break;
      }
      rp +=rd*d.x;

  }  
  col +=acc;
    // Output to screen
    fragColor = vec4(col,1.0);
}

#define pi acos(-1.)

float noise(vec2 st){
    return fract(sin(dot(vec2(12.23,74.343),st))*43254.);  
}

float noise2D(vec2 st){

  //id,fract
  vec2 id =floor(st);
  vec2 f = fract(st);

  //nachbarn
  float a = noise(id);
  float b = noise(id + vec2(1.,0.));
  float c = noise(id + vec2(0.,1.));
  float d = noise(id + vec2(1.));


  //f
  f = smoothstep(0.,1.,f);

  //mix
  float ab = mix(a,b,f.x);
  float cd = mix(c,d,f.x);
  return mix(ab,cd,f.y);
}

mat2 rot45 = mat2(0.707,-0.707,0.707,0.707);

mat2 rot(float a){
  float s = sin(a); float c = cos(a);
  return mat2(c,-s,s,c);
}
float fbm(vec2 st, float N, float rt){
    st*=3.;

  float s = .5;
  float ret = 0.;
  for(float i = 0.; i < N; i++){

      ret += noise2D(st)*s; st *= 2.9; s/=2.; st *= rot((pi*(i+1.)/N)+rt*8.);
      //st.x += iTime;
  }
  return ret;

}


