//----------------------------------------------------------------------------------------
//  3 out, 1 in...
vec3 hash31(float p)
{
    //from David Hoskin's "Hash without sine"
   vec3 p3 = fract(vec3(p) * vec3(.1031, .1030, .0973));
   p3 += dot(p3, p3.yzx+33.33);
   return fract((p3.xxy+p3.yzz)*p3.zyx); 
}

vec2 triangle_wave(vec2 a,float scale){
    return abs(fract((a+c1.xy)*scale)-.5);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0.0);
    vec3 col;  
    float t1 = 4.5/2.;

    vec2 uv = (fragCoord-iResolution.xy)/iResolution.y/t1/2.0;
    uv += vec2(iTime/2.0,iTime/3.0)/t1/8.0;
    float t2 = +floor((iTime)/10.0+uv.x);
    vec3 random1 = (hash31(t2)-vec3(.5))/4.;
    vec3 random2 = (hash31(1.+t2)-vec3(.5))/4.;
    vec3 random3 = (hash31(2.+t2)-vec3(.5))/4.;
    vec3 random4 = (hash31(3.+t2)-vec3(.5))/4.;

    float offset = .5;
    for(int c=0;c<3;c++){
        float scale = c1.z;
        for(int i=0;i<3;i++)
        {
            float factor = 1.25;
            float l1 = col.x;

            uv = triangle_wave(uv.yx+l1+offset,scale)+triangle_wave(uv,scale);
            uv.x *= -factor;

            for(int k = 0; k < 3; k++){
                uv = triangle_wave((uv+uv.yx*vec2(random1[k],random2[k])-l1+offset)/(random4[k]+1.),scale+random3[k]);
                uv.x /= -factor;
                //uv += vec2(random1[k],random2[k])*l1;

            }


            scale *= 1./(1.+l1);

            //scale2 *= 2. - l1;

            col = col.yxz;
            col[c] = fract((uv.x)-(uv.y));


        }
	}

    fragColor = vec4(vec3(col),1.0);

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy-.5f;
    uv.x*=iResolution.x/iResolution.y;

    vec2 uv0 = uv;

    //Normalized mouse position
    vec2 mousePos = iMouse.xy / iResolution.xy -.5f;
    mousePos.x*=iResolution.x/iResolution.y;
    if(iMouse.z<.1f) mousePos = vec2(0);

    //Set UV center to MousePos and mul by centered UVs
    uv-=mousePos;
    uv*=uv0; 


    // Time varying pixel color    
    float r= abs(sin(length(uv)*20.f+iTime));
    float g= abs(cos(length(uv)*21.f+iTime*1.15f));
    float b= (1.f-r)*abs(cos(iTime*.6f));


    // Output to screen
    fragColor = vec4(r,g,b,1.f);
}

