/*
    The code here is kind of messy
    but in the end the geometry is 
    based on modPolar. Then I add
    glow, texture, bumpmapping, etc.

    There are definitly a ton of cool
    things you can do with this type
    of tunnel. Changing the number
    of angles is an easy one.

    Jeyko has some really good ones:

    https://www.shadertoy.com/view/Wl3XD2
    https://www.shadertoy.com/view/wdXczn
    https://www.shadertoy.com/view/ttdXzj

*/

//you can change number of angles here but only 5 plus works without
//issues but if you are willing to reduce the radius
//most of the artifacts can be hidden.
#define number_of_angles 8.
#define defined_radius .9


#define eps 0.001
#define S smoothstep
#define T iTime
#define TX texture
#define C clamp

#define MAX_STEPS 50
#define MAX_DIST 50.
#define pi 3.14159265
#define air 1.
#define glass 1.4

float smin( float a, float b, float k )
{
    float h = clamp( 0.5+0.5*(b-a)/k, 0.0, 1.0 );
    return mix( b, a, h ) - k*h*(1.0-h);
}

float smax( float a, float b, float k ){
    float h = clamp( 0.5 + 0.5 * (a - b) / k, 0.0, 1.0 );
    return mix( b, a, h ) + k * h * (1.0 - h);
}

mat2 rot(float a){
    float c = cos(a), s = sin(a);
    return mat2(c,-s,s,c);
}

float sdBox(vec3 p, vec3 d){
    p = abs(p)-d;
    return length(max(p,0.)) + 
    min(max(p.x,max(p.y,p.z)), 0.);
}
float sdBox2D(vec3 p, vec3 d){
    p = abs(p)-d;
    //return length(max(p,0.)) + 
    return max(p.x,p.z);
}
vec2 modPolar(vec2 p, float n){

    float a0 = float(n) / radians(360.); 
    float a = round( atan(p.y, p.x) * a0 ) / a0;

    p -= vec2(cos(a), sin(a));

    p *= rot(-a);

    return p;
}

vec3 path(vec3 p){
     p.x += sin(p.z/4.);
     p.y += sin(p.z/4.+3.);
     return p;
}

float globalID = -1.;
float glow;

float map(vec3 p){

    p = path(p);
    p.xy*=rot(p.z/20.);
    float radius = defined_radius;//0.8
    float num = number_of_angles; //5 to 8
    float z = sin(floor(p.z/4.))/4.;
    float d = length(p.xy) - 2.;
    //p.xy -= vec2(z);

    //magic!
    vec3 st = vec3(modPolar(p.xy, num), mod(p.z, 4.)-2. );

    float wall = sdBox( st-vec3(radius,0.,0.), vec3(0.1,2.,2.) );
    d = min(-d,wall);
    //d = min(d, length(st-vec3(radius,0.,0.))-0.3);

    //d = min(d, sdBox( st-vec3(radius,0.,0.), vec3(0.6,.3, pow(length(p.xy)-1., 2.7) ) ));

    //door layer one thick
    float layer1 = sdBox( st-vec3(radius,0.,0.), vec3(0.5,2.,.1) );
    //door layer two thinner
    float layer2 = sdBox( st-vec3(radius,0.,0.), vec3(0.8,2.,.08) );
    //door layer three really thin edge thing
    float layer3 = sdBox( st-vec3(radius,0.,0.), vec3(0.84,.7,.01) );

    d = min(d,min(layer1,min(layer2,layer3)));

    vec3 st3 = vec3(modPolar(p.xy, num), mod(p.z, .2)-.1);
    float lines = sdBox( st3-vec3(radius*1.0,0.,0.), vec3(0.13,1.,.02) );
    //float lines = sdBox( st3-vec3(radius,0.,0.), vec3(0.15,1.,.03) );
    d = min(d,lines);

    vec3 st2 = vec3(modPolar(p.xy, num), mod(p.z, 2.)-1.);
    //panel thing
    float panel = sdBox( st2-vec3(radius,0.,0.), vec3(0.13,.6,.9));
    d = min(d,panel);
    //panel-indent thing
    //first version has artifacts
    //float indent = sdBox( st2-vec3(radius*0.74,0.,0.), vec3(0.15,.58,.8) );
    float indent = sdBox( st2-vec3(radius*0.74,0.,0.), vec3(0.15,.5,.75) );
    d = smax(d, -indent,0.003);
    float outdent = sdBox( st2-vec3(radius,0.,0.), vec3(0.15,.5,.78) );
    d = min(d, outdent);

    //ramp thing
    float ramp = sdBox( st-vec3(radius,0.,0.), vec3(0.6,.3, pow(length(st.x/6.), .7) ));
    //ramp = max(ramp, -sdBox( st-vec3(radius,0.,-.3), vec3(0.5,.05, 0.2 ) ));
    d = smin(d,ramp,0.01);

    vec3 st4 = vec3(p.x,p.y,mod(p.z,2.)-1.);
    float bridge = sdBox( st4-vec3(0.,-1.1,0.), vec3(0.6,.04, .8 ));
    //d = min(d,bridge);

    if(wall <= d)globalID = 4.;
    else if(outdent <= d)globalID = 6.;
    else if(layer1 <= d)globalID = 1.;
    else if(layer2 <= d)globalID = 2.;

    else if(layer3 <= d){
        globalID = 3.;
        //glow += abs(max(0.0001,(0.00005/(0.03*pow(layer3, 1.)))));
        //glow += abs(max(0.0001,(0.0001/(0.0075*pow(layer3,.7 - 0.2*sin(iTime))))));//* (.5-augen*1.5);

        //attempting to cut off artifacts with a smoothstep
        glow += smoothstep(0.2,1.,0.015/pow(layer3,.9))*4.;
    }

    else if(lines <= d){globalID = 5.; 
       glow += abs(max(0.0001,(0.00009/(0.03*pow(lines, 1.)))));

    }
    else if(panel <= d)globalID = 4.; 
    else if(ramp <= d)globalID = 7.; 
    //else if(bridge <= d)globalID = 8.; 
    return d;
    //+sin(atan(p.x,p.y)*80.)/80. + sin(p.z*8.)/8.;   
}

float trace(vec3 ro, vec3 rd){

    float t, d= 0.;

    for(int i = 0; i < MAX_STEPS; i++){
        d = map(ro + rd*t);
        if(abs(d) < 0.007 || t > MAX_DIST) break;
        t += d*0.65;
    }

    return t;
}


vec3 normal(vec3 p){

    float d = map(p);
    vec2 e = vec2(eps,0.);

    vec3 n = d - vec3(
            map(p-e.xyy),
            map(p-e.yxy),
            map(p-e.yyx));

    return normalize(n);
}
vec3 camRay(vec2 uv, vec3 o, vec3 target, float zoom){

    vec3 fwd = normalize(target - o);
    vec3 uu = vec3(0.,1.,0.);
    vec3 right = normalize(cross(uu, fwd));
    //order matters
    vec3 up = cross(fwd,right);
    vec3 rd = right*uv.x + up*uv.y + fwd*zoom;
    return normalize(rd);

}

//from shane
float softShadow(vec3 ro, vec3 lp, float k){

    // More would be nicer. More is always nicer, but not really affordable... Not on my slow test machine, anyway.
    const int maxIterationsShad = 24; 

    vec3 rd = lp - ro; // Unnormalized direction ray.

    float shade = 1.;
    float dist = .002;    
    float end = max(length(rd), .001);
    float stepDist = end/float(maxIterationsShad);

    rd /= end;

    // Max shadow iterations - More iterations make nicer shadows, but slow things down. Obviously, the lowest 
    // number to give a decent shadow is the best one to choose. 
    for (int i = 0; i<maxIterationsShad; i++){

        float h = map(ro + rd*dist);
        //shade = min(shade, k*h/dist);
        shade = min(shade, smoothstep(0., 1., k*h/dist)); // Subtle difference. Thanks to IQ for this tidbit.
        // So many options here, and none are perfect: dist += min(h, .2), dist += clamp(h, .01, .2), 
        // clamp(h, .02, stepDist*2.), etc.
        dist += clamp(h, .02, .25);

        // Early exits from accumulative distance function calls tend to be a good thing.
        if (h<0. || dist>end) break; 
        //if (h<.001 || dist > end) break; // If you're prepared to put up with more artifacts.
    }

    // I've added 0.5 to the final shade value, which lightens the shadow a bit. It's a preference thing. 
    // Really dark shadows look too brutal to me.
    return min(max(shade, 0.) + .25, 1.); 
}
float getGrey(vec3 p)
{
    return p.x*0.299 + p.y*0.587 + p.z*0.114;
       }

//from shane, and he got it from an nvidia tutorial you can google
vec3 triPlanar(sampler2D tex, vec3 p, vec3 n)
{

 vec3 norm = max(abs(n), 0.0001);	//I'll keep it simple with just this
 float sum = norm.x+norm.y+norm.z;
 norm = norm/sum;
    return vec3(texture(tex, p.yz)*norm.x + 
                texture(tex, p.xz)*norm.y +
                texture(tex, p.xy)*norm.z) ;

}

vec3 bumpMap(sampler2D tex, in vec3 p, in vec3 n, float bumpfactor)
{




    const vec3 eps3 = vec3(0.001, 0., 0.);//I use swizzling here, x is eps
    float ref = getGrey(triPlanar(tex, p, n));//reference value 

    vec3 grad = vec3(getGrey(triPlanar(tex, p - eps3, n)) - ref,
                     //eps.yxz means 0.,0.001, 0. "swizzling
                     getGrey(triPlanar(tex, p - eps3.yxz, n)) - ref,
                     getGrey(triPlanar(tex, p - eps3.yzx, n)) - ref)/eps3.xxx;

    grad -= n*dot(grad, n);

    return normalize(n + grad*bumpfactor);
}

float calcAo(vec3 p, vec3 n)
{

const float ao_samples = 5.;
    float r = 0.0, w = 1.0, d;
    for(float i = 0.01 ; i<ao_samples ; i+=1.1)
    {
    d = i/ao_samples;//1/5, 2/5, 3/5, 4/5, 1
    r += w*(d - map(p + n*d));


    w*=0.5;
    }

    return 1.0 - clamp(r,0.0, 1.0);
}

float sss(vec3 p, vec3 ldir, float distToTravel){

  //Here is my basic understanding:
  //step out form the hit point a bit in the direction of the sun
  //find dist from map
  //whne map dist is equal to step dist w have a/b = 1.
  //when map dist is smaller we have a/b < 1.


  return smoothstep(0.,1.,map(p + ldir*distToTravel)/distToTravel);

}

