        float diff = max(dot(ldir,n),0.);
        float spec = pow(max(dot(reflect(-ldir,n),-rd),0.),200.)*3.;
        col = alb*0.9*diff + spec*vec3(0.5,0.8,0.9);


        float sha = softShadow(p,farLight,20.);
