        diff = max(dot(ldir,n),0.);
        spec = pow(max(dot(reflect(-ldir,n),-rd),0.),100.)*4.;
        col += alb*0.3*diff*vec3(1.,0.7,0.7);
        col/=2.;
        rd = reflect(rd,n);
        ro = p + n*0.01;
        col += clamp(glow,0.,1.);//*pow(t/MAX_DIST,.7);
