            //col = n*0.5+0.5;//0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
            //rcol = abs(fract(vec3(atan(p.y,p.x)*pi,p.z,p.z))-0.5);
           // rcol= n*0.5+0.5;
            vec3 alb = 0.5+0.5*cos(vec3(1.,2.,4.)/4. + globalID*5.+1.);

            float ldist = length(farLight-p);
