    )
);

vec3 palette(float t,int id)
{
    vec3 a=pcol[id][0];
    vec3 b=pcol[id][1];
    vec3 c=pcol[id][2];
    vec3 d=pcol[id][3];

    return a + b*cos( 6.28318*(c*t+d) );
}
vec3 gray(vec3 c){
    return vec3(dot(c,vec3(0.298912,0.586611,0.114478)));
}
vec3 sepia(vec3 c){
    return gray(c)*vec3(1.07,0.74,0.43);
}
int map(vec2 uv,float t)
{
    int id=0;
    float i=t*1.2;

    bool c=sdCircle(uv,vec2(0.0,0.5),0.3)<0.0;
    bool s=uv.y-(0.5+sin(uv.x*10.0+i*2.0)*0.1)<0.0;
    bool w1=uv.y-gnoise(vec2(uv.x-i*0.1,1.0))*0.5+0.1<0.0;
    bool w8=uv.y-gnoise(vec2(uv.x+i*0.3,8.0))*0.5+0.3<0.0;
    bool w4=uv.y-gnoise(vec2(uv.x-i*0.3,4.0))*0.5+0.5<0.0;

    id=c?s?1:2:id;
    id=w1?3:id;
    id=w8?4:id;
    id=w4?5:id;

    return id;
}
vec3 render(in vec2 uv,float t)
{

    vec3 col;
    switch(map(uv,t))
    {
        case 0:
            col=palette(uv.y*0.1+0.96,0);
            break;
        case 1:
            col=palette(-uv.y*0.5+0.0,1);
            break;
        case 2:
            col=palette(uv.y*0.5+0.5,2);
            break;
        case 3:
            col=palette(uv.y*0.8+0.4,1);
            break;
        case 4:
            col=palette(-uv.y*0.7+0.5,2);
            break;
        case 5:
            col=palette(uv.y*0.6+0.2,1);
            break;
    }
    return col;
}

