const float PI = 3.14159;

const vec3 COLOR_BG = vec3( 17,  26,  46) / 255.0;
const vec3 COLOR_FG = vec3(121, 133, 156) / 255.0;

// https://www.shadertoy.com/view/4djSRW
float hash(in float p) {
    p = fract(p * 0.011);
    p *= p + 7.5;
    p *= p + p;
    return fract(p);
}

// https://www.shadertoy.com/view/4djSRW
float hash(in vec2 p) {
    vec3 p3 = fract(vec3(p.xyx) * 0.13);
    p3 += dot(p3, p3.yzx + 3.333);
    return fract((p3.x + p3.y) * p3.z);
}

float hash(in vec3 p) {
    vec3 q = fract(p * 0.1031);
    q += dot(q, q.yzx + 33.33);
    return fract((q.x + q.y) * q.z);
}

// https://www.shadertoy.com/view/4dS3Wd
float vnoise(in vec2 p) {
    vec2 i = floor(p);
    vec2 f = fract(p);

    // Four corners in 2D of a tile
    float a = hash(i);
    float b = hash(i + vec2(1.0, 0.0));
    float c = hash(i + vec2(0.0, 1.0));
    float d = hash(i + vec2(1.0, 1.0));

    // Smooth interpolation (smoothstep without clamping)
    vec2 u = f*f * (3.0 - 2.0*f);

    // Mix 4 corners
    return mix(a, b, u.x) +
            (c - a)* u.y * (1.0 - u.x) +
            (d - b) * u.x * u.y;
}

// https://github.com/patriciogonzalezvivo/lygia
vec3 brightness_contrast(in vec3 color, in float brightness, in float contrast ) {
    return (color - 0.5) * contrast + 0.5 + brightness;
}

float stripes(in vec2 p, in float nstripes) {
    // Stripe config
    vec2 sc = vec2(nstripes, 1.0) * p;
    vec2 id = floor(sc);
    vec2 cc = fract(sc);

    // Random variables
    float r1 = hash(id.x);
    float r2 = hash(id.x * 13.37);
    float r3 = hash(id.x * 47.11);
    float r4 = hash(id.x * 73.23);

    // Horizontal variation
    float freq = nstripes * PI;
    float f = smoothstep(1.0, 0.0, sin(freq * p.x));

    // Vertical variation
    float t = sin(0.25 * iTime + 10.0 * r1);   // Randomized time offset
    float dt = cos(0.25 * iTime + 10.0 * r1);  // Derivative of time offset
    float len = 0.9 * r2;                      // Randomized length
    float slt = -0.01 + 0.02 * r3;             // Randomized slant
    float ext = 0.2 * t * r4;                  // Randomized extent
    f *= step(len + slt * cc.x - ext, cc.y);

    // Fade out when receeding
    f *= smoothstep(-0.1, 0.5, dt);

    // Apply noise
    f *= 0.5 + 0.5 * vnoise(10.0 * (cc + 0.2 * t));

    return f;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    // Normalize input coordinates
    vec2 uv = fragCoord.xy / iResolution.xy;

    // Background
    vec3 col = mix(COLOR_BG, brightness_contrast(COLOR_FG, -0.3, 1.25), 1.0 - length(uv - vec2(0.5, 1.0)));

    // Stripe layers
    col = mix(col, COLOR_FG, stripes(uv, 200.0));
    col = mix(col, brightness_contrast(COLOR_FG, -0.1, 1.0), stripes(uv + vec2(0.123, 0.0), 250.0));
    col = mix(col, brightness_contrast(COLOR_FG, -0.2, 1.0), stripes(uv + vec2(0.456, 0.0), 150.0));
    col = mix(col, brightness_contrast(COLOR_FG, -0.3, 1.0), stripes(uv + vec2(0.789, 0.0), 300.0));

    // Add layer of animated white noise
    col += 0.1 * vec3(hash(vec3(fragCoord.xy, fract(0.001 * iTime))));

    // Vignetting
    float vig = length(uv - vec2(0.5)) * 0.85;
    vig = vig * vig + 1.0;
    col *= 1.0 / (vig * vig);

    // Final result
    fragColor = vec4(col, 1.0);
}

float rect(vec2 uv, vec2 pos, vec2 size, float blur){
    float x1 = smoothstep(pos.x-blur, pos.x+blur, uv.x);
    float x2 = smoothstep(pos.x+size.x+blur, pos.x+size.x-blur, uv.x);
    float y1 = smoothstep(pos.y-blur, pos.y+blur, uv.y);
    float y2 = smoothstep(pos.y+size.y+blur, pos.y+size.y-blur, uv.y);
    return x1*x2*y1*y2;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 R = iResolution.xy;
    vec2 uv = fragCoord.xy/R.y*R.x;
    vec3 col = vec3(0.);
    float t = iTime;

    float x = uv.x;
    float y = uv.y;
    col.x += rect(uv, vec2(150.+sin(y*.5)*5.+sin(t)*100., 100.+sin(x*.5)*5.)+cos(t)*100.+R/2., vec2(100., 100.), (y+x)/10.)*10.;
    col.y += rect(uv, vec2(float(1+int(y)%20)+cos(t*float(int(t)%9))*100.+200., float(1+int(x)%19)+sin(t*float(int(t)%11))*100.+100.)+R/2., vec2(100., 100.), 50.)*20.;
    float pixelSize = (sin(t/1.5)+1.05)*100.;
    col.z += rect(uv, vec2(float(int(x)%int(pixelSize))+cos(t*1.5)*100.+100., float(int(y)%int(pixelSize))+sin(t/1.5)*100.+100.)+R/2., vec2(100., 100.), pixelSize)*20.;

    fragColor = vec4(col,1.0);
}

#define pi acos(-1.)
#define eps 9./iResolution.y


#define maxPoints 16.
#define screenSize 2.9
float rnd(vec2 id){
    return sin(fract(dot(id,vec2(14.27,74.97)))*54329.34);
}
mat2 rot(float a){
    float s = sin(a), c = cos(a);
    return mat2(c,-s,s,c);
}

float point(vec2 uv, float r){
    return smoothstep(r+eps,r-eps,length(uv));
}

float ring(vec2 uv, float r){
    return smoothstep(eps+0.01, 0., abs(length(uv)-r+0.01));
}

float line(vec2 P, vec2 A, vec2 B, float r){
    vec2 PA = P-A;
    vec2 AB = B-A;
    //dot(AB,P-P3) = 0
    //dot(AB,P-AB*t)
    float t = clamp(dot(PA,AB)/dot(AB,AB),0.,1.);
    return smoothstep(r+eps,r-eps,length(PA-AB*t));

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (2.*fragCoord-iResolution.xy)/iResolution.y;
    vec2 id = floor(uv*screenSize);
    uv = fract(uv*screenSize)*2.-1.;

    vec2 ringPos = vec2(0.,0.);
    float radius = 0.5;
    float ring1 = ring(uv-ringPos, radius);
    vec3 col = vec3(0.);
    col += ring1;

    float mx = 1.+floor(abs(mod(iTime*2.+rnd(id*89.)*831.,
                        maxPoints)-maxPoints/2.))+1.;
    vec2 now,last=vec2(0.,radius)*rot(iTime);
    vec2 first = last;
    col += ring(uv-last, 0.1);
    col += point(uv-last, 0.04);

    for(float i = 1.; i < mx; i++){


        now = vec2(0.,radius)*rot(iTime+i*pi*2./mx);
        col += ring(uv-now, 0.1);
        col += point(uv-now, 0.04);
        col += line(uv,now,last,0.005);



        last = now;
    }
    col += line(uv,now,first,0.005);
    // Output to screen
    fragColor = vec4(col,1.0);
}

vec3 PixeltoWorldSpace(vec2 fragCoord)
{
    vec2 uv = (fragCoord - iResolution.xy * 0.5)/iResolution.y * 1.5;
    float xRot = 3.1415 + iMouse.x / iResolution.x * -6.28;
    return normalize(vec3(uv.x * cos(xRot) - sin(xRot), uv.y, cos(xRot) + uv.x * sin(xRot)));
}

vec3 pcg3d(uvec3 v) {

    v = v * 1664525u + 1013904223u;

    v.x += v.y*v.z;
    v.y += v.z*v.x;
    v.z += v.x*v.y;

    v ^= v >> 16u;

    v.x += v.y*v.z;
    v.y += v.z*v.x;
    v.z += v.x*v.y;

    return vec3(v) / 4294967296.0;
}

vec3 ProceduralStars(vec3 rayDir)
{
    vec3 r = (rayDir + vec3(1,1,1)) * 330.;
    float probability = pcg3d(uvec3(r)).y - abs(rayDir.y) * 0.001;
    if (probability < 0.999)
        return vec3(0,0,0);
    if (probability < 0.9996)
        return vec3(1,.7,.59);
    return vec3(1,.96,.9);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 rayDir = PixeltoWorldSpace(fragCoord);
    vec3 rayPos = vec3(sin(iTime * 0.758334) * .2, cos(iTime * 0.5548334) * .1,1.) * (exp(iMouse.y / iResolution.y * 3. - 1.) * -4.0 + 3.5); // Position of camera

    vec3 col = vec3(0,0,0);
    float transparency = 1.0; // Fog, amount of light that could pass through to reach current pixel
    bool crossedHorizon = false; float elapsedDist = 0.0;
    for (int i = 0; i < 60; i++)
    {
        float distFromBH = length(rayPos);
        float GravStr = 1.0 / (distFromBH * distFromBH * distFromBH);

        if (GravStr > 50.0) // Definitely within horizon
        {
           crossedHorizon = true;
           i = 70;
           continue;
        }
        if (elapsedDist > 150.0) // Outisde range
        {
           i = 70;
           continue;
        }

        rayPos += rayDir * distFromBH * 0.2; // Step ray forwards
        rayDir = normalize(rayDir - normalize(rayPos) * GravStr * distFromBH * 0.2); // Apply gravity numerical integration
        elapsedDist += distFromBH * 0.2; // track total distance

        float opacityFog = 1. - exp(-.25 * distFromBH / (rayPos.y * rayPos.y + 32.)); //Equatorial Fog for nice effect
        col += vec3(.3, .4, .6) * transparency * opacityFog;
        transparency *= 1. - opacityFog;
    }

    if (!crossedHorizon)
        col += ProceduralStars(rayDir) * transparency; // Add stars

    fragColor = vec4(col,1.0);
}

vec3 hsl2rgb( in vec3 c )
{
    vec3 rgb = clamp( abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0 );
    return c.z + c.y * (rgb-0.5)*(1.0-abs(2.0*c.z-1.0));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    float timePeriod = 100.0;
    float wavePeriod = 20.0;
    float width = 1.5;
    vec2 screenCoord = fragCoord - iResolution.xy/2.0;
    vec2 uv =  width * screenCoord / min(iResolution.x, iResolution.y);
    float x = uv.x;
    float y = uv.y;
    float z = 0.0;
    z = wavePeriod * mod(iTime,timePeriod)/timePeriod;

    //G' from https://www.msri.org/publications/sgp/jim/papers/morphbysymmetry/text/levelset.pdf
    float pi = 3.14159265359;
    float d = 
    sin(4.0 *pi * x)* cos(2.0 * pi * y)* sin(2.0 * pi * z) +
    sin(4.0 *pi * y)* cos(2.0 * pi * z)* sin(2.0 * pi * x) +
    sin(4.0 *pi * z)* cos(2.0 * pi * x)* sin(2.0 * pi * y) + 0.32;


    float h = (1.3*d)/5.0;
    float s = 0.8;
    float l = 0.9 - 0.15*d;

    if(0.42<d&&d<0.58){l=0.0;};

    vec3 col = hsl2rgb(vec3(h,s,l));

    // Output to screen
    fragColor = vec4(col,1.0);
}

float thc(float a, float b) {
    return tanh(a * cos(b)) / tanh(a);
}

vec3 pal( in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d )
{
    return a + b*cos( 6.28318*(c*t+d) );
}

float h21 (vec2 a) {
    return fract(sin(dot(a.xy, vec2(12.9898, 78.233))) * 43758.5453123);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;
    float r = length(uv);
    //float a = atan(uv.y, uv.x);
    //uv = vec2(0.2 * cos(a + 0.2 * iTime),r);

    float k = 10. + 8. * thc(3., r + iTime);
    float sc = 6. * ceil(k * fract(abs(uv.y) + r - 0.2 * iTime));
    float d = length(floor(sc * uv) + 0.5)/sc;
    float s = smoothstep(-1., 0.5, 0.1 + 0.5 * h21(uv));

    // 33333. is super hacky
    vec3 col = 1.1 * s * pal(d * 33333. + r - iTime * 0.1, 
               vec3(1.), vec3(1.), vec3(1.), vec3(0.,0.33,0.66));

    fragColor = vec4(col,1.0);
}

#define max_steps 256
