    l.pos = pos;
    return l;
}

Sphere initSphere(in vec3 pos, in float r){
    Sphere s;
    s.pos = pos;
    s.radius = r;
    return s;
}

float sphereSDF(in vec3 point, in Sphere s){
    return distance(point, s.pos)-s.radius;
}

vec3 rayDirection(float fov, vec2 size, vec2 fragCoord) {
    vec2 xy = fragCoord - size / 2.0;
    float z = size.y / tan(radians(fov) / 2.0);
    return normalize(vec3(xy, z));
}

Ray initRay(in vec3 pos,in vec2 fragCoord){
    Ray r;
    r.pos = pos;
    r.rot = rayDirection(45.0, iResolution.xy, fragCoord);
    return r;
