// number of spheres

#define sphereC 2
Sphere s[sphereC];
Light l;

bool check1 = false;

void initScene(){
    if(check1) return;   //initialize the scene only 1 time
    check1 = true;

    s[0] = initSphere(vec3(0, 0, 10), 1.0f);
    s[1] = initSphere(vec3(3, .5, 10), .5f);
    l = initLight(vec3(10, 0, 20));
}

float sceneSDF(in vec3 p){
    float ret = max_dist;

    for(int i=0;i<sphereC;++i){
        ret = min(ret, sphereSDF(p, s[i]));
    }

    //displacement ff
    // ret += sin(5.0 * p.x ) * sin(5.0 * p.y) * sin(5.0 * p.z) * 0.25 * sin(iTime);

    return ret;
}

vec3 estimateNormal(in vec3 p) {
    return normalize(vec3(
        sceneSDF(vec3(p.x + epsi, p.y, p.z)) - sceneSDF(vec3(p.x - epsi, p.y, p.z)),
        sceneSDF(vec3(p.x, p.y + epsi, p.z)) - sceneSDF(vec3(p.x, p.y - epsi, p.z)),
        sceneSDF(vec3(p.x, p.y, p.z  + epsi)) - sceneSDF(vec3(p.x, p.y, p.z - epsi))
    ));
}

vec3 getColor(in vec3 p){

    vec3 normal = estimateNormal(p);

    vec3 direction_to_light = normalize(p - l.pos);

    float diffuse_intensity = max(0.0, dot(normal, direction_to_light));

    return vec3(1.0, 1.0, 1.0) * diffuse_intensity;
}

void rotateLight(){
    float x = 50.0 * sin(iTime);
    float y = 50.0 * cos(iTime);

    l.pos = vec3(x, 30, y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    initScene();

    rotateLight();

    Ray ray = initRay(vec3(0), fragCoord);

    float dist;

    for(int i=0;i<max_steps;++i){
        dist = sceneSDF(ray.pos);

        if(dist <= epsi){
            fragColor = vec4(getColor(ray.pos), 1.0f);
            break;
        }

        if(dist >= max_dist){
            fragColor = vec4(0.0f);
            break;
        }

        ray.pos += ray.rot * dist;
    }    
}

