            uv -= (t2.yx)/(scale);

            //uv -= (t2.yx)/(scale+t3);
            //uv -= (t2.yx)/(scale+col.x);
            t2 = triangle_wave(uv.yx-offset-.5,scale);

            t3 = triangle_wave(uv-offset,scale);

            uv.yx = (t2+t3)/scale;
        }
