int iterations = 100;
float bailout = 6.;



float pMod1(inout float p, float size) {
	float halfsize = size*0.5;
	float c = floor((p + halfsize)/size);
	p = mod(p + halfsize, size) - halfsize;
	return c;
}

float pModMirror1(inout float p, float size) {
	float halfsize = size*0.5;
	float c = floor((p + halfsize)/size);
	p = mod(p + halfsize,size) - halfsize;
	p *= mod(c, 2.0) * 2. - 1.;
	return c;
}

float pModPolar(inout vec2 p, float repetitions) {
	float angle = 2. * PI/repetitions;
	float a = atan(p.y, p.x) + angle/2.;
	float r = length(p);
	float c = floor(a/angle);
	a = mod(a,angle) - angle/2.;
	p = vec2(cos(a), sin(a))*r;
	// For an odd number of repetitions, fix cell index of the cell in -x direction
	// (cell index would be e.g. -5 and 5 in the two halves of the cell):
	if (abs(c) >= (repetitions / 2.)) c = abs(c);
	return c;
}

float pModPolarMirror(inout vec2 p, float repetitions) {
	float angle = 2. * PI/repetitions;
	float a = atan(p.y, p.x) + angle/2.;
	float r = length(p);
	float c = floor(a/angle);
	a = mod(a,angle) - angle/2.;
    a *= mod(c, 2.0) * 2. - 1.;
	p = vec2(cos(a), sin(a))*r;
	// For an odd number of repetitions, fix cell index of the cell in -x direction
	// (cell index would be e.g. -5 and 5 in the two halves of the cell):
	if (abs(c) >= (repetitions / 2.)) c = abs(c);
	return c;
}


float pMirror (inout float p, float dist) {
	float s = sign(p);
	p = abs(p)-dist;
	return s;
}

float smin(float a, float b, float k) {
  float h = clamp(0.5 + 0.5*(a-b)/k, 0.0, 1.0);
  return mix(a, b, h) - k*h*(1.0-h);
}

float modp(float pComponent, float n)
{
    return mod(pComponent + n, n * 2.) - n;
}

vec4 quat(in vec3 v, in float a)
{
    return vec4(v * sin(a / 2.0), cos(a / 2.0));
}

vec4 quat_inv(in vec4 q)
{
    return vec4(-q.xyz, q.w);
}

vec4 p2q(in vec3 p)
{
    return vec4(p, 0);
}

vec4 q_mul(in vec4 q1, in vec4 q2)
{
    return vec4(q1.w*q2.x + q1.x*q2.w + q1.y*q2.z - q1.z*q2.y, 
                q1.w*q2.y - q1.x*q2.z + q1.y*q2.w + q1.z*q2.x, 
                q1.w*q2.z + q1.x*q2.y - q1.y*q2.x + q1.z*q2.w, 
                q1.w*q2.w - q1.x*q2.x - q1.y*q2.y - q1.z*q2.z);
}

vec3 rotate(in vec3 p, in vec3 v, in float a)
{
    vec4 q = quat(v, a);
    return q_mul(q_mul(q, p2q(p)), quat_inv(q)).xyz;
}

vec3 rotateq(in vec3 p, in vec4 q)
{
    return q_mul(q_mul(q, p2q(p)), quat_inv(q)).xyz;
}


float sdSphere(vec3 p, vec3 c, float r)
{
    p -= vec3(cos(iTime * 2.) * 6., sin(iTime * 2.) * 6. + 0.5, 4);

    float dist = length(p - c) - r;

    return dist;
}

float sdBox(vec3 p, vec3 b)
{

    p -= vec3(3, 2, 10);

    p = rotate(p, normalize(vec3(0, 1, 0)), iTime/8.);
    p = rotate(p, normalize(vec3(1, 0, 0)), iTime/8.);

    float amt = sin(iTime) * 9.;

    pModMirror1(p.z, 25. + amt);
    pModMirror1(p.y, 25. + amt);
    pModMirror1(p.x, 25. + amt);

    p = rotate(p, normalize(vec3(0, 1, 0)), iTime * 2.);
    p = rotate(p, normalize(vec3(0, 0, 1)), 2. + iTime/2.);

    pModPolar(p.xz, 2. + 1.);
    pMirror(p.z, 0.);

    p = rotate(p, normalize(vec3(0, 0, 1)), iTime * 1.);

    pModPolarMirror(p.yx, 8.);

    p.y -= 3.;

    p = rotate(p, normalize(vec3(0, 1, 0)), 2. + iTime/3.);
    p = rotate(p, normalize(vec3(0, 0, 1)), 2. + iTime/2.);
    p = rotate(p, normalize(vec3(1, 0, 0)), 2. + iTime/1.);

    vec3 q = abs(p) - b;
    return length( max(q, 0.)) + min(max(q.x, max(q.y, q.z)), 0.);
}

float sdCapsule(vec3 p, vec3 a, vec3 b, float r)
{
    p.z -= 5.;
    p.z = mod(p.z + 1., 2.) - 1.;
    p.x += 2.;
    p.x = mod(p.x + 1., 2.) - 1.;
    p.y -= 2.;

    p = rotate(p, normalize(vec3(1,1,1)), iTime * 0.5);


    vec3 aToB = b - a;
    vec3 aToP = p - a;

    float t = dot(aToB, aToP) / dot(aToB, aToB);

    t = clamp(t, 0., 1.);

    vec3 c = a + t * aToB;

    float dist = length(p - c) - r;

    return dist;
}

float sdTorus(vec3 p, vec3 center, vec2 r)
{
    //p.x += 2.;
    //p.y -= 2.;
    //p.z -= 3.;

    p = p - center;



    //p *= 0.1;


    p = rotate(p, normalize(vec3(0.2, 0, 1)), 2. + iTime/8.);

    p = rotate(p, normalize(vec3(0.3, 0.9, 1)), 2. + iTime/10.);

    //p = rotate(p, normalize(vec3(1, 0, 0.1)), iTime * 5.);

    //pModMirror1(p.x, 20. - iTime/1.);

    pModPolar(p.xz, 6. + sin(iTime) * 3.);
    pMirror(p.z, 0.);

    p.x -= 9.;

    //pModPolarMirror(p.zx, floor(iTime/2.)*50.);



    pModMirror1(p.y, (sin(iTime) + 1.) * 8.);
    //pModMirror1(p.z, iTime/10. + 15.);
