float RayMarch(vec3 ro, vec3 rd, out bool hit, out int stepNum) {
    //initialize distance to zero
    float dist = 0.;
    //Initialize hit bool
    hit = false;

    stepNum = 0;

    for(int i = 0; i < MAX_STEPS; i++)
    {
        stepNum += 1;
        vec3 p = ro + rd * dist;
        float dS = GetDist(p);
        dist += dS;
        if(abs(dS) < SURF_DIST)
        {
            hit = true;
            break;
        }
        else if(dist > MAX_DIST)
        {
            break;
        }
    }

    return dist;
}

float RayMarch2(vec3 ro, vec3 rd, out bool hit) {
    //initialize distance to zero
    float dist = 0.;
    //Initialize hit bool
    hit = false;

    for(int i = 0; i < MAX_STEPS; i++)
    {
        vec3 p = ro + rd * dist;
        float dS = GetDist(p);
        dist += dS;
        if(abs(dS) < SURF_DIST)
        {
            hit = true;
            break;
        }
        else if(dist > MAX_DIST)
        {
            break;
        }
    }

    return dist;
}

vec3 GetNormal(vec3 p)
{
     float d = GetDist(p);
     vec2 e = vec2(0.01, 0);
     vec3 n = d - vec3(
         GetDist(p-e.xyy),
         GetDist(p-e.yxy),
         GetDist(p-e.yyx));

     return normalize(n);
}

float GetLight(vec3 p)
{
    vec3 lightPos = vec3(3, 10, -8);
    //lightPos.xz += vec2(sin(iTime), cos(iTime)) * 4.;

    vec3 lightDir = normalize(lightPos-p);
    vec3 n = GetNormal(p);

    float dif = clamp(dot(n, lightDir), 0., 1.);

    bool hit = false;
    float distToLight = RayMarch2(p + n * SURF_DIST * 2., lightDir, hit);
    if(distToLight < length(lightPos-p))
    {
        dif = 0.;
    }

    return mix(dif, 0.6, 0.2);
    //return dif;
}

