    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;

    // Time varying pixel color
    vec3 col = vec3(0);

    //Initialize camera / rays
    vec3 ro = vec3(0, 6, -2);
    //Determines field of view
    vec3 rd = normalize(vec3(uv.x, uv.y, 0.4));
    rd = rotate(rd, vec3(1,0,0), -(iMouse.y / iResolution.y - 0.5) * 6.);
    rd = rotate(rd, vec3(0,1,0), (iMouse.x / iResolution.x - 0.5) * 3.);

    //Hit or not?
    bool hit = false;

    int numSteps = 0;

    //Shoot rays
    float d = RayMarch(ro, rd, hit, numSteps);

    //Initialize point, light brightness, and normal direction
    vec3 p;
    float dif;
    vec3 normal;

    //If raycast hits
    if(hit)
    {
        p = ro + rd * d;

        dif = GetLight(p);

        normal = GetNormal(p);
    }
    else
    {
        dif = 0.;
        normal = vec3(0);
    }

    col = vec3(dif);
    //col = vec3(20./d)/6.;
    //col = normal;
    //col = (vec3(10./d) + normal) / 4.;
    //col = vec3(1) * float(numSteps) / 50.;

    // Output to screen
    fragColor = vec4(col, 1.0);
}

float getLatitude( vec2 pos, float radius ) {
    return acos(length(pos)/radius);
}


float getLongitude( vec2 pos, float radius ) {
    if (pos.x > 0.0) { return 0.5*atan(pos.y/pos.x)+2.36; }
    else { return 0.5*atan(pos.y/pos.x)+0.8; };
}

// not used
float getLongitude( vec2 pos, vec2 mouse, float radius ) {
    if (pos.x > 0.0) { return 0.5*(atan(pos.y/pos.x)- atan(mouse.x/mouse.y))+2.36; }
    else { return 0.5*(atan(pos.y/pos.x)-atan(mouse.x/mouse.y))+0.8; };
}

