const float SHAPE_SIZE = .1;
const float CHROMATIC_ABBERATION = .02;
const float ITERATIONS = 20.;
const float INITIAL_LUMA = .3;

mat2 rotate2d(float _angle){
    return mat2(cos(_angle),-sin(_angle),
                sin(_angle),cos(_angle));
}


float getColorComponent(in vec2 st, in float modScale, in float blur) {
    vec2 modSt = mod(st, 1. / modScale) * modScale * 2. - 1.;
    float dist = length(modSt);
    float angle = atan(modSt.x, modSt.y);
    float shapeMap = smoothstep(SHAPE_SIZE + blur, SHAPE_SIZE - blur, (sin(st.x * 3.) + 1.) * .5);
    return shapeMap;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 st =
        (2.* fragCoord - iResolution.xy)
        / min(iResolution.x, iResolution.y);
    st *= rotate2d(sin(iTime * .6) * .3);
    st *= (sin(iTime * .3) + 2.) * .3;

    float modScale = 1.;

    vec3 color = vec3(0);
    float luma = INITIAL_LUMA;
    float blur = .2;
    for (float i = 0.; i < ITERATIONS; i++) {
        vec2 center = st + vec2(sin(iTime * .5), cos(iTime * .3));
        //center += pow(length(center), 1.);
        vec3 shapeColor = vec3(
            getColorComponent(center - st * CHROMATIC_ABBERATION, modScale, blur),
            getColorComponent(center, modScale, blur),
            getColorComponent(center + st * CHROMATIC_ABBERATION, modScale, blur)        
        ) * luma;
        st *= 1.1;
        st *= rotate2d(sin(iTime  * .05) * .3);
        color += shapeColor;
        color = clamp(color, 0., 1.);
        if (color == vec3(1)) break;
        luma *= .8;
        blur *= .8;
    }
    fragColor = vec4(color, 1.0);
}

vec2 points[6];
float abs_sum(vec2 xs){
	return abs(xs[0]) + abs(xs[1]);
}
float p_norm(vec2 delta, float p) {
    return pow(pow(abs(delta[0]), p) +
               pow(abs(delta[1]), p),
               1.0/p);
}

vec3 color_from_point(vec2 point){
	return vec3(point[0]+cos(iTime*0.02)*0.1+0.1,sin(iTime*0.01)*0.1+0.1,point[1]+0.3);
}

vec3 minimumDistColor(vec2 point){
	vec2 del = points[0] - point;
    float p = (sin(iTime/1.0)*0.5 + 0.5)+1.0;
	float min_dist_sq = p_norm(del, p);
	vec3 min_color = color_from_point(points[0]);
	if(min_dist_sq<0.005) return color_from_point(vec2(0.5,0.2))-vec3(0.2,0.2,0.2);
	for(int i=0;i<6;i++){
		del = points[i]-point;
		float d = p_norm(del, p);
		if(d < 0.005) return color_from_point(vec2(0.5,0.2))-vec3(0.2,0.2,0.2);
		if(d < min_dist_sq){
			min_color = color_from_point(points[i]);
			min_dist_sq = d;
		}
	}
	return min_color;
}


