#define PI 3.14159265359
#define TWO_PI 6.28318530718
float ng(vec2 st,float N,float size,float radian){
   float a =  atan(st.x,st.y)+radian;
   float r = TWO_PI/float(N);
   float d = cos(floor(.5+a/r)*r-a)*length(st);
   return 1.0-step(size,d);
}
float circle(vec2 st,vec2 c,float r,float d,float start,float end){
  vec2 dir = st-c;
  float a = atan(dir.y,dir.x);
  a = step(start,a)*step(a,end);
  return (step(r-d,distance(st,c))-step(r,distance(st,c)))*a;
}
float segment(vec2 st,float pct,float w,float s,float e){
  return (step(-w*0.5,st.y-pct)-step(w*0.5,st.y-pct))*step(s,st.x)*step(st.x,e);
}
vec3 circle2(vec2 st,vec2 c,float r){
  vec3 col = vec3(0.995,0.325,0.085);
  float c1 = circle(st,c,r*0.1,r*0.1*0.1,-PI,PI);
  float c2 = circle(st,c,r*0.1,r*0.1,-PI,PI)*step(0.0,sin(iTime*50.0));
  float rs= abs(sin(iTime));
  r =r*rs;
  float c3 = circle(st,c,r,r,-PI,PI);
  vec3 lc= col*(smoothstep(r*0.6,r,distance(st,c))-smoothstep(r,r,distance(st,c)));
  return lc+col*(c1+c2); 
}
vec3 circle3(vec2 st,vec2 c,float r){
  vec3 col = vec3(0.240,0.63,0.87);
  mat2 rotate =mat2(cos(iTime),-sin(iTime),
                    sin(iTime),cos(iTime));
  st=rotate*st;
  float a = PI*0.5;
  float b = atan(st.y,st.x);
  float d = (1.0-step(r,distance(st,c)))*step(0.,b)*step(b,a);
  return col*d*smoothstep(0.0,1.0,b*0.5)+col*step(0.,st.x)*step(st.x,0.2)*step(0.,st.y)*step(st.y,r);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float size = 100.0;   
    vec2 st = fragCoord/iResolution.y*size-vec2(iResolution.x/iResolution.y,1.0)*size*0.5;
    vec3 col = vec3(0.0);
    vec3 col1 = vec3(.6,0.6,0.6);
    vec3 col2 = vec3(0.583,0.938,0.995);
    vec3 col3 = vec3(1.,1.,1.);
    vec3 col4 = vec3(0.773,0.957,0.995);
    vec3 col5 = vec3(0.583,0.938,0.995);

    float r = 30.0;
    float w = 0.3;
    float w1 = w*0.8;
    float w2 = w*1.2;
    float x = r*cos(PI/4.0);
    float l1 = segment(st,st.x,w1,-x,x);
    col += col1*l1;
    float l2 = segment(st,-st.x,w1,-x,x);
    col += col1*l2;

    vec2 c = vec2(0.0);
    float c1 = circle(st,c,r*0.1,w1,-PI,PI);
    float c2 = circle(st,c,r*0.5,w1,-PI,PI);
    float c3 = circle(st,c,r*0.7,w1,-PI,PI);
    float c4 = circle(st,c,r,w1,-PI,PI);
    col+= col2*(c1+c2+c3);
    col+= col3*c4;

    float a = PI*0.1*2.0*(0.5+abs(sin(iTime))*0.5);
    float c5 = circle(st,c,r*1.2,w,a,PI-a);
    float c6 = circle(st,c,r*1.2,w,-PI+a,-a);
    col+= col4*c5+c6;

    float d = 0.05;
    float c7 = circle(st,c,r*1.5,w2,d,PI-d);
    float c8 = circle(st,c,r*1.5,w2,-PI+d,-d);


    float tx = r*1.5 + sin(iTime)*2.0;
    float t1 = ng(st+vec2(tx,0.0),3.0,r*1.4*tan(d*0.5),PI*0.5);
    float t2 = ng(st-vec2(tx,0.0),3.0,r*1.4*tan(d*0.5),-PI*0.5);
    col+= col5*(c7+c8+t1+t2);
    float off=iTime*0.1;
    vec2 cenoff=c+vec2(sin(off)*sin(-off)*cos(off),cos(off)*sin(off*2.0))*r; 
    vec3 cir = circle2(st,cenoff,r*0.4);
    vec3 cir3 = circle3(st,c,r);





    fragColor= vec4(col+cir+cir3,1.0);
}

//I stole the color palette from iq's Palettes (https://www.shadertoy.com/view/ll2GD3).

#define INV_PI 0.31830988618

vec3 pal( in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d )
{
    return a + b*cos( 6.28318*(c*t+d) );
}

float GetAngle(vec2 center, vec2 start, vec2 end)
{
    vec2 v0 = start - center;
    vec2 v1 = end - center;

    float cosVal = dot(v0, v1);
    float sinVal = v0.x * v1.y - v0.y * v1.x;

    return INV_PI * atan(sinVal, cosVal) * 0.5 + 0.5;
}

float GetAngleFlip(vec2 center, vec2 start, vec2 end)
{
    vec2 v0 = -(start - center);
    vec2 v1 = end - center;

    float cosVal = dot(v0, v1);
    float sinVal = v0.x * v1.y - v0.y * v1.x;

    return 0.5 - INV_PI * atan(sinVal, cosVal) * 0.5;
}

// b > a
float EasyStep(float a, float b, float x)
{
    float value = clamp(x - a, 0.0, b - a);
    return value / (b - a);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float size = iResolution.y * 0.5;
    vec2 p2 = vec2(iResolution.x * 0.5 - size * 0.5, size * 0.5);
    vec2 p1 = p2 + size;
    vec2 p0 = vec2(p2.x, p1.y);
    vec2 p3 = vec2(p1.x, p2.y);

    float gradientOne = GetAngleFlip(p0, p1, fragCoord);
    float gradientTwo = 1.0 - GetAngleFlip(p3, p2, fragCoord);

    //Not as good as two gradients above.
    //float gradientOne = GetAngle(fragCoord, p0, p1);
    //float gradientTwo = GetAngle(fragCoord, p2, p3);

    float lerpOne = GetAngleFlip(p2, p1, fragCoord);
    float angleOne = GetAngleFlip(p2, p1, p3);
    lerpOne = EasyStep(angleOne, 0.5, lerpOne);

    float lerpTwo = GetAngleFlip(p1, p2, fragCoord);
    float angleTwo = GetAngleFlip(p1, p2, p0);
    lerpTwo = 1.0 - EasyStep(angleTwo, 0.5, lerpTwo);

    float lerpValue = mix(lerpOne, lerpTwo, step(0.5, fragCoord.y / iResolution.y));  
    float gradientValue = 1.0 - mix(gradientTwo, gradientOne, lerpValue);
    gradientValue = gradientValue * 0.5 - 0.05 * iTime;

    vec3 col = pal(gradientValue, vec3(0.5,0.5,0.5),vec3(0.5,0.5,0.5),vec3(1.0,1.0,1.0),vec3(0.0,0.33,0.67) );

    col = pow(col, vec3(0.4545));
    fragColor = vec4(col,1.0); 
}

const float pi = acos(-1.);

float atan2(float y, float x){
    if(x >= 0.){
        return atan(y/x)/pi/2.+.75;
    }
    return (pi+2.*atan(y/x))/4./pi;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //======================
    //parameters
    int amount = 1;
    float width = 5.;
    float speed = 1.;
    vec3 color1 = vec3(1.0, 1.5, 1.0);
    vec3 color2 = vec3(-0.1, 0.0, 0.1);
    //======================

    vec2 R = iResolution.xy;
    vec2 uv = fragCoord-R/2.;
    vec3 col;
    float circle = sqrt(uv.x*uv.x+uv.y*uv.y);
    float d = (circle/100.);
    d -= iTime*speed;

    float mask = smoothstep(R.y/2.+width*4., R.y/2.-width*4., circle);

    col = vec3(sin((2.*pi*atan2(uv.y, uv.x)+d*width)*float(amount))+1.)/2.;
    col = color1+col*(color2-color1);
    col *= mask;
    fragColor = vec4(col,1.0);
}

