#define CONSTRUCT_FROM_ROOTS

void quinticFromRoots(in float x1, in float x2, in float x3, in float x4, in float x5,
    out float a, out float b, out float c, out float d, out float e, out float f) {
    a = 1.0;
    b = -x1 - x2 - x3 - x4 - x5;
    c = x1 * x2 + x3 * x4 + (x1 + x2) * (x3 + x4) + (x1 + x2 + x3 + x4) * x5;
    d = -(x1 + x2) * x3 * x4 - x1 * x2 * (x3 + x4) - (x1 * x2 + x3 * x4 + (x1 + x2) * (x3 + x4)) * x5;
    e = x1 * x2 * x3 * x4 + ((x1 + x2) * x3 * x4 + x1 * x2 * (x3 + x4)) * x5;
    f = -x1 * x2 * x3 * x4 * x5;
}

// Hash from "Hash without Sine" by Dave_Hoskins (https://www.shadertoy.com/view/4djSRW)
float Hash11(in float x) {
    x = fract(x * 0.1031);
    x *= x + 33.33;
    x *= x + x;
    return fract(x);
}

vec2 cmul(in vec2 z, in vec2 w) { return mat2(z, -z.y, z.x) * w; }
vec2 cdiv(in vec2 z, in vec2 w) { return cmul(z, vec2(w.x, -w.y)) / dot(w, w); }
int solveQuintic(in float a, in float b, in float c,
    in float d, in float e, in float f, out float[5] realRoots) {
    // TODO: refactor
    // Reduction to x^5 + px^3 + qx^2 + rx + s
    float p = (5.0 * a * c - 2.0 * b * b) / (5.0 * a * a);
    float q = (25.0 * a * a * d - 15.0 * a * b * c + 4.0 * b * b * b) / (25.0 * a * a * a);
    float r = (125.0 * a * a * a * e - 50.0 * a * a * b * d + 15.0 * a * b * b * c - 3.0 * b * b * b * b) / (125.0 * a * a * a * a);
    float s = (3125.0 * a * a * a * a * f - 625.0 * a * a * a * b * e + 125.0 * a * a * b * b * d - 25.0 * a * b * b * b * c + 4.0 * b * b * b * b * b) / (3125.0 * a * a * a * a * a);

    float bound = 1.0 + max(1.0, max(abs(p), max(abs(q), max(abs(r), abs(s)))));
    //bound *= 0.414213562373; // Correction if perturbing with random([-1...1])
    bound *= 0.5; // Keep safely within bounds

    vec2[5] roots;
    roots[0] = vec2(bound, 0.0); // QuinticRootOfUnity^0
    roots[1] = vec2(0.309016994375, 0.951056516295) * bound; // QuinticRootOfUnity^1
    roots[2] = vec2(-0.809016994375, 0.587785252292) * bound; // QuinticRootOfUnity^2
    roots[3] = vec2(-0.809016994375, -0.587785252292) * bound; // QuinticRootOfUnity^3
    roots[4] = vec2(0.309016994375, -0.951056516295) * bound; // QuinticRootOfUnity^4

    for (int iter=0; iter < 25; iter++) {
        float maxEval = -1e20;
        for (int root=0; root < 5; root++) {
            vec2 z = roots[root];
            vec2 quinticVal = cmul(cmul(cmul(cmul(z, z) + vec2(p, 0.0), z) + vec2(q, 0.0), z) + vec2(r, 0.0), z) + vec2(s, 0.0);
            maxEval = max(maxEval, max(abs(quinticVal.x), abs(quinticVal.y)));

            vec2 denom = z - roots[(root + 1) % 5];
            denom = cmul(denom, z - roots[(root + 2) % 5]);
            denom = cmul(denom, z - roots[(root + 3) % 5]);
            denom = cmul(denom, z - roots[(root + 4) % 5]);

            roots[root] -= cdiv(quinticVal, denom);
        }

        if (maxEval < 1e-7) break;
    }

    // Undo substitution y = x + b/5a
    // and weed out complex roots
    int numRealRoots = 0;
    float offs = b / (5.0 * a);
    for (int root=0; root < 5; root++) {
        vec2 z = roots[root];
        if (abs(z.y) < 1e-7) {
            realRoots[numRealRoots] = z.x - offs;
            numRealRoots++;
        }
    }

    return numRealRoots;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    // Boilerplate
    vec2 center = 0.5 * iResolution.xy;
    vec2 uv = (fragCoord - center) / iResolution.y * 4.0;
    vec4 mouse = (vec4(iMouse.xy, abs(iMouse.zw)) - center.xyxy) / iResolution.y * 4.0;
    float unit = 8.0 / iResolution.y;
    vec3 color = vec3(0.0);
    float time = iTime;

    // Coefficients
    float a, b, c, d, e, f;

    #ifdef CONSTRUCT_FROM_ROOTS
    time *= 0.25;
    float sec = floor(time);
    float lerp = smoothstep(0.0, 1.0, fract(time));

    // Generate random animated roots
    float x1 = mix(Hash11(sec), Hash11(sec + 1.0), lerp) * 6.0 - 3.0;
    float x2 = mix(Hash11(sec + 17.13), Hash11(sec + 18.13), lerp) * 6.0 - 3.0;
    float x3 = mix(Hash11(sec + 34.26), Hash11(sec + 35.26), lerp) * 6.0 - 3.0;
    float x4 = mix(Hash11(sec + 51.39), Hash11(sec + 52.39), lerp) * 6.0 - 3.0;
    float x5 = mix(Hash11(sec + 68.52), Hash11(sec + 69.52), lerp) * 6.0 - 3.0;

    quinticFromRoots(x1, x2, x3, x4, x5,
                     a, b, c, d, e, f);

    #else
    a = sin(time * 0.25) * 1.25;
    b = sin(time) * 2.0;
    c = sin(time) * 2.0;
    d = cos(time * 0.75);
    e = sin(time * 0.5);
    f = cos(time);
    #endif

    // Draw the x axis and quintic equation
    float fx = ((((a * uv.x + b) * uv.x + c) * uv.x + d) * uv.x + e) * uv.x + f;
    float dx = (((5.0 * a * uv.x + 4.0 * b) * uv.x + 3.0 * c) * uv.x + 2.0 * d) * uv.x + e; // Derivative for distance estimation
    color.b += smoothstep(unit, 0.0, abs(uv.y));
    color.rg += smoothstep(unit, 0.0, abs(uv.y - fx) / sqrt(1.0 + dx * dx));

    // Solve and draw expected versus found roots
    float[5] roots;
    int numRoots = solveQuintic(a, b, c, d, e, f, roots);
    for (int n=0; n < numRoots; n++) {
        color = mix(color, vec3(1.0), smoothstep(unit, 0.0, length(uv - vec2(roots[n], 0.0)) - 0.05));

        #ifdef CONSTRUCT_FROM_ROOTS
        color = mix(color, vec3(0.0, 1.0, 0.0), smoothstep(unit, 0.0, abs(length(uv - vec2(roots[n], 0.0)) - 0.1)));
        #endif
    }

    fragColor = vec4(color, 1.0);
}

